css:
@-moz-document domain("steamcommunity.com") {
/*
	Dark Steam v1.36
	Created by Hellsing4682
	http://steamcommunity.com/profiles/76561198023003050
	http://steamcommunity.com/groups/DSUI
	Modify as you please!
*/

/*
	=================================================================
						    Universal Changes
	  These changes will affect all or every page that concerns it
	=================================================================
*/

/*
	Dark Space: http://i.imgur.com/zi5sEWR.jpg
	Space Blue: http://i.imgur.com/4CT1OsS.jpg
*/

/* Adds 'space' background, - Modify the url to place your own background */
body.flat_page.blue,body.flat_page,body {
	background-image: url(http://i.imgur.com/4CT1OsS.jpg);
    background-attachment: fixed;
    background-position: top;
    background-color: #000;
    background-repeat: no-repeat;
    text-shadow: 1px 1px 1px #000;
}

/* Changes the header in the 'group' and 'friends' list in the 'User' tab */
.profile_small_header_bg,.profile_subpage_column .profile_small_header_bg {
    background-image: none;
	background-color: rgba(0,0,0,0);
    max-width: 950px;
}

.profile_small_header_bg {
	height: 200px;
}

.profile_small_header_texture {
	background-image: none;
}

/* Moves the tabs lower to not have content overlap with the header */
body.migrated_profile_page #BG_bottom.maincontent {
	padding-top: 60px;
}

/* Changes appearance of the tooltip when hovering over badges, for instance */
.community_tooltip {
	color: #eee;
    background-color: rgba(0,0,0,.8);
}

.emoticon_popup {
	background-color: rgba(0,0,0,.7);
}

/* Changes appearance of the drop down menu in the global header */
.supernav_content {
	background-color: rgba(0,0,0,.8);
}

/* Changes the arrow on certain hover tool tips */
.hover_arrow_inner {
	background-image: url(http://i.imgur.com/b6tsf56.gif);
    opacity: .7
}

.commentthread_comment_timestamp {
	color: #888;
}

/* Changes  appearance of the global header at the top of the screen, shortened height */
div#global_header{
	background: #000;
    height: 75px;
}

div#global_header .content {
	background: none;
    max-width: 950px;
}

div#global_header .menuitem {
	top: -5px;
}

/* Removes background behind right column contents */
#rightContents {
	background: none;
}

/* Changes appearance of the tooltip when hovering over Steam profiles */
.miniprofile_top,.miniprofile_ingame {
	background: rgba(0,0,0,.9);
}
.miniprofile_favorite_badge {
	background: none;
}

/* Changes appearance of the tooltip when hovering over inventory items */
.economyitem_hover_content {
	background: rgba(0,0,0,.9);
}

#footer .footer_content,#footer {
    background: none;
}

/*
	=================================================================
					   Steam Profiles Section
		For those using Enhanced Steam, some elements will get
		   overrided when they choose a custom profile style
	=================================================================
*/

/* Removes the header texture, more shorter, transparent background */
.profile_header_bg {
	background-image: none;
    background-color: rgba(0,0,0,.6);
    max-width: 950px;
    box-shadow: 0px 0px 10px #000;
}

/* Makes the content background more transparent, drop shadow */
.profile_background_overlay_content {
    background-color: #000;
	opacity: .6;
}

/* Adds drop shadow to profile content container for those w/backgrounds */
.profile_content.has_profile_background {
	box-shadow: 0px 14px 10px #000;
}

/* Black body for those w/o backgrounds */
.profile_content {
	background-image: none;
    background-color: rgba(0,0,0,.4);
    z-index: 5
}

.profile_header_bg_texture {
    background-image: none;
}

/* Removes body from private profiles */
div.profile_page.private_profile, div.private_profile .profile_content {
	background-image: none;
    background-color: rgba(0,0,0,0);
}

/* Changes the appearance of favorite badge */
.profile_header_badge {
	background: rgba(0,0,0,.15);
    border-radius: 5px;
    border: none;
    box-shadow: none;
}

/* Changes the custom user boxes */
.profile_customization {
	background: url(http://i.imgur.com/J9CO1OO.png) top left no-repeat, url(http://i.imgur.com/DgAwVLt.png) bottom right no-repeat;
}

.showcase_content_bg {
	background-color:rgba(0,0,0,0);
}

.game_info_stats {
	border-top: none;
}

.recent_game .game_info_stats, .favoritegame_showcase .game_info_stats {
	border-top: none;
    background-color: rgba(0,0,0,.4);
}
.game_info_stats_rule {
	display: none;
}
.recent_game, .profile_comment_area .commentthread_header, .profile_leftcol_header {
	background: none;
}

.game_info_badge_border {
	box-shadow: none;
    background: rgba(0,0,0,.5);;
    border: none;
}

.profile_customization.customization_edit.none_selected {
	background: rgba(0,0,0,.1);
    border: 1px dashed #222;
}
.profile_customization_area .profile_customization.customization_edit.none_selected:hover {
	border-color: #555;
}

/* Centers screenshots */
.screenshot_showcase_screenshot img {
	margin: auto;
}

/* Fills in the footer spacer to not have it become an odd blank space */
body.flat_page #footer_spacer, body.headerless_page #footer_spacer {
	height: 55px;
}

/* Changes header texts to white */
.profile_customization_header,.profile_content h2,.commentthread_header {
	color: #FFF;
}

#ScreenshotManagementToggle,#ScreenshotUploadLink {
    text-shadow: none;
}

/*
	===Notification Section
*/

/* Appearance and positional changes */
.commentnotification {
	background: rgba(0,0,0,.5);
}

.commentnotifications_top_content {
	padding-top: 40px;
}

/*
	=================================================================
					    Steam Group Profile Section
	=================================================================
*/

/* 
	===Overview Page
*/

/* Change content look */
.grouppage_membertiles_overlay {
    background-image: none;
    background-color: rgba(0,0,0,0.5);
}

/* Black gradients for content */
.group_content.group_summary {
	background-image: url('http://i.imgur.com/DgAwVLt.png');
	background-position: bottom left;
	background-repeat: no-repeat;
}
.group_content {
		background: url('http://i.imgur.com/DgAwVLt.png') bottom left no-repeat, 
					url('http://i.imgur.com/y93G42e.png') top left no-repeat;
}
.group_content.bottomless {
	background: url(http://i.imgur.com/y93G42e.png) top left no-repeat;
}

.rightbox .rule {
	border-top: none;
}

.group_overview_discussions .forum_topic:hover {
	background-color: rgba(60,60,60,.2);
}

.group_overview_announcement_readmore {
	border-top: 1px solid #555;
}

.group_comments .commentthread_entry_quotebox {
	border: none;
    background-color: rgba(0,0,0,.7);
}

.group_comments .commentthread_entry {
	background: none;
}
/* Removes second BG from associated games */
.group_associated_game {
	background: none;
}

/*  Text color changes */
.grouppage_content_area h1 {
	color: #fff;
}

.groupstat,.rightbox_content_header,.group_content_header {
	color: #9D9D9D;
}

.group_content_header a,a.large_title,a.readmore_link,.btn_grey_black > span,.group_curator_more_box,#group_tab_content_announcements .group_content h2 {
	color: #fff;
}

div.bb_h1,.group_overview_announcement_footer,.announcement_byline {
	color: #fff;
}

.group_overview_discussions .forum_topic .forum_topic_name,.group_overview_discussions .forum_topic.unread .forum_topic_name {
	color: #fff;
}

/*
	===Members Section
*/

/* Changes color of members block */
.member_block {
	background-color: rgba(0,0,0,.6);
}

.group_paging {
	background-color: rgba(0,0,0,.4);
}

/*
	===Game App Group Profiles Section
*/

#mainBody,#mainContents {
	background: none;
}

.rightSectionHeader,#rightActionBlock,#rightActionBlockFooter {
    background: none;
}
.rightSectionHeader {
	color: #898989;
}

/*
	=================================================================
						Steam Discussions Section
		Affects Steam Groups Discussions and Community Discussions
	=================================================================
*/

/* Avoids overlap in Steam Discussions homepage */
.grouppage_content_area {
	padding-bottom: 15px;
}

/* Changes color of the forum topic block */
.forum_topic {
	background-color: rgba(0,0,0,.6);
}
.forum_topic:hover {
	background-color: rgba(60,60,60,.6);
}

.forum_topic_overlay {
	background: none;
}

.forumtopic_comments .commentthread_comment {
	background-color: rgba(0,0,0,.6);
    border: 1px solid rgba(83, 127, 166, 0.29);
}

/* Idle state when posts aren't hovered by mouse */
.forumtopic_comments .commentthread_comment .commentthread_comment_author {
	opacity: .85;
}

.btnv6_blue_hoverfade {
	background-color: rgba(0,0,0,.5);
    color: #fff !important;
}

.forum_op {
    background-image: none;
	background-color: rgba(0,0,0,.6);
    border: 1px solid rgba(83, 127, 166, 0.29);
}

.forum_paging {
	background-color: rgba(0,0,0,.4);
    color: #9D9D9D;
}

.forum_newtopic_box {
	background-color: rgba(0,0,0,.6);
}

.forum_topic_op {
	color: #9D9D9D;
}

.forum_op.locked, .forum_topic_locked_notice,.forum_topic.locked {
	background: url(http://i.imgur.com/BITVrLH.png);
    background-repeat-y: no-repeat; /* If the property is unknown, it's lying */
    background-color: rgba(20,0,0,.6);
    border: 1px solid #300;
}

.rightbox,.rightbox.forum_list {
	background-color: rgba(0,0,0,.4);
}

.discussionSearchTextContainer {
    background-image:none;
	background-color: rgba(0,0,0,.6);
}

/* Changes the appearance of forum topics in Discussions */
.discussions_forum_row {
	background-color: rgba(0,0,0,.6);
}

a.recent_game_discussion {
	background-color: rgba(0,0,0,.6);
}

/* Changes the apperance of the hover tooltip over posts */
.forum_topic_tooltip {
	background-color: rgba(0,0,0,.8);
    border: 1px solid #333
}

.grouppage_content_area h2, a.large_title, h2.large_title {
	color: #fff;
}

.rightcol_minor_text,.rightbox_list_header {
	color: #9D9D9D;
}

.discussions_view_more_link a,.discussions_view_more_link a:hover {
	color: #fff;
}

.apphub_background{
	background-image: none !important;
}

/*
	=================================================================
						Steam Activities Section
	=================================================================
*/

/* Change of content body colors, transparency */
.friend_activity_header h1 {
	color: #fff;
}

.blotter_group_announcement_headline, .blotter_group_announcement_headline a, .blotter_group_announcement_headline a:visited, .blotter_group_announcement_headline a:active {
	color: #fff;
}

.blotter_block {
	background-color: rgba(26,26,26,.9);
}

.blotter_status_entry .commentthread_entry {
	background-color: rgba(34,34,34,.5);
}

/* The border massacre */
.blotter_author_block,.blotter_comment_thread,.blotter_comment_thread .commentthread_entry_quotebox,.blotter_group_announcement_header {
    border: none;
}

.blotter_status_entry .commentthread_entry_quotebox {
	border: 1px solid #444;
}

/* Search borders and color */
div#headerSearch.friend_activity_header_search, .profileFriendSearchBox {
	border: 1px solid #444;
    background-color: #111;
}

/*
	=================================================================
							Group List Section
	=================================================================
*/

/* Removes large BG behind group blocks */
.profile_subpage_column .maincontent, body.migrated_profile_page .profile_subpage_column #BG_bottom.maincontent {
	background-image: none;
}

/* Removes second BG behind group blocks */
.left17 {
	background: none;
}

/* Changes appearance of group blocks */
.groupBlock,.groupBlock.darkBG {
	background-color: rgba(0,0,0,.5);
}

/*
	=================================================================
						   Friends List Section
	=================================================================
*/

/* Removes the god-awful gray body */
.graytab_body_wrapper,.graytab_body {
	background: none;
}

/* Changes appearance of tab buttons */
.graytab.active {
    border: 1px solid #666;
    background: none;
	background-color: rgba(0,0,0,.6);
}

.graytab {
	border: 1px solid #222;
    background-color: rgba(0,0,0,.6);
}

.graytab:hover {
	border: 1px solid #444;
}

/* Changes appearance of friend blocks */
.profile_friends .friendBlock {
	background-color: rgba(0,0,0,.6);
}

/* Changes appearance of 'Manage Friends List' container */
.manage_actions_ctn {
    border: none;
	background-color: rgba(0,0,0,.6);
}

/* The 'Recently Played With' tab is pretty borked by default, don't think it's worth fixing */

/*
	=================================================================
						    Inventory Section
	=================================================================
*/

#tabcontent_inventory {
	background: rgba(0,0,0,.7);
    margin-bottom: 100px;
}

.view_inventory_page {
	background: none;
}

.inventory_iteminfo .item_desc_content {
	background: rgba(0,0,0,.5);
}

.games_list_tabs {
	background: rgba(0,0,0,.7);
    border: none;
}
.games_list_tab, a.games_list_tab {
	background: rgba(0,0,0,.7);
    border: none;
}
.games_list_tab:hover {
	background: rgba(40,40,40,.7);
}
.games_list_tab.active {
	background: rgba(60,60,60,.7);
    -webkit-box-shadow: none;
    box-shadow: none;
}

/*
	=================================================================
						  Player Badges Section
	=================================================================
*/

/* Removes the background from the player experience */
.profile_xp_block {
	background: none;
    border: 1px solid #000;
}

/* Changes appearance of row content */
.badge_row {
	background: none;
}

.badge_row_inner {
	background: rgba(0,0,0,.7);
}

.badge_row.is_link:hover {
	border: 1px solid #666;
	background-color: rgba(20,20,20,.7);
}

/*
	=================================================================
							 Games Section
	=================================================================
*/

/*
	===Recently Played/All Games/Followed Section
*/

.gameListRow {
	background-color: rgba(0,0,0,.5);
}

/*
	===Wishlist Section
*/

/* Changes the appearance of each row */
.wishlistRow.sortableRow,.wishlistRow {
	background-color: rgba(0,0,0,.5);
}

/* Hides the orange Steam character */
#wishlist_comic {
	display: none;
}



/*
	===Reviews Section
*/

.review_box {
	background: rgba(0,0,0,.5);
    border: none;
}

/*
	=================================================================
						Community Home Section
	=================================================================
*/

.apphub_background {
	background-image: none !important;
}

/*
	=================================================================
						    Guides Section
	=================================================================
*/

.apphub_ContentGuides .workshopItemCollection {
	background-color: rgba(0,0,0,.6);
}
.apphub_ContentGuides .workshopItemCollection:hover {
	background-color: rgba(60,60,60,.6);
}

.workshopItemCollection .workshopItemShortDesc {
	color: #fff;
}
.workshopItemCollection .workshopItemAuthorLine {
	color: #ccc
}
.workshopItemCollection .workshopItemAuthorName {
	color: #fff;
}
.apphub_ContentGuides .workshopItemCollection .workshopItemTitle {
	color: #6CB0E4;
}

/*
	=================================================================
						   Workshop Section
	=================================================================
*/

.detailBox {
	padding-bottom: 5px;
}

.commentsLink {
	margin-top: 10px;
}

.headline,.byline,.bodytext {
	margin-left: 5px
}

.browseApps {
	background-color: rgba(0,0,0,.5);
}

/* Changes slider below popular items in game app workshop */
.slider .handle,.slider_left,.slider_right {
	background-color: rgba( 35, 60, 81, .8 ) !important;
}
.slider_container {
	background: rgba(0,0,0,.5);
}

.parentCollection {
	width: 620px;
}

/* Black gradients for detail boxes on mod pages */
.detailBox,.detailBox.altFooter {
	background: url( 'http://i.imgur.com/DgAwVLt.png' ) bottom right no-repeat,
	url( 'http://i.imgur.com/y93G42e.png' ) 0px 8px no-repeat,
	url( 'http://steamcommunity-a.akamaihd.net/public/images/groups/content_header_rule.png?v=1' ) -4px 0px no-repeat;
}

.collectionItem {
	background-color: rgba(0,0,0,.5);
}

.workshop_item_hover {
	background-color: rgba(0,0,0,.8);
}

.game_area_purchase_game {
	background: rgba(0,0,0,.5);
}

.hover_box {
	background: rgba(0,0,0,.8);
}

.commentthread_area .commentthread_header {
	background: none;
}

.creatorsBlock .friendBlock_offline, .creatorsBlock .friendBlock_online, .creatorsBlock .friendBlock_in-game, .creatorsBlock .friendBlock_ignored {
	background: none;
}
.creatorsBlock .friendBlock_offline:hover, .creatorsBlock .friendBlock_online:hover, .creatorsBlock .friendBlock_in-game:hover, .creatorsBlock .friendBlock_ignored:hover {
	background: rgba(60,60,60,.5);
}

.jsTooltip {
	background: rgba(0,0,0,.8);
    color: #fff;
}

/*
	=================================================================
						   Greenlight Section
	=================================================================
*/

.detailBox.noFooter {
	background: url( 'http://i.imgur.com/y93G42e.png' ) 0px 8px no-repeat,
 				url( 'http://steamcommunity-a.akamaihd.net/public/images/groups/content_header_rule.png?v=1' ) -4px 0px no-repeat;
}

.workshop_item_header {
	background: rgba(0,0,0,.5);
}
.workshopItemPreviewArea.accent {
	background: none;
}

/*
	=================================================================
						     Market Section
	=================================================================
*/

#BG_bottom {
	background: rgba(0,0,0,.5);
}

.my_listing_section .market_listing_row {
	background: rgba(0,0,0,.5);
}

.market_listing_row {
	background: rgba(0,0,0,.5);
}

#myMarketTabs {
	background: none;
}

.market_listing_table_message {
	background: rgba(0,0,0,.5);
}

.game_button > span {
	background: rgba(0,0,0,.5);
}

/*
.market_tab_well_tab_active > span {
    
}
*/

.game_button:hover > span, .game_button .game_button_preload {
	background: rgba(60,60,60,.5);
}

.market_large_tab_well,.market_section_header,.market_large_tab_well_gradient,.market_large_tab_well .market_section_header_cap, .market_section_header .market_section_header_cap {
    background: none;
}

#searchResultsTable {
	background: rgba(0,0,0,.5);
}

.market_listing_iteminfo {
	background: rgba(0,0,0,.5);
}

/*
	=================================================================
						    Game App Section
	=================================================================
*/

/* Adds 'space' background, - Modify the url to place your own background */
body.apphub_blue {
	background-image: url(http://i.imgur.com/4CT1OsS.jpg);
    background-attachment: fixed;
    background-position: center;
}

.apphub_Card {
	background: rgba(0,0,0,.5);
}

.apphub_CardContentNewsFade,.apphub_CardContentMain .UserReviewCardContent_Footer .gradient {
	background: none;   
}

/*
	=================================================================
				   Error Page Section... 'cause why not
	=================================================================
*/

.error_ctn {
	background: none;
    background-color: rgba(0,0,0,.5);
}
}

@-moz-document url-prefix("http://steamcommunity.com/id/") {
