css:
@-moz-document domain("twitch.tv") {

.tw-root--theme-light .tw-c-background-alt-2 {
    background-color: #1a1a1c!important;
}

.chat-author__display-name {
    color: #7797be !important;
}
.emote-picker__tab-content {
    max-height: 66vh !important;
}
.emote-picker__emote {
    width: 30px !important;
}
a.chat-line__message--link {
    color: #d2880a !important;
}
a.chat-line__message--link:visited {
    color: #6b4507 !important;
}

.chat-line__message a {
    color: #d2880a !important;
}

.chat-line__message a:visited {
    color: #6b4507 !important;
}
.chat-line__message a:hover {
    color: #e7a43b !important;
}

.chat-room__content {
    background-color: #1e1e1e !important;
}
.chat-line__message {
    color: #c2c2c2 !important;
    font-size: 14px !important;
}

.mention-fragment {
    background-color: #7797be !important;

    color: #000 !important;
}
.chat-author__intl-login {
    color: #ff9771 !important;
}
textarea.tw-block {
    background-color: #1e1e1e !important;
}
.pinned-cheer-v2,
.pinned-cheer-v2__loading {

    display: none !important;
}
.tw-svg__asset--bits {
    display: none !important;
}

.emote-picker-plan-upsell {
    display: none !important;
}
.chat-badge {

    opacity: 0.3 !important;
}
.room-selector__header {
    display: none !important;
}
}
