css:
@-moz-document domain(account.microsoft.com) {

/* Made by Brian Owens */
body {
	  background-image: url('http://3.bp.blogspot.com/_enVLP57PrXw/TKYPHn8wdXI/AAAAAAAAD-c/h_ai0TCYFhk/s1600/free-wallpaper-1+%281%29.jpg');
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: 100%;
} 
a:link { 
    color: #FFF;
}
a:visited{
    color: #FFF;
}
.msame_Header_name {
    color: #fff;
    display: inline-block;
    font-family: "Segoe UI","Segoe UI Web Regular","Segoe UI Symbol","Helvetica Neue","BBAlpha Sans","S60 Sans",Arial,sans-serif;
    font-size: 86%;
    line-height: 64px;
    max-width: 160px;
    padding-left: 12px;
    vertical-align: top;
}
.shell-header i[class*="shell-icon-"]::before, .shell-footer i[class*="shell-icon-"]::before, .shell-header-horizontalL2-container i[class*="shell-icon-"]::before {
    color: #fff;
    display: block;
    font-family: "shell-icons";
    font-style: normal;
    font-weight: normal;
    height: 1em;
    line-height: 1.1;
    width: 1em;
}
#home-index::before {
    background-color: transparent;
    bottom: 0;
    content: "";
    display: block;
    left: -63.512px;
    position: absolute;
    right: 62.5%;
    top: 56px;
}
.shell-header a:link, .shell-header a:visited {
    color: #fff;
}
#servicessubscriptions-landing .section-background {
    background-color: transparent;
    margin: auto 5.6%;
    padding: 1px 5.6%;
    width: 90%;
}
#servicessubscriptions-landing .subsection-item-header {
    color: #fff;
    font-size: 0.75rem;
    font-weight: 400;
    line-height: 0.875rem;
    margin-bottom: 5px;
    padding: 10px
}
.row {
    margin-left: 42px;
    margin-right: 22px;
}  
.row::before, .row::after {
    content: " ";
    display: table;
    padding: 30px 0 0;
}
.shell-header {
    background-color: rgba(0,0,0,0.2);
    color: #33FF00;
    font-size: 16px;
    padding: 0 8px;
} 
#site-header {
    margin-bottom: 30px;
    position: relative;
}
.table > tbody > tr:nth-child(2n+1) {
    background-color: rgba(0,0,0,0.2)
}
td, th {
    color: #fff;
}
#devices-landing .page-title {
    margin-top: 4px;
}
#devices-landing .devices-list-container .device-properties-container li {
    color: #fff;
    max-width: 240px;
}
.shell-rwd .shell-header-wrapper, .shell-rwd .shell-footer-wrapper {
    max-width: 100%;
    width: 90%;
}
.member-tile:nth-child(2n+1) {
background: transparent;}
.col-lg-16 {
    background-color: rgba(0,0,0,0.2);
    margin: 10px 10px 10px 20px;
    padding: 10px 10px 10px 20px;
    width: 70%;
}
#devices-landing .devices-list-container {
    background-color: transparent;
    margin: 30px;
    padding: 10px;
}
.container, .container-fluid {
    background-color: rgba(0,0,0,0.4);
    margin-left: auto;
    margin-right: auto;
    padding-left: 22px;
    padding-right: 22px;
    width: 60%;
}
.profile-section.picture {
    background-color: rgba(0,0,0,0.2);
    margin: 10px 0 0;
    max-width: 240px;
    padding: 20px 15px 10px 3px;
}
.shell-footer {
    background-color: rgba(0,0,0,0.2);
    color: #fff;
    font-size: 0.875em;
    padding: 20px 0;
    margin: 40px 0px 0px 0px;
}
.shell-footer a {
    color: #fff;
}
.shell-footer h4 {
    color: #000;
    font-family: "wf_segoe-ui_normal","Arial",sans-serif;
    font-size: 100%;
}
.family-sub-footer {
    margin-bottom: -1px;
    width: 1%;
}    
}
