css:
@-moz-document domain("www.pivotaltracker.com") {
.model section.edit li.activity,
.model section.edit .mini.attachments .attachments {
  background-color: #444;
  border-color: #444;
  font-size: 140%;
  color: #eee;
  line-height: 1.5em;
}

/* Increase line height in comments, so that inline code blocks have gap between */
.model section.edit li.activity {
  line-height: 1.8em;
}

.model section.edit li.activity.github,
.model section.edit .mini.attachments .attachments.github {
  background-color: #444;
  border-color: #222;
}

/* Collapsible commit - block */
[data-aid="commit-comment-group"] > div {
  background-color: #444;
  border-color: #222;
  color: #eee;
}

/* Collapsible commit - commit */
[data-aid="commit-comment-group"] > div > div > div {
	border-bottom-color: #222;
}

[data-aid="commit-comment-group"] > div div[data-aid="message"] {
  color: #eee;
}

[data-aid="commit-comment-group"] a {
  color: #8BC5FF;
}

/* Add comment block  */
[data-aid="comment-new"],
[data-aid="comment-edit"] {
  background-color: #444;
}

.tracker_markup .mention {
  color: #fff;
  background-color: #333;
  border-color: #555;
  display: inline-block;
  padding: .1em .3em;
}

/* doesn't apply
.model section.activity ol.comments .activity.github .author {
  color: #f0f0f0;
}*/

.tracker_markup a/*, doesn't apply
.model section.activity ol.comments .activity.github .url a:link,
.model section.activity ol.comments .activity.github .url a:hover,
.model section.activity ol.comments .activity.github .url a:visited*/ {
  color: #8BC5FF;
}

.story.started .preview .tracker_markup .story_name a,
.story.finished .preview .tracker_markup .story_name a,
.story.delivered .preview .tracker_markup .story_name a,
.story.rejected .preview .tracker_markup .story_name a {
  color: #0957a4;
}

.model section.edit .description .edit_description {
  color: #8BC5FF;
}

/* Increase story description font size and line-height */
.model section.edit .description.editing textarea.editor,
.model section.edit .description .rendered_description {
  font-size: 150%;
  padding: 4px;
  line-height: 1.6em;
}

.model section.edit .description .rendered_description a {
  color: #0957a4;
}

section.edit .tracker_markup .story.flyoverable {
  color: #0957a4;
}

.tracker_markup .story_name a {
  color: #0957a4;
}

/* Comment/Commit timestamp */
.model section.edit li.activity time,
[data-aid="commit-comment-group"] time {
  color: #aaa;
  font-style: normal;
  font-size: 120%;
}

/* "(updated)" text */
.model section.edit li.activity time + span {
  color: #aaa;
}

[data-aid="initials"] {
  background-color: #e9e7dd;
}

/* Fix comment author name and make it uppercase */
.model section.edit li.activity > header > div > div:nth-child(2),
[data-aid="comment-new"] > header > div > span:nth-child(2),
[data-aid="comment-edit"] > header > div > span:nth-child(2),
.activity.comment > header > div > :nth-child(2) {
  color: #f0f0f0;
  font-size: 90%;
  text-transform: uppercase;
}

/* doesn't apply
.model section.activity .activity header .author .initials {
  background-color: #E9E7DD;
  border-color: #444;
}*/

.details.edit .cancel {
  color: #eee;
  text-shadow: 0 1px 1px #333;
}

.details.edit .field_controls .cancel {
  color: #666;
  text-shadow: 0 1px 1px #fff;
}

.edit .task, .ghost .task, .edit .task_new,
.ghost .task_new {
  background-color: #444;
}

.editor.std,
.info_box,
.model section.edit .description .rendered_description,
.labels_maker {
  border: 1px solid #666;
  -moz-box-shadow: #222 0 1px 0;
  -webkit-box-shadow: #222 0 1px 0;
  box-shadow: #222 0 1px 0;
}

.model section.edit .description .field_controls {
  -moz-box-shadow: #222 0 1px 0;
  -webkit-box-shadow: #222 0 1px 0;
  box-shadow: #222 0 1px 0;
  border-bottom-color: #666;
  border-left-color: #666;
  border-right-color: #666;
}

.model section.edit {
  background: #222 url('data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAUAAAAFCAYAAACNbyblAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAACxEAAAsRAX9kX5EAAAAYdEVYdFNvZnR3YXJlAHBhaW50Lm5ldCA0LjAuNWWFMmUAAAAiSURBVBhXY/j//z8KDgkJwRQA0RgCcEFkAbAgusD///8ZAGOsRazO8qTCAAAAAElFTkSuQmCC');
  -moz-box-shadow: inset #333 0 0 7px;
  -webkit-box-shadow: inset #333 0 0 7px;
  box-shadow: inset #000 0 0 7px;
}

.model section.edit h4 {
  color: #eee;
  text-shadow: 0 1px 1px #333;
}

ul.attachments .filename a,
ul.attachments .filename span {
  color: #eee;
  text-shadow: 0 1px 1px #333;
}

.tasks .description {
  color: #eee;
}

.dropdown .selection {
  color: #eee;
}

.info_box .dropdown .selection{
  color: #000;
}

::-webkit-scrollbar-thumb {
  background: #333;
}

::-webkit-scrollbar {
  background-color: #aaa;
}

.model section.activity .view_all_images {
  color: #aaa;
}

/* fly over */
.flyover.story_flyover, .flyover.integration_story_flyover, .flyover.epic_flyover {
  background: none #444;
  color: #eee;
}

.flyover.story_flyover section.activity ol>li:nth-child(even) {
  background: none;
}

.flyover.epic_flyover section.activity ol>li:nth-child(even),
.flyover.followers_flyover section.activity ol>li:nth-child(even),
.flyover.integration_story_flyover section.activity ol>li:nth-child(even),
.flyover.story_flyover section.activity ol.comments>li:nth-child(even) {
  background: none #555;
}

.flyover .this_project .window .activity header .author div.initials {
  background-color: #E9E7DD;
  border-color: #444;
}

.flyover.story_flyover section h4, .flyover.integration_story_flyover section h4, .flyover.epic_flyover section h4, .flyover.followers_flyover section h4 {
  color: #aaa;
}

.flyover .tracker_markup a {
  color: #8BC5FF;
}

/* Increase font size and height for add comment field*/
.editor.std.add_comment {
  font-size: 150%;
  height: 5em;
}

/* Increase line height for li, so that inline code blocks have gap between */
.tracker_markup li {
  line-height: 1.8em;
}

  
}
