css:
@-moz-document domain(8ch.net), domain(55ch.org){

   body {
  background: #1C508A;
  background-repeat: no-repeat;
  background-position: center center;
  color: white;
  background-size: cover;
}
body::after {
    position: fixed !important;
        content: url('http://i.imgur.com/dMVO94d.png');
    bottom: -20px !important;
    right: -140px !important;
  opacity:1;
  z-index:-1;
transform: scale(0.7, 0.7);
-ms-transform: scale(0.7, 0.7);
-webkit-transform: scale(0.7, 0.7);
}
 .post-hover, .post.table.handle,post-table .handle,.handle{
background: #406C9D !important;
border: #406C9D 0px solid !important;
}
#options_div {
  width: 900px;
  height: 650px;
}
video{
border-radius: 15px;
}
#quick-reply th {
  border: 0px solid #222;
}
.watchlist-inner, .watchlist-controls {
    margin: 0pt;
    text-align: center;
    font-size: 10pt;
}

div.pages {
  background:  #113053 none repeat scroll 0% 0%;
  border: 0px;
  color: white;
}
div.pages a.selected {
    color:  #6EB7FC;
font-style: bold;
padding: 1px;
}

img.banner, img.board_image {
  display: block;
  border: 0px solid #A9A9A9;
  margin: 12px auto 0px;
}

.intro span.subject {
  color: #00C9FF;
  font-weight: bold;
font-size: 14pt;
}
.intro span.capcode, p.intro a.capcode, p.intro a.nametag {
    color: #2BC986 !important;
}
div.post i.fa, div.thread i.fa {
    margin: 0px 4px;
    font-size: 16px;
    color: #2BC986 !important;
font-weight: bold;}
span.gpg-signature{
color:#2BC986 !important;}
#quick-reply td {
  border-collapse: collapse;
  background: #161616 none repeat scroll 0% 0% !important;
  margin: 0px;
  padding: 3px;
}

div.boardlist {
  color: white;
}
.desktop-style div.boardlist:not(.bottom) {
    border-bottom: 0px solid;
    border-radius: 15px;
    size: unset;
}
div.post.op {
  color: white;
  font-weight: bold;
/*text-shadow:
    -1px -1px 0 black,
    1px -1px 0 black,
    -1px 1px 0 black,
    1px 1px 0 black;  
}*/;
}
.post-table, .post-table-options{
    width: 100%;
    background: #174374;
border-radius: 10px 10px 0px 0px;
    padding: 12px;
}
.post-table{
border-radius: 15px 15px 15px 15px;
}
.post-table-options{
border-radius: 0px 0px 15px 15px;
margin: -20px 0px 0px 0px;}
.post-menu ul {
  background-color: #FFF;
  color: black;
  border: 0px solid #666 !important;
  list-style: outside none none;
  padding: 0px;
  margin: 0px;
  white-space: nowrap;
}

div.body a {
  color: #fff !important;
}

pre.prettyprint {
  padding: 2px;
  border: 0px solid #888 !important;
  background-color: rgba(0,0,0,0.7);
border-radius: 15px;
  padding: 15px !important;
min-width: 500px !important;

}
.str{
color:#97E578 !important;}

.kwd{
color: #73A7D8 !important;}
.com{
color:#B9312E !important;}
.lit{
color: #05BFBF !important;}
.pun{
color: #FEFE3E !important;}
div.body a:hover {
  color: #6EB7FC !important;
}

.options_tab, #options_tablist {
  background-color: #161616 !important;
}
td {
    background: #174374 none repeat scroll 0% 0% !important;
}

#options_div, #alert_div {
  background-color: #161616;
  border: 0px solid #000;
}

#options_tablist {
  border-right: 0px solid #000;
}

.options_tab_icon {
  padding: 5px;
  color: #FFF;
  cursor: pointer;
}

.options_tab_icon.active {
  padding: 5px;
  color: #4C7DAD;
  cursor: pointer;
}

span.heading {
  color: #00C9FF;
  font-size: 14pt;
  font-weight: bold;
}

h2 {
  color: #4C7DAD;
}

div.post p {
  display: block;
  margin: 0px 15px 0px 6px;
  line-height: 1.16em;
  font-size: 13px;
  min-height: 1.16em;
}

.quote {
  color: #00C9FF;
}

div.post.reply, .theme-catalog div.thread {
  background: rgba(0,0,0,0.4) none repeat scroll 0% 0%;
  border: 0px;
  border-radius: 15px;
}

.post-image, .full-image {
  padding: 5px;
  margin: 0px 20px 0px 0px;
  border-radius: 15px !important;
}

.full-image {
  max-width: 99% !important;
}

#quick-reply .post-table, #quick-reply td {
  background: #212121 none repeat scroll 0% 0%;
}

.intro span.name {
  color: white !important;
  font-weight: bold;
}

div.post.reply.highlighted, .theme-catalog div.thread:hover {
  background: rgba(255,255,255,0.16);
}
.inline{
background: rgba(0,0,0,0.2);;
border: 0px !important;
padding: 15px;
border-radius: 15px;}
span.pln {
    color: white;
}

.desktop-style div.boardlist:not(.bottom) {
  background-color: rgba(27, 27, 27,1);
  color: white;
}

.intro a.post_no:hover {
  color: #6EB7FC;
}

form table tr th {
  background: #406C9D none repeat scroll 0% 0%;
  border: 0px;
}

.desktop-style div.boardlist:not(.bottom) {
  border-bottom: 0px solid;
  border-radius: 15px;
}

textarea {
  border: grey 0px solid;
  background-color:#202020;
color: white;
min-height: 19px;
min-width: 400px;
}
input{
background: #212121;
color: white;
  border: 0px solid !important;
}

div.banner {
  background-color: rgba(0,0,0,0);
  padding: 1px;
}

div.boardlist a, a:link{
  color: white;
}

div.boardlist a{
  color: white;
}

h1 {
  color: white;
}

header div.subtitle, h1 {
  color: white;
  text-align: center;
}

a:link:hover, a:visited:hover {
  color: #6EB7FC !important;
}

div.blotter {
  color: white;
  font-weight: bold;
  font-size: 14pt;
  text-align: center;
}

a, a:visited {
  color: white;
  text-decoration: none !important;
}
span.spoiler {
    background: white  none repeat scroll 0% 0%;
    color: white;
    padding: 0px 1px;
}
span.spoiler:hover{
    background: transparent none repeat scroll 0% 0% !important;
    color: white !important;
    padding: 0px 1px;
}
/*homepage - /boards.html configs*/
table.board-list-table tr:nth-of-type(2n) .board-tags .board-cell{
    background: #174374 none repeat scroll 0% 0%;;
}
table.board-list-table tr:nth-of-type(2n) .board-tags .board-cell:hover, table.board-list-table .board-tags .board-cell:hover{
background: #1C508A;}

table.board-list-table .board-uri .board-sfw {
    color: #FFF;
}

.box{
border:0px;}

table.board-list-table .board-uri, table.board-list-table .board-title, table.board-list-table .board-pph, table.board-list-table .board-unique, table.board-list-table .board-tags, table.board-list-table .board-max{
border:0px;
background: #4779B1;
color: white;}


tbody.board-list-omitted td {
border: 0px;}


.box-title {
    background: #4779B1 none repeat scroll 0% 0%;
    color: white;
}

div.col-5, div.col-5 a, aside.search-container .box a{
color: #1C508A;}
div.col-5 a:hover, aside.search-container .box a:hover{
color: #6EB7FC !important;
}

div.ban{
border: 0px;}
}
