css:
@-moz-document domain("jeuxvideo.com") {
/*Modifie le fond*/
#content
{
	background-image: url('http://image.noelshack.com/fichiers/2013/15/1365948173-simple-bleue.jpg');
    background-repeat: no-repeat;
    background-color: #02596A;
    background-position: center 70px;
    background-attachment: fixed;
    padding-bottom: 0px;
}

/*Modifie la taille banière pub*/
#prospect
{
    min-height: 0px;
    height: 45px;
}

/*Modifie la largeur contenu*/
.container
{
    width: 1000px;
}

/*Supprime Outbrain de merde*/
.bonus-track
{
    display: none;
}
.outbrain
{
    display: none;
}

/*Supprime partenaires*/
.titre-affli-ligne
{
    display: none;
}
#affli-ligne-blocs
{
	display: none;
}

/*Modifie la couleur header supérieur*/
#header-top
{
    background: #194D41;
}

/*Modifie la couleur header inférieur*/
#header-bottom, #header-bottom .bloc-sticker
{
    background: #347E6D;
}

/*Modifie la couleur bouton magasin*/
#header-store-jv #header-store-jv-link
{
    background-color: #347E6D;
}

/*Modifie logo JVC*/
#header-logo .a-logo
{
    background: url("http://image.noelshack.com/fichiers/2015/26/1435524787-header-logo-vert.png") left top no-repeat;
}

/*Modifie bouton Répondre Forum*/
.btn-repondre-msg
{
    border: 1px solid #194D41;
    background: #347E6D;
    color: #fff;
}

/*Modifie boutons sombres Forum*/
.btn-actu-new-list-forum
{
    border: 1px solid #194D41;
    background: #194D41;
    color: #fff;
}

/*Modifie page active*/
.bloc-liste-num-page .page-active
{
    background: #347E6D;
    color: #fff;
    padding: 3px 5px;
}

/*Modifie titre Forum*/
.titre-head-bloc-forum .titre-bloc-forum
{
    background: url("http://image.noelshack.com/fichiers/2015/26/1435526692-ico-titre-ff4000.png") no-repeat left 2px;
    color: #347E6D;
}

/*Modifie Header formulaire Forum*/
.bloc-header-form
{
    background: url("http://image.noelshack.com/fichiers/2015/26/1435526692-ico-titre-ff4000.png") no-repeat left 2px;
    color: #347E6D;
}

/*Modifie Bordure Fenêtre*/
.heading-pres1
{
    background: #347E6D;
}
.panel-jv-forum > .panel-heading
{
  background-color: #347E6D;
}

/*Cache WootBox*/
#prospect-square, #prospect-square2, #prospect-square3
{
    visibility: hidden;
    height: 0px;
}

/*Bouton Poster*/
.btn-poster-msg
{
	background: #347E6D;
}

/*Bouton Poster Survol*/
.btn-poster-msg:hover
{
  background: #347E6D;
  color: #000;
}

/*JVC en news a regarder*/
.slice-pres1:hover .titre-slice-pres1 .type
{
    color: #56AF59;
}
.slice-pres1 .type
{
    color: #59B15C;
}

/*Footer*/
#jv-footer .liens-pied
{
    border-top: 5px solid #347E6D;
    background: #194D41;
}
#jv-footer .liens-pied .a-footer
{
  color: #347E6D;
}

/*Cache highlights*/
.bloc-accueil
{
    height: 0px;
    display: none;
}

/*E3*/
.bandeau-e3
{
    background-color: #347E6D;
}

/*Liens*/
a, .lien-jv
{
  color: #194D41;
  text-decoration: none;
}
a, .lien-jv
{
  color: #347E6D;
  text-decoration: none;
}
a
{
  color: #347E6D;
  text-decoration: none;
}
a:hover, a:focus, a:active, .lien-jv:hover, .lien-jv:focus, .lien-jv:active
{
  color: #194D41;
  text-decoration: none;
}
.bloc-actu-home ul.list-actu-home .titre-actu:hover, .bloc-actu-home ul.list-actu-home .titre-actu:hover span, .bloc-actu-home ul.list-actu-home .titre-actu:focus, .bloc-actu-home ul.list-actu-home .titre-actu:focus span
{
  color: #59B15C;
  text-decoration: none;
}
.bloc-actu-home ul.list-actu-home .type-actu
{
  color: #59B15C;
}
.bloc-toute-actu > a
{
    color: #59B15C;
}
.item-home-accroche a:hover .group-wrapper .titre-wrapper
{
    color: #347E6D;
}
.item-small-pres .a-titre-item
{
    color: #5CB25F;
}
.officiel
{
    color: #194D41;
}
.titre-head-bloc .bloc-on-right.bloc-link-plus .lien-chev
{
    color: #5CB25F;
}
.bloc-dark-full-largeur .item-small-pres .a-titre-item:hover span, .bloc-dark-full-largeur .accr-item-large .a-titre-item:hover
{
    color: #5CB25F;
}
.bloc-onfire-home .menu-onfire span:hover
{
  color: #5CB25F;
}
.bloc-actu-home .menu-actu-home .filtre span:hover
{
  color: #5CB25F;
}
.bloc-fofo-boutique .bloc-autre-lien-fofo a
{
    color: #5CB25F;
}

/*Header titre*/
.titre-head-bloc .titre-bloc
{
    background: url("http://image.noelshack.com/fichiers/2015/26/1435526692-ico-titre-ff4000.png") no-repeat left 2px;
    color: #5CB25F;
}
.bloc-fofo-boutique .titre-fofo
{
    background: url("http://image.noelshack.com/fichiers/2015/26/1435526692-ico-titre-ff4000.png") no-repeat scroll left 4px rgba(0,0,0,0);
    color: #5CB25F;
}

/*Rectangles accroche*/
.item-home-accroche .mtag 
{
    background: #347E6D;
}
.accr-item-large .mtag
{
    background: #347E6D;
}
.bloc-onfire-home .number-fire
{
    background: #347E6D;
}

/*Texte fenêtre forum*/
.panel-jv-forum .titre-info-fofo
{
    color: #5EB361;
}

/*Fenêtre navbar*/
#navbar-jv .content-menu-jv
{
    border-top: 5px solid #59B15C;
    border-bottom: 5px solid #59B15C;
}
.mdd-liste-one li .big-one
{
      color: #55AE58;
}
.mdd-liste-one li .big-one:hover
{
      color: #194D41;
}
.mdd-liste-card-l .mtag, .mdd-card-xl .mtag
{
    background: #347E6D;
}
#navbar-jv .liste-rubrique > li.open
{
    background: #194D41;
}

/*Boutons liste*/
.type-card-off
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -40px 0 no-repeat;
}
.type-card-off:hover
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -40px -20px no-repeat;
}
.type-liste-on
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -20px -20px no-repeat;
}
.type-card-on
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -20px -20px no-repeat;
}
.type-liste-off
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -20px 0 no-repeat;
}
.type-liste-off:hover
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563521-ico-fx-liste-news.png") -20px -20px no-repeat;
}
.bloc-actu-home .menu-actu-home .filtre .actif
{
    color: #5DB360;
    background: url("http://image.noelshack.com/fichiers/2015/27/1435563971-barre-menu-actif.png") center bottom no-repeat;
    cursor: auto;
}
.bloc-onfire-home .menu-onfire .actif
{
  background: url("http://image.noelshack.com/fichiers/2015/27/1435563971-barre-menu-actif.png") no-repeat center bottom;
  color: #50B25F;
  cursor: auto;
}

/*WebTV*/
.glivetv .titre-bloc
{
    background: #5CB25F;
}
.glivetv .bloc-info-tv .titre-pg
{
    color: #5CB25F;
}
.glivetv .m-one.on, .glivetv .m-two.on
{
    background: #32383d;
    color: #5CB25F;
}
.glivetv .tab-chaine.on > div
{
    border-color: #5CB25F;
}
.glivetv .tab-chaine.on
{
    color: #5CB25F;
}
.glivetv .en-tete-bloc-pg
{
    color: #5CB25F;
}
.glivetv .separation
{
    color: #5CB25F;
}
.glivetv .ld-two
{
    color: #5CB25F;
}

/*Vidéo*/
.play-on-big
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435564726-ico-play-40.png") left top no-repeat;
}
.item-home-accroche .mask-img a.first-lien:hover .play-on-big
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435564726-ico-play-40.png") -40px top no-repeat;
}
.mdd-liste-card-l li .mdd-a:hover .play-on-big
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435564726-ico-play-40.png") no-repeat -40px top;
}
.accr-item-large .mask-img:hover .play-on-big
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435564726-ico-play-40.png") -40px top no-repeat;
}
.play-on-small
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435565932-ico-play-16.png") left top no-repeat;
}
.play-on-small:hover
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435565932-ico-play-16.png") -16px top no-repeat;
}
.item-small-pres .mask-img:hover .play-on-small
{
    background: url("http://image.noelshack.com/fichiers/2015/27/1435565932-ico-play-16.png") -16px top no-repeat;
}

/*Boutons Accueil*/
.bloc-chev-pix
{
    background: #347E6D;
}

/*Supprime bloc facebook*/
#wads_po_fanbox
{
    display: none;
}

/*Supprime pubs*/
#prospect #table-prospect
{
    display: none;
}
.bloc-fofo-boutique .part-boutique
{
    display: none;
}
#video-footer
{
    display: none;
}
#video-footer.active
{
    display: none;
}
.table-ending-home .bloc-fofo-boutique
{
  position: relative;
  padding: 20px 20px 0px 20px;
}
.bloc-boutique-droite
{
    display: none;
}
.prospect-interstice
{
    display: none;
}
}
