css:
@-moz-document domain("fanfiction.net"), domain("fictionpress.com") {
/*
STYLE AUTHOR: NHansen
STYLE URI: http://userstyles.org/styles/17556/
STYLE VERSION: 1.5.9
LAST UPDATE: MAR 26, 2014
*/

/************************************* 
HIDE ADVERTISEMENTS - Thanks, BullFrog
*************************************/  
#menu-extra-c,
DIV[style^="border-bottom"],
[id^="google_ads"],
[style="padding-left:1em;padding-right:1em;padding-top:0.5em;"] > DIV > TABLE[style="float:right"]:first-child, 
div#content_wrapper_inner > div.maxwidth,  
div#review > table[style="border=0 padding=0 cellspacing=0 style='width:100%;'"]:first-child  {
	display:none!important
	}

/************************************* 
BODY
*************************************/
* {
    text-shadow: none !important;
    }
body {
	background: #000 !important;
 	color: #aaa !important;
	}

body div {
	background-color: transparent !important;
	color: #9B2121 !important;
	}

div.sep {
	margin: 5px 0 5px 0 !important;
	border-bottom: 1px solid #2D2D2D !important;
	}

hr {
	background: #ccc none !important;
	color: #555 !important;
	size: 1px !important;
	opacity: .2;
	}

b {
 	color: #aaa !important;
	}

/************************************* 
WRAPPERS
*************************************/

div#content_wrapper_inner {
    background-color: #070707 !important;
    border-left: 1px solid #0f0f0f !important;
    border-right: 1px solid #0f0f0f !important;
    border-bottom: 1px solid #0f0f0f !important;
    }
       
.lc {
    background:#222 !important;
    border: 1px solid #333 !important;
    }

.lc-wrapper:after{
    border-top:solid 1px #333 !important;
    }

/************************************* 
BODY LINKS 
*************************************/

a:link, span.fake_link {
	color: red !important;
	text-decoration: none !important;
	border-bottom: 1px solid #333 !important;
	}

a:visited {
	color: red !important;
	text-decoration: none !important;
	border-bottom: 1px solid #333 !important;
	}

a:hover, a:visited:hover, span.fake_link:hover { 
	color: #fff !important;
	text-decoration: none !important;
	border-bottom: 1px solid #fff !important;
	}

a:active { 
	color: #78648c !important;
	text-decoration:none !important;
	}

a[href='http://m.fanfiction.net/m/yes_mobile.php'] { 
	border: 0 !important;
	}

/*************************************
TOP MENU LINKS
*************************************/

.zui a:link {
	color: red !important;
	background: #222 !important;
	text-decoration: none !important;
	border: none !important;
    border-radius: 4px !important;
	padding: 5px 10px 5px 10px !important;
	}

.zui a:visited {
	color: red !important;
	background: #222 !important;
	text-decoration: none !important;
	border: none !important;
	}

.zui a:hover, .zui a:visited:hover { 
	color: #fff !important;
	background: #333 !important;
	text-decoration:underline !important;
	}

.zui a:active { 
	color: #78648c !important;
	background: #333 !important;
	text-decoration:none !important;
	}
	
li.divider {
	background: #444 !important;
	border: 0 !important;
	}
	
.dropdown-menu li {
	margin: 1px !important;
	}
	
ul.dropdown-menu {
	background: #222 !important;
	}
	
.btn {
	background: #222 !important;
	color: #fff !important;
	border: 1px solid #555 !important;
	box-shadow: 0px 0px 0px !important;
    }

/*************************************
TOP MENU/NAVIGATION BARS
*************************************/

.zmenu {
	background-color: #0f0f0f !important;
	border-bottom: 1px solid #2D2D2D !important;
	border-top: 1px solid #181818 !important;
	padding: 0px !important;
	}

.menu-child {
	background-color: #181818 !important;
	border-bottom: 1px solid #2D2D2D !important;
	}

.zhide {
	display:none;
	}
	
div[style='width:100%;border-bottom:1px solid #CDCDCD;background-image:url("http://ffcdn2012.fictionpressllc.netdna-cdn.com/static/images/adbg.gif");'] {
    display:none !important;
    }

/* for fictionpress.com */
table.xxmenu {
	background-color: #000 !important;
	border-bottom: 1px solid #2D2D2D !important;
	}
	
/*************************************
INPUTS (DROP-DOWNS, SELECTION, TEXTAREAS, ETC.)
*************************************/

select, input {
	background: #111 !important;
	color: #ccc !important;
	border: 1px solid #2D2D2D !important;
	-moz-appearance: none !important;
	margin-right: 3px!important;
	}

input.searchfield {
	border: 1px solid #666 !important;
	}

textarea {
	background: #050505 !important;
	color: #ccc !important;
	border: 1px solid #2D2D2D !important;
	-moz-appearance: none !important;
	}

select:hover, select:focus, input:hover, input:focus, textarea:hover, textarea:focus {
	border: 1px solid #666 !important;
	}

/*************************************
TABLE COLORING/FIXES
*************************************/

.tborder, .trowh {
	background: #333 !important;
	border: 0px solid #2D2D2D !important;
	}

.tcat {
	background: #222 !important;
	border: 1px solid #2D2D2D !important;
	border-bottom: 0px solid !important;
	}

.tcat a:link, .tcat a:visited {
	color: #fff !important;
	}

.tcat a:hover, .tcat a:active {
	color: #666 !important;
	}

.thead {
	background: #222 !important;
	color: #ccc !important;
	}

.myborder {
	background: #0F0F0F !important;
	border: 1px solid #2D2D2D !important;
	padding-bottom: 5px !important;
	}

.myborder.menulinks {
	background: #222 !important;
	border: 1px solid #2D2D2D !important;
	padding-bottom: 0px !important;
	}

iframe {
	border: 0px !important;
	}

#list_output table tr td {
	border-style: solid  !important;
 	border-color: #222 !important;
	border-width: 1px 1px 1px 3px !important;
	padding-left: 5px !important;
	background: #090909 !important;
	}
	
td[style="width:260px;vertical-align:top;padding:5px;border: solid 1px #6581C1;-moz-box-shadow: -1px 0px 2px #cdcdcd;-webkit-box-shadow: -1px 0px 2px #cdcdcd;box-shadow: -1px 0px 2px #cdcdcd;"] {
    border: solid 1px #2D2D2D !important;
    -moz-box-shadow: 0px 0px 0px !important;
    -webkit-box-shadow: 0px 0px 0px !important;
	box-shadow: 0px 0px 0px !important;
	background-color: #0F0F0F !important;
	}
 
.table-bordered {
    border: solid 0px #2d2d2d !important;
    }
    
.table-bordered td {
    border: 0 !important;
    }
    
th {
    border-top: 1px solid #333 !important;
    border-left: 0 !important;
    border-radius: 1px !important;
    }
    
#gui_table1i td {
    border: 0 !important;
    background-color: #111 !important;
    }
    
#gui_table2i {
    background-color:#070707 !important;
    }

#gui_table3i td {
    border: 0 !important;
    }

.table-striped tbody>tr:nth-child(even)>td,.table-striped tbody>tr:nth-child(even)>th{
    background-color:#070707 !important;
    }

#bio_tbl {
    background:#333 !important; 
    border: 0 !important;
    }

span.lc-left {
    background: #070707 !important;
    }

/* profile tabs */
    
#l_st, #l_fs, #l_fa {
    background-color: #111 !important;
    border-color: #333 !important;
    }    

.favstories[style='min-height:77px;border-bottom:1px #cdcdcd solid;'], .mystories[style='min-height:77px;border-bottom:1px #cdcdcd solid;'], #mytab {
    border-bottom: 1px #333 solid !important;
    }
    
span.badge {
    background-color: #555 !important;
    }
    
div[style='font-size:0.9em;color:gray;'] > span.gray {
    color: red !important;
    cursor: pointer;
    border-bottom: 1px solid #333;
    }

div[style='font-size:0.9em;color:gray;'] > span.gray:hover {
    color: #fff !important;
    }

/*************************************
BORDERS
*************************************/

.myborder_no_bottom, .myborder_only_bottom, .myborder_only_leftbottom {
	border-style: solid !important;
	border-color: #2D2D2D !important;
	}

.myborder_only_bottom.grayback {
	background-color: #111 !important;
	}

img.cimage {
    border: 1px solid #2D2D2D !important;
    }
 
#xtab ul#primary a {
    border: 1px solid #2D2D2D !important;
    border-bottom: 1px solid #2d2d2d !important;
    background-color: #0f0f0f !important;
    }

#xtab ul#primary a.current {
    border-bottom: 1px solid #070707 !important;
    background-color: #070707 !important;
    }

#xtab ul#primary a:hover {
    border-bottom: 1px solid #555 !important;
    background-color: #2d2d2d !important;
    }
    
div[style='border:1px solid #666;padding-top:5px;'] {
    background-color: #070707 !important;
    border: 1px solid #2d2d2d !important;
    }

/*************************************
LOGIN AREA + ALTERNATING BACKGROUNDS
*************************************/

.topnav {
	border: 1px #2d2d2d solid !important;
	}

ul.topnav li a {
	border-bottom: 1px solid #111 !important;
	color: red !important;
	}

ul.topnav li a:hover {
	background-color: #333 !important;
    border-bottom: 1px solid #222 !important;
	color: #fff !important;
	text-decoration:underline !important;
	}

ul.topnav ul li a {
	border-bottom: 1px solid #111 !important;
	color: #777!important;
	}

ul.topnav ul li ul li a {
	border-bottom: 1px solid #111 !important;
	color: #bbb !important;
	}

ul.topnav ul li a:hover, ul.topnav ul li.active, ul.topnav ul ul li a:hover {
	background-color: #222 !important;
	}

.table-striped tbody>tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color:#111 !important
    }
    
#gui_table1i tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color:#070707 !important
    }

.fhigh {
	background: #333 !important;
	}
	
.xtabc, .admintabs a{
	border-color: #2D2D2D !important;
	}

.admintabs a:hover, .admintabs a.selected, td[bgcolor="#f0f0f0"] {
	background: #555 !important;
	}

a.positive {
	border: 1px solid #2D2D2D !important;
	}

div.panel_normal, div.panel_warning, div.panel_success {
	border: 1px solid #666 !important;
	background-color: #0f0f0f !important;
	color: red !important;
	}

span.gui_warning, span.gui_success {
	color: red !important;
	}

div.panel_normal hr, div.panel_warning hr, div.panel_success hr {
	background-color: #966eb4 !important;
	color: #966eb4 !important;
	}

/*************************************
TABLE SPACING
*************************************/

table#gui_table15i,table#gui_table14i,table#gui_table13i,table#gui_table12i,
table#gui_table11i,table#gui_table10i,table#gui_table9i,table#gui_table8i,
table#gui_table7i,table#gui_table6i,table#gui_table5i,table#gui_table4i,
table#gui_table3i,table#gui_table2i,table#gui_table1i {
	border-spacing: 0px !important;
	border: 1px solid #2D2D2D !important;
	-moz-appearance: none !important;
	border-style: solid !important;
	}

.x-high, .z-high {
	background: #111 !important;
	}

/*************************************
REVIEW BUTTON CUSTOMIZATION
*************************************/

.xinput_style {
	color: #aaa !important;
	border: solid 1px #2D2D2D !important;
	background-color: #111 !important;
	background: -webkit-gradient(linear, left top, left 30, from(#111), color-stop(4%, #111), to(#111)) !important;
	background: -moz-linear-gradient(top, #111, #111 1px, #111 30px) !important;
	}

.share_link a {
	color: #966eb4 !important;
	}
	
div.toast {
    background: rgba(0,0,0,.8) !important;
    border-color: #2D2D2D !important;
    color: #aaa !important;
    }

div.modal-body, div.modal-footer {
    background: rgba(0,0,0,.8) !important;
    border: 1px solid #2D2D2D !important;
    color: #aaa !important;
    }
    
div.modal-body td { 
    color: #aaa !important;
    }
    
label.checkbox {
    color: #aaa !important;
    margin-bottom: 5px !important;
    }
    
table[style='padding:6px;color:black !important;'] {
    color: #aaa !important;
    }
    
a.xdrop_login:hover, ul.dropdown-menu a:hover {
    background: #333 !important;
    border-bottom: 1px solid #444 !important;
    }
    
/************************************* 
FAKE LINKS FIX 
*************************************/
 
div.simplemodal-container {
    background-color: #0F0F0F !important;
    border: 1px solid #2d2d2d !important;
    }
    
div.simplemodal-overlay {
    background-color: #111 !important;
    }

/************************************* 
MISC FIXES 
*************************************/

.gray {
	color: #bbb !important;
	font-style: italic !important;
	}

.bm {
	border-bottom: 1px solid #2D2D2D !important;
	}

.drop_m {
	border-bottom: 1px solid #2D2D2D !important;
	border-top: 1px solid #2D2D2D !important;
	}

div[style="height:10px;border-top:1px solid #cdcdcd;"] {
	border-top: 1px solid#666 !important;
	margin-top: 5px !important;
	margin-bottom: 5px !important;
	}

span.sort-sort {
    color: red !important;
    }
    
.lc > span#bio_text {
    cursor: pointer;
    }

/*************************************
Firefox 3 Top Menu Fix - DO NOT DELETE! DO NOT DELETE! DO NOT DELETE! DO NOT DELETE!
*************************************/

div.menu-child form {
	width: 50% !important;
	}
}

@-moz-document domain("m.fanfiction.net"), domain("m.fictionpress.com") {
/***** navigation tabs/bar at top *****/
.bm a:link, a:visited {
	background: #222 !important;
	text-decoration:none !important;
	border-style: solid !important;
	border-width: 1px 1px 0px 1px !important;
	border-color: #444 !important;
	}

.bm a:hover, a:active {
	background: #444 !important;
	text-decoration:none !important;
	border-style: solid !important;
	border-width: 1px 1px 0px 1px !important;
	border-color: #555 !important;
	}

.t_head {
	border-bottom: 1px solid #555 !important;
	background: #000 !important;
	}
	
.v_divider {
	background: #555 !important;
	}
	
#t_drop {
    background: #222 !important;
    border-bottom: 1px solid #333 !important;
    }

/***** review button *****/
.xrnd {
	-moz-border-radius: 0px !important;
	-webkit-border-radius: 0px !important;
	}

.xbut {
	padding: 4px;
	border: 1px solid #555 !important;
	background-color:#333 !important;
	}

/***** share div (bottom of chapters where you share, change text size, follow, etc.) *****/
.drop_m {
	background-color: #111 !important; 
	padding-bottom: 5px !important; 
	border-bottom: 1px solid #333 !important;
	border-top: 1px solid #333 !important;
	}

/***** alternating backgrounds *****/
.bs.alto.brb {
	border-color: #666 !important; 
	background: #0f0f0f !important;
	color: #999 !important;
	}

.bs.brb {
	border-color: #333 !important; 
	background: #000 !important;
	color: #999 !important;
	padding: 5px !important;
	}

.bs.brb:hover {
	background: #222 !important;
	}
	
#gui_table2i td {
    border: 0 !important;
    background-color: #222 !important;
    border-bottom: 1px solid #333 !important;
    padding: 5px !important;
    }
    
#gui_table2i tr:nth-child(odd)>td,.table-striped tbody>tr:nth-child(odd)>th{
    background-color:#111 !important;
    }
}
