css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ijreview.com") {
  /*Made by Brian Owens
  If you like this then please donate.
  If you want to change the code then please donate.*/
.SandboxRoot {  
  color: #fff;
  font: 16px/1.4 Helvetica,Roboto,"Segoe UI",Calibri,sans-serif;
}
  
blockquote, button, h1, h2, h3, h4, h5, h6, iframe, ol, p, ul {
    border: medium none;
    color: #fff;
    list-style: outside none none;
    margin: 0;
    padding: 0;
}
.site-content .entry-title {
  color: #000066;
  font-family: "Roboto Condensed","Helvetica";
  margin-top: 0;
}
.site-content .entry-content {
  color: #000066;
  font-family: "Droid Serif",serif;
  font-size: 15px;
  line-height: 1.7em;
}
#login-overlay {
  align-items: center;
  background-color: #c0c0c0;
  color: white;
  display: flex;
  font-weight: 200;
  height: 100%;
  margin-top: 48px;
  padding-bottom: 40px;
  width: 100%;
  z-index: 90;
}
.container {
  width: 1340px;
}
.news-single-column .col-md-9 {
  margin-left: 31px;
}
#floating_sidebar .container .post_stats .number {
  color: white;
  font-family: "Roboto Condensed";
  font-size: 16px;
  margin-bottom: 0;
  margin-top: 10px;
  text-align: center;
}
.sidebar {
  background-color: #480000;
  display: none;
  height: 100vh;
  position: fixed;
  top: 0;
  width: 275px;
  z-index: 50001 !important;
}
#floating_sidebar {
  display: inline;
  left: 50%;
  margin-left: -930px;
  position: fixed !important;
  width: auto;
  z-index: 1;
}
.options .select_wrapper select option {
  color: white !important;
}
header#masthead, header {
  background: #400000 none repeat scroll 0 0;
  color: #a0a0a0;
  height: 48px;
  left: 0;
  opacity: 0.9;
  position: fixed;
  text-align: center;
  top: 0;
  width: 100%;
  z-index: 100;
}
body {
  background-color: #c0c0c0;
}
footer {
  background-color: #480000;
  color: white;
  display: block;
  font-family: "Roboto Condensed",sans-serif;
  height: auto;
  padding: 30px;
  position: relative;
  z-index: 200;
}
}
