css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.reddit.com") {
  /*HEADER*/
  
  #header{border-bottom: 1px solid #111;}
  
  .res-nightmode #header, .res-nightmode #header-bottom-left {
    background: #333 none repeat scroll 0 0 !important;
  }
  .pagename a{color: #CCC!important; vertical-align: 0.75em!important; font-weight: normal!important; padding: 20px!important;}
  
  /*hot new rising etc. buttons*/
  
  a{color: #BBB}
  .tabmenu{vertical-align: 1em;}
  .tabmenu li a{
    font-weight: normal!important;
    background: rgba(0,0,0,0)!important;
  }
  .tabmenu li.selected a{
    color: #FFF;
    background: transparent -moz-linear-gradient(center top , #333, #2D2D2D) repeat scroll 0% 0%!important;
    border: 1px solid #222!important;
    padding: 5px;
    border-radius: 5px;
  }
  
   /*Top RES header*/
  
  .res-nightmode #sr-header-area{background: #333!important;}
  #sr-header-area{border-bottom: 1px solid #222;}
  .sr-bar a{color: #438BB7;}
  #RESShortcutsViewport{color: #888;}
  
  .res-nightmode div#RESShortcutsSort, .res-nightmode div#RESShortcutsEditContainer,
  .res-nightmode div#RESShortcutsRight, .res-nightmode div#RESShortcutsLeft,
  .res-nightmode div#RESShortcutsAdd, .res-nightmode div#RESShortcutsTrash {
    background-color: #333!important;
  }
  
  /*My Subreddits*/
  
  #srList td a {
    color: #000!important;
  }
  
  /*Account info/toolbar*/
  
  .res-nightmode #header-bottom-right{background-color: #292929!important;}
  #userbarToggle{
    color: #888!important;
    background-color: #222!important; 
    border-right: 1px solid #1E1E1E!important;
  }
  .user{color: #FFF!important;}
  
  .res-nightmode .RES-keyNav-activeElement a.title:first-of-type {
    color: #dddddd !important;
  }
  
 /*Reddit stories/links*/ 
  
  .sitetable>div:nth-of-type(4n+1){background: #2E2E2E!important;}
  .sitetable{background: #333;}
  .res-nightmode .RES-keyNav-activeElement{background: rgba(0,0,0,0)!important; color: #F00!important; background-color: transparent!important;}
  .link .score{color: #666}
  .res-nightmode .entry time{color: #DDD!important;}
  
  .res-nightmode .NERPageMarker{background-color: #2E2E2E!important; border: 1px solid #555}
  
  .tagline .stickied-tagline{color: #967AFF}
  .thing .tagline .author.moderator{background-color: #7A57FF!important;}
  
  .res-nightmode .buttons .stamp{color: #F00!important;}
  
  /*Side bar*/
  
  .side{padding-left: 1em;}
  .sidebox .subtitle{padding-bottom: 1em;}
  
  .res-nightmode .side, .res-nightmode .side .titlebox, .res-nightmode .side .titlebox form.flairtoggle, 
  .res-nightmode .side .spacer .titlebox .redditname, .side .md li, .res-nightmode body .content, .res-nightmode body{
    background-color: #333!important;
  }
  
  .res-nightmode .sidecontentbox .content{background-color: #313131!important; border-color: #272727!important;}
  
  #search input[type="text"]{
    border: 1px solid #222;
    border-radius: 2px;
  }
  .morelink, .morelink a, .fancy-toggle-button .remove, .fancy-toggle-button .add{
    background: transparent -moz-linear-gradient(center top , #B44747, #AB3C3C) repeat scroll 0% 0%!important;
    border-color: #933333!important;
    color: #FFF7F7!important;
    border-radius: 2px!important;
  }
  
  .res-nightmode .goldvertisement{background-color: rgba(0,0,0,0)!important; box-shadow: none!important;}
  .goldvertisement .progress p{color: #FFF}
  
  /*Comments*/
  
  .res-nightmode .usertext-edit textarea{
    background-color: #292929!important; 
    border: 1px solid #222!important;
    border-radius: 2px!important;
  }
  .res-nightmode .markdownEditor .edit-btn:not(.btn-macro){
    background-color: #606060!important;
    border: 1px solid #808080!important;
  }
  .res-nightmode .content button{background-image: transparent -moz-linear-gradient(center top, #4B4B4B, #454545) repeat scroll 0% 0%!important;}
  .md blockquote{color: #888!important;}
  .res-nightmode.res-commentBoxes .comments-page .comment{background-color: #F00!important;}
  
  pre, code {
    background-color: #181D20 !important;
    border: 1px solid #343C45 !important;
    color: #B6BABF !important;
  }
  
  .res-nightmode .RES-keyNav-activeElement, .res-nightmode .RES-keyNav-activeElement .usertext-body, 
  .res-nightmode .RES-keyNav-activeElement .usertext-body .md, .res-nightmode .RES-keyNav-activeElement .usertext-body .md p, 
  .res-nightmode .commentarea .RES-keyNav-activeElement .noncollapsed, .res-nightmode .RES-keyNav-activeElement .noncollapsed .md, .res-nightmode .RES-keyNav-activeElement .noncollapsed .md p{
    color: #FFF!important; 
    background-color: transparent!important;
  }
}
