css:
@-moz-document domain("online.s-pankki.fi") {
.navigation ul.level-1 li a, .navigation ul.level-1 li a span, body.default-skin .navigation ul.level-1 li a span{
    background: none;
    background-image: none !important;
}
.cf-container,.cf-content,cf-bottom .content{
	min-height: 0;
}
.cf-header .content{
    background: none;
}
.cf-header .navigation{
    border-bottom: 0;
    position: static;
}
body.fi .cf-header .logo a{
    background-image: url('https://www.s-pankki.fi/Assets/img/logos/s-pankki-logo-880px.png?width=200&quality=90');
}
body{
    background: #7AB837;
}
.cf-bottom .content{
    background: none;
}
.cf-bottom{
    background: #5B8F22;
    padding: 24px 0;
}
.cf-content.cfSp{
    border-radius: 5px;
    box-shadow: 1px 1px 2px rgba(0,0,0,0.3333);
    margin-bottom: 32px;
}
.logout{
	float: right;
    position: static;
}
.logout a.customer-name{
	color: #FFF;
    display: block;
    font-weight: bold;
    text-align: center;
}
.logout a.button .btn-small{
    background: #FFA200;
    padding: 4px;
    border-bottom: 2px solid rgba(0,0,0,0.15);
    color: #FFF;
    text-transform: uppercase;
}
.logout .btn-small span{
    background: none;
}
#footer{
    text-align: left;
	color: #FFF;
}
.print-link,#to-top-link{
    display: none;
}
.wrapper .cf-container.clear{
    background: none;
}
ul#footer li.helplinks{
	border-right: 0;
    padding: 0 8px;
}
ul#footer li.helplinks a{
    color: #FFF;
}
ul#footer li#date{
    display: none;
    position: absolute;
}
ul#footer li#copyright{
    font-weight: bold;
}
ul#footer li#powered-by{
    opacity: 0.5;
}
}
