css:
@-moz-document url-prefix("https://www.ingress.com/intel"), url-prefix("https://www.ingress.com/mission") {
#nav,
#play_button,
#header_login_info,
#header_login_info_box,
#player_stats,
#game_stats
{
	display: none;
}

#dashboard_container
{
	left: 0;
	right: 0;
	top: 28px;
	bottom: 27px;  
}

#geotools
{
	right: -1px;
	top: -29px;
}

#header
{
	margin: 0;
  	padding: 0;
}

#header .separator
{
	margin-left: 0;
	top: 2px;
}

.header_icon_container
{
	float: left;
	height: auto;
	width: auto;
	z-index: 1;
	position: relative;
	margin-right: 15px;
	margin-left: 18px;
	margin-top: 3px;  
}

.header_icon
{
	float: left;
}

.header_icon_text
{
	margin-top: 5px;
	float: right;
	margin-left: 3px;
}

.header_box
{
	top: 28px;
	left: 0px;
}

#portal_filter_header
{
	bottom: -28px;
	right: -1px;
}

#tm_button
{
	right: 90px;
	bottom: -28px;
}

#filters_container,
#tm_view_container
{
	right: 0;
	bottom: 0;
}

#loading_msg
{
	bottom: -28px;
	right: 170px;
	height: 25px;
	line-height: 21px;
	overflow: hidden;
}

#loading_percent
{
  transform: scale(0.5);
  margin-top: -10px;
}

#comm
{
	bottom: -28px;
	left: 0px;
}

#health_slider_container
{
	width: auto;
}
}
