css:
@-moz-document
	url-prefix('https://www.tumblr.com/')
{

SVG.compose-button {fill: rgba(255,255,255,.5)}
DIV.glass.blue {background-color: rgba(0,0,0,.9)}

DIV.l-header-container {background-color: rgba(0,0,0,.2)}
DIV.l-header-container:hover {background-color: rgba(0,0,0,1)}
#right_column .controls_section LI:hover:not(.section_header) {background-color: rgb(0,0,0);}
BUTTON.compose-button {
	background-color: transparent;
	border-color: #808080;
}
BUTTON.compose-button:hover {border-color: #c0c0c0}
BUTTON.compose-button:hover:before {color: #c0c0c0}
BUTTON.compose-button:before {
  color: #808080;
}

DIV.post_avatar {background-color: transparent}

/* blue fix */
/*div.search_form_row*/#search_query {background-color: rgba(100,100,100,0.2)}
div.l-header .selection_nipple {border-bottom-color: rgba(100,100,100,0.2)}

#left_column, #right_column {
	background-color: transparent;
}
.follow_list_item_blog:before {
  background-image: none !important;
  border-right: none !important;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.blog.has_follow_button:before,
#right_column:after {display: none}

BODY:not(.mceContentBody), #new_post.editing {
	background: linear-gradient(to top, rgba(255,255,255,.15), rgba(255,255,255,.15)),
	  url('https://40.media.tumblr.com/dafb726eba197ecc3f29b0cbaf59e572/tumblr_ndipcd0Wiv1rxghfko2_1280.jpg')
	  center #000000 no-repeat fixed !important;
	background-size: cover !important;/*++*/
}
.new_post .title, .new_post .tag_editor {
  background-color: rgba(255,255,255,1) !important;
}

ol#posts li blockquote,
body.mceContentBody blockquote {
	margin-left: 0 !important;
	margin-right: 0 !important;
	padding-left: 10px !important;
	border-width: 4px !important;
	border-color: #9d9d9d!important;
}

ol#posts li.notification.single_notification {
	border-bottom: 1px solid #000000;
}

.l-content {
	background: transparent !important;
	border-radius: 20px !important;
}

h1 {
	color: #9d9d9d;
	text-shadow: 0 0 3px #ffffff;
}

.no_posts_found, h1.dashboard_header {
	color: #9d9d9d;
}

#right_column a#dashboard_switch_blog_menu_current #dashboard_switch_blog_arrow_current {
	background: none;
}

#right_column .dashboard_nav_item {
	background: none;
}

#right_column .dashboard_nav_item #hide_radar {
	background: transparent;
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media.photo, #right_column .dashboard_nav_item .dashboard_controls_radar_media.photoset, #right_column .dashboard_nav_item .dashboard_controls_radar_media.video {
	background: transparent;
}

#nav .nav_item.active {
	background: transparent !important;
}

#nav .nav_item .nav_item_nipple .nav_item_nipple_pixel {
	background: transparent !important;
}

ol#posts li.notification.alt {
	background-color: transparent ;
}

ol#posts li.notification.first_notification {
	background-color: transparent;
}

ol#posts li.notification {
	background-color: transparent;
    border-bottom: 1px solid #000000;
}

ol#posts li.notification.last_notification {
	border-bottom: 1px solid #ffffff;
}

ol#posts li.notification a {
	color: #ffffff;
	border-bottom: 1px solid #dcdcdc;
}

ol#posts li.post .permalink {
	background: transparent;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs {
	background: transparent ;
	border: solid #000000;
}

ol#posts li.post .post_avatar{
	border-bottom: 0px solid #9d9d9d;
}

#right_column .dashboard_nav_item #dashboard_controls_suggested_blogs .dashboard_controls_suggested_blog {
	border-top: 1px solid #000000;
	border-bottom: 1px solid #000000;
}

#right_column a#dashboard_switch_blog_menu_current:hover {
	background: none;
}

#right_column a#dashboard_switch_blog_menu_current:hover #dashboard_switch_blog_arrow_current {
	background: none;
}

form.dashboard_options_form {
	background-color: transparent ;
	border-bottom: 1px solid #000000;
	border-radius: 10px;
	color: #cccccc;
	display: block;
	font-size: 13px;
	margin-bottom: 25px;
	padding: 15px 22px;
}

form.dashboard_options_form .option_container {
	border-top: 1px solid #000000;
}

.no_posts_found, h1.dashboard_header {
	color: #9d9d9d;
}

.answer_container {
	background-color: #000000;
	border-bottom: 0px solid #9d9d9d;
}

#nav .nav_item .nav_menu {
	background-color: #000000;
	border-bottom: 2px solid #9d9d9d;
}

#nav .nav_item .nav_menu a {
	border-top: 1px solid #9d9d9d;
}

#auto_pagination_loader {
	background: none;
	border-top: 0px solid #9d9d9d;
}

#right_column .dashboard_nav_item a:hover {
	color: #cccccc;
}

#right_column .dashboard_nav_item a {
	color: #ffffff;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a:hover {
	color: #cccccc;
}

#right_column .dashboard_nav_item ul.dashboard_subpages li a {
	color: #ffffff;
}

#right_column .dashboard_nav_item .dashboard_nav_title {
	color: #ffffff;
}

#right_column {
	color: #ffffff;
}

#right_column a#dashboard_switch_blog_menu_current {
	color: #f2f2f2;
}

#nav .nav_item {
	color: #000000;
}

#nav .nav_item:hover {
	color: #333333;
}

.new_post_notice {
	background-color: #333333;
}

.new_post_notice img {
	opacity: 0;
}

#search_box input {
	border: 1px solid #cccccc;
}

#footer, #footer .footer_link > A  {
	color: #ffffff !important;/*Cr*/
}

.new_post_label {
	padding: 71px 1px 8px 0 !important;
	font-variant: small-caps;
}
.new_post_label > I::before {content: none}
A.new_post_label > I {
/*  color: transparent !important;*/
	transition: background-position .08s linear;
	height: 90px;
	background: url('https://40.media.tumblr.com/8c752b6077c34d59db59f1c4628015c6/tumblr_ndipcd0Wiv1rxghfko1_1280.png') no-repeat;
}
#new_post_label_text > I {background-position: 2px -5px !important}
#new_post_label_photo > I {background-position: -73px -5px !important}
#new_post_label_quote > I {background-position: -148px -5px !important}
#new_post_label_link > I {background-position: -223px -5px  !important}
#new_post_label_chat > I {background-position: -298px -5px !important}
#new_post_label_audio > I {background-position: -373px -5px !important}
#new_post_label_video > I {background-position: -448px -5px  !important}

#new_post_label_text:hover > I {background-position: 2px -9px !important}
#new_post_label_photo:hover > I {background-position: -73px -9px !important}
#new_post_label_quote:hover > I {background-position: -148px -9px !important}
#new_post_label_link:hover > I {background-position: -223px -9px  !important}
#new_post_label_chat:hover > I {background-position: -298px -9px !important}
#new_post_label_audio:hover > I {background-position: -373px -9px  !important}
#new_post_label_video:hover > I {background-position: -448px -9px  !important}

#new_post_label_text:active > I {background-position: 2px -5px !important}
#new_post_label_photo:active > I {background-position: -73px -5px !important}
#new_post_label_quote:active > I {background-position: -148px -5px !important}
#new_post_label_link:active > I {background-position: -223px -5px !important}
#new_post_label_chat:active > I {background-position: -298px -5px !important}
#new_post_label_audio:active > I {background-position: -373px -5px !important}
#new_post_label_video:active > I {background-position: -448px -5px !important}

.logo .svg-logo {display: none}
H1.logo/*.logo-redesign*/ .logo-anchor {height: 100%}

/*#logo {*/
H1.logo/*.logo-redesign*/ {
	position: absolute;
	height: 65px;
	width: 209px;
	margin: 4px 0 0 4px;
}
#search_field {margin-left: 229px}
/*#logo > .logo_anchor {*/
H1.logo/*.logo-redesign*/ > .logo-anchor {
	display: block;
	background-size: contain;
	background-repeat: no-repeat;
	background-image: url('https://40.media.tumblr.com/f2233b4fb977bfd006e6ab5e5a89b6fc/tumblr_ndipcd0Wiv1rxghfko3_r1_250.png');
}

#right_column .dashboard_nav_item .dashboard_controls_radar_media {
	background: transparent;
	color: #1F2B38;
	font: normal normal normal 12px/normal 'Lucida Grande', Verdana, sans-serif;
	line-height: 16px;
	margin: 10px auto 0px;
	padding: 4px 6px;
	position: relative;
	width: 190px;
	word-wrap: break-word;
}

#dashboard_controls_radar_media_fold {
	opacity: 0;
}

#edit_post_controls #slug_url {
	color: #ffffff;
}

h2 {
	color: #ffffff;
}

#promos .promo .tidbit {
	color: #ffffff;
}

#edit_post_controls .advanced_option label {
	color: #ffffff;
}

#right_column:after {display:none}/*++*/

}
