/* aGENT_SHEET */
/** Black (2009) Perfect Style! [YouTube] **/
/*
 * YouTube - dark gray redesign
 *
 * Author      : Valacar
 * Updated     : June 17th, 2009
 * Website     : http://www.valacar.com/userstyles/
 * Description : Modifies the colors, logos, and a few other images on YouTube (http://youtube.com/)
 * Usage       : Use with Stylish Firefox extension (http://userstyles.org/) or copy to your Firefox userContent.css file
 */

@-moz-document domain("youtube.com") {

div#yt-masthead-container,
div#masthead-appbar {
  background: #222;
}
#masthead-positioner {
  background: linear-gradient(to top, #222, black);
}

button.yt-uix-button-primary {
  background-color: #333;
}
button.yt-uix-button-primary:hover {
  background-color: #3b3b3b;
}


/* "Subscribe" */
button.yt-uix-button-subscribe-branded {
  background-color: #222 !important;
  }
button.yt-uix-button-subscribe-branded:hover {
  background-color: #666 !important;
  }
button.yt-uix-button-subscribe-branded .yt-uix-button-content > SPAN {
  color: #666 !important;
  }
button.yt-uix-button-subscribe-branded:hover .yt-uix-button-content > SPAN {
  color: #000 !important;
  }
button.yt-uix-button-subscribe-branded:before {
  background: transparent;
  border: 4px solid transparent;
  border-left: 6px solid #666;
  border-right-width: 0;
  width: 0; height: 0;
  }
button.yt-uix-button-subscribe-branded:hover:before {
  border-left-color: #000;
  }

  /* home page blurbs */
.branded-page-module-title SPAN,
.branded-page-module-title A:link,
.branded-page-module-title A:visited,
.yt-lockup .yt-lockup-title A,
.yt-lockup .yt-lockup-byline,
.yt-lockup .yt-lockup-byline A,
.yt-lockup .yt-lockup-meta LI {
  color: #666 !important;
}
.yt-lockup {background-color: #222}
.yt-lockup:hover {background-color: #666}
.yt-lockup:hover .yt-lockup-title A {
  color: #000 !important;
}
.yt-lockup:hover .yt-lockup-byline,
.yt-lockup:hover .yt-lockup-byline A,
.yt-lockup:hover .yt-lockup-meta LI {
  color: #000 !important;
}
.yt-lockup .yt-lockup-title .yt-ui-ellipsis {
  background-color: inherit !important;
}

.branded-page-module-title:hover {
  color: #000;
}
.feed-item-container:hover .branded-page-module-title-text,
.feed-item-dismissable:hover .branded-page-module-title:hover A {
  color: #fff !important;
}

/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
.yt-uix-form-input-text {
  background-color: transparent;
  /*color: #0ff;*/
  }

/* 20 shades of gray:
	#000 (30)	default background color
	#ddd ( 2)	default text color
	#111		center tray
	#222		titles, in center tray
	#050505
	#272727
	#282828
	#303030
	#333
	#383838
	#3c3c3c
	#3f3f3f
	#444
	#454545
	#555
	#666	(13)	.eLink
	#777
	#999
	#ccc
	#ddd
	#fff

   8 colors:
	#600
	#900
	#060
	#03c
	#ffa
	#ffc
	#772600
	#bbccee
*/
#masthead_child_div,
[id^="ad_"] {display: none !important}

#watch7-headline, #watch7-user-header, #watch7-sidebar {
  background: rgba(255,255,255,.1) !important;
  }

.branded-page-v2-primary-col,
#yt-picker-country-footer, #yt-picker-language-footer, #yt-picker-safetymode-footer {
  background-color: transparent !important;
  }
#yt-masthead-container,
#footer-container {
  background-color: rgba(242,242,242,0) !important;
}

.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
  background-color: rgb(51, 51, 51) !important;
  }
.yt-uix-button-default:hover, .yt-uix-button-text:hover {
  background-color: rgb(59, 59, 59) !important;
  }

.lohp-shelf-cell-container:hover {
  background-color: #333 !important;
  }

.watch-content {background-color: transparent !important}
#watch7-action-panels #watch7-action-panel-footer {
  background-color: #333 !important;
}

#watch7-headline, #watch7-notification-area, #watch7-user-header,
.comments-post-alert {background-color: #333}

.yt-subscription-button-subscriber-count-branded-horizontal, .yt-subscription-button-subscriber-count-unbranded-horizontal {
  background-color: #050505 !important;
  }

/* Youtube Black (2009) Perfect Style! background */
body {
  background: #000 !important;
  color: #ddd !important;
}
.site-center-aligned .yt-card {background-color: #111}
.yt-ui-ellipsis {background-color: #222 !important}
DIV.compact-shelf .yt-uix-button-shelf-slider-pager {background-color: #666}

/** search **/
DIV#masthead-search-terms {background-color: #666}
#masthead-search-terms INPUT {color: #ddd}
#masthead-positioner:hover #masthead-search-term,
#masthead-search-term:focus {
  background-color: #666;
}
DIV#masthead-search-terms {background-color: transparent}
#masthead-positioner:hover #masthead-search-terms {
  color: #ddd;
}

TABLE.gssb_m {background-color: #666}
.gssb_a TD > SPAN {color: #000 !important}

	/*drop-down selection*/
TR.gssb_i, TR.gssb_i TD {background-color: transparent}
.gssb_i > .gssb_a TD > SPAN {color: #ddd !important}

#masthead-positioner:hover #appbar-guide-button {
  background-color: #444;
}

a:link, a:visited, a:active, .hLink { color: #fff !important; }
:focus { outline: none !important; }
.eLink { border-bottom: 1px solid #666 !important; }


/* Google suggest */
.aAutoComplete .cAutoComplete, .google-ac-a .google-ac-c { color: #000 !important; }
.aAutoComplete .dAutoComplete, .google-ac-a .google-ac-d { color: green !important; }
.google-ac-e td, .google-ac-e td span { color: blue !important; }

/* Text colors */
span, div, p, li, td, label { color: #ddd !important; }
input *, textarea * { color: -moz-fieldtext !important; }


/* Text headers */
h1, h2, h3, h4, h5, h6, .hpBlockHeadingGray, .name, .title, #vidTitle, .wsHeading, .header, #catIndexGridHeading, #footer .links th {
  color: #fff !important;
}

/* Default for most borders */
hr, div, [class], [id], [style*="border"], tr, td, th, h1, h2, h3 {
  border-color: #444 !important;
}

/* Highlight Blocks */
#footContent, #footer, #old-footer, #channelVidsDiv, [style*="background-color: rgb(238, 238, 238)"], [style*="background-color: rgb(230, 230, 230)"], [style*="background-color: white"], #catIndexFeaturedWrapper, .highlightBoxes, .highlightBoxesOpacity[class], .highlightBox, .attentionBox, #actionsAndStatsDiv, #statsBox, .settingDiv, #vResponseParent, #vrParentDiv, #localePickerBox, .notopbox, #relatedVidsBody, .changeSearchTypeLinks, .lowlightBox, .vNowPlaying, #guidelines, .highlightBoxBrowse, fieldset, .commentsTooltip, .cat-spotlight-box, #actionsAreaDiv .moreAction, #sendMessage td, #addToFavesResult, #copyrightText, #videoOwnerDiv, #loginForm, #signupForm, #accountQuickLinksMore div, .spotlightItem, #search-settings-form, #addToPlaylistResult, #addToFavesResult, #addToBlogResult, [id^="reportConcernResult"], table[bgcolor="#bbccee"], #notopbox, .subtab b, .confirmBox, #search-video-results-link-box, .contentBox, #watch-channel-vids-div, .search-setting-inner, #watch-video-response, #watch-video-owner, .signInBoxContent, #yt-static-info-box, .signup-table, #search-duplicates-message, #wsSearchForm, .pyv-callout-box, .pyv-callout-box *, .whats-new, .hub-section, #instructions, #masthead-ticker, .side-announcement-box, .fauxBtn, #default-language-box, #upload-email-to {
  background: #282828 !important;
  border-color: #444 !important;
}

/* Various background color fixes */
[bgcolor="#ffffff"], [bgcolor="#ffffbb"], [style*="background-color: rgb(255, 255, 255)"], #exContentWrapper, #exRelatedDiv, #exUserContentDiv, #exPlaylistDiv, #hpEmbedUnderBlock, .basicBoxes, .basicBoxesOpacity[class], .pagingDiv, #hpContentBlock, .vListBox, .previewBox, .vGridBox, .dBG, .rBG, #vlogBox div, .empty_player_list, .form_fieldsets, .form_fieldset, #ticker, .user-thumb-large, .footerBox, #pageContainer, #manageContent[style*="background"], #watch-customize-embed-div, .video-thumb-medium, #watch-actions-area .watch-more-action-share, .topic_body, .feedmodule-anchor, .feedmodule, .watch-comment-spam, .smallChannelMastheadTable, .file, .google-signup, .promo-movie-unit, #movie-promo-pager, #mymedia .action-button .dialog .dialog-body {
  background: #000 !important;
}
[bgcolor="#eeeeee"], .eeeBG, #groupTopicTopEntry, .field_inputs, #watch-refer-div .header, #yt-safetytips, #yt-safetytips *, [id$="PickerBox"], #upload-file-list, .homepage-content-block [style*="background"], #chrome-link, #masthead-region-and-language-picker-box, #iyt-login-suggest-side-box div, .main-tabs-layout-top, #watch-show-vids-div, .opt-banner, .opt-main, #watch-comments-options, #subscribeLoginInvite *, .yt-narrative-box, #suggestions *, #watch-more-popular [style*="background"] {
  background-color: #282828 !important;
}
.headerBox *, #watch-channel-brand-div, .headerRCBox, #search-options-container .search-type .search-type-selected, .profile-box .box-head .box-bg[class], .profile-box .box-foot .box-bg[class], .watch-playlist-row-playing, div.module h2, .color-offset, #search-options-container .search-type-selected div.rtext, #search-options-container .search-type-selected b.rtop, #search-options-container .search-type-selected b.rbottom, #annotations-prompt-message, #annotations-toggle-switch, #inviteDiv, .yt-sr-panel h2, .box-title {
  background: #454545 !important;
}

.errorBox, .error-msg, #error-message, .yt-message-error { background-color: #600 !important; color: #ffc !important; }
#watch-remove-faves, .greenBG, #watch-actions-area #watch-add-faves, .success-msg, #feed_success, #subscribeMessage, .yt-message-success, .iyt-undo-remove-msg { background: #060 !important; }
#loginBoxZ, .status-msg { background: #555 !important; }
#error-message, #success-message, #status-message { color: #fff !important; }

.vlcell, .grayTextAdWords { background: #333 !important; }


/* thumbnail borders */
[class$="WrapperInner"], .videobarthumbnail_white, .videobarthumbnail_gray, .video-thumb { border-color: transparent !important; background: transparent !important; }
[class$="WrapperOuter"], [class^="vimg"] { border-color: transparent !important; }
.video-thumb-medium, .video-thumb-small, .user-thumb-large, .video-thumb-large, .video-thumb-big, .video-thumb img { border: none !important; }
.user-thumb-xlarge, .user-thumb-medium { background: transparent !important; }
.feedmodule-thumbnail, .vimg70, .user-thumb-jumbo { border: none !important; }

div.module h2 { border-color: #666 !important; }

/* Ads */
#bottomAdDiv, #leaderboardAd, .spOffersDiv, #sideContent, img[src^="/img/ad_"], a[href*="doubleclick.net"], #sideAd, img[src^="/img/pic_verizonwireless"], img[src*="pic_verizon_yellow_146x22.gif"], #FLASH_AD, #channelBrandCap, #ad300x250, #channelBrandDiv, [id^="myAd_"], [id^="ad_"], object[width="160"][height="600"], img[src*="fastclick"], .grayTextAdWords + .vlcell, .grayTextAdWords, #search-pva, #chrome-link, .grayTextAdWords + [style*="background-color: rgb(255, 249, 221)"], #watch-channel-brand-div, #pyv-yva-placeholder
{
  display: none !important;
}

/* remove "Videos being watched right now" */
#active_sharing_div, #active_sharing
{
  display: none !important;
}

/* hide stuff */
img[class^="ratingVS-"] { display: none !important; }

/* Purple and Yellow blocks
--------------------------*/
.hpPurpleBlock, .hpYellowBlock, .homepage-yellow-block, .purpleBlock, .search-channel-onebox, .blue-box, .left-right-border-yellow {
  background: #333 !important;
  padding: 8px 10px !important;
  border: 1px solid #444 !important;
}

.hpPurpleBlockBot, .hpYellowBlockBot, .homepage-yellow-block-bot, .purpleBlockBot, .img-side-cap {
  display: none !important;
}

/* Channels masthead
---------------------*/
.channelMastheadTable, #mastheadTable {
  background: #333 !important;
  padding-bottom: 10px !important;
  border-bottom-left-radius: 9px !important;
  border-bottom-right-radius: 9px !important;
}

img[src="/img/masthead_round.gif"], #smallMastheadBottom { display: none !important; }

/* Comments
------------*/
.watch-comment-head { background: #333 !important; border: 1px solid #444 !important; padding-top: .15em !important; }
.watch-comment-auth-head { background: #444 !important;  border: 1px solid #555  !important; border-left: none !important; }
.watch-comment-head-hidden { background: #333 !important; }

.watch-comment-entry-reply { padding-left: 0 !important; }

.watch-comment-action a, .watch-comment-head-link {
  color: #fff !important;
  border-color: #666 !important;
}

.watch-comment-pagination { background: #444 !important; padding: .5em !important; margin-top: 5em !important; }

.watch-comment-spam .watch-comment-body { opacity: 0.6 !important; }
.watch-comment-entry { border-top: none !important; }

.commentHead { background: transparent !important; }

/* Subscription box
-------------------*/
img[src*="pic_subsbox_"] { display: none !important; }
#hpSubsBoxTop, #hpSubsBox, #homepage-subscriptions, #homepage-subscriptions-top { background: #000 !important; border: none !important; }
#vbror, #vbrol { opacity: 0.3 !important; }
#hpSubsBox table td { background: #222 !important; }
#homepage-subscriptions { border: none !important; }


/* Profiles */
.profile-highlightbox .box-body .box-bg[class] { background: #000 !important; }
.profile-box .box-body .box-bg[class] { background: #000 !important; }
.box-head .box-bg[class] { background: #555 !important; }

/* Pager
--------*/
.pagerNotCurrent { background: transparent !important; color: #ccc !important; }
.pagerCurrent { background: #666 !important; color: #fff !important; border: 1px solid #999 !important; }

/* Main tabs
-------------*/
#masthead .nav-item .content { color: #999 !important; }
#masthead .nav-item.selected .content { color: #fff !important; }

#old-masthead .nav-item .content { color: #999 !important; }
#old-masthead .nav-item.selected .content { color: #fff !important; }

#masthead .bar, #masthead .nav-item { background: transparent !important; }
#masthead .nav-item:hover { background: rgba(222,222,222, 0.2) !important; }

#masthead #bar { border-color: #272727 !important; }

#masthead .nav-item {
  border-left-color: #4f4f4f !important;
  border-right-color: #1f1f1f !important;
}

/* My Account
------------- */
#manageNav .selected, #manageContent [bgcolor="#cccccc"], #manageContent .controls, #manageContent #compose {
  background: #333 !important;
}

#manageContent tr[bgcolor="#cccccc"] td,
#manageContent [style^="background-color: rgb(204, 204, 204)"] { background-color: #333 !important; }

#manageContent [bgcolor="#f7f7f7"] {
  background: transparent !important;
}

#manageContent, #manageContent [style*="border"] {
  border-color: #333 !important;
}

#mymedia #table, #mymedia #view-pane .actions, #playlist-form {
  background: #000 !important;
}

#mymedia #view-pane .settings {
  background: #333 !important;
}

.video.odd { background: #000 !important; }
.video.even { background: #222 !important; }

.yt-button .yte-text, .yt-button-alt .yte-text, .yt-button-compact .yte-text, .yt-button-disabled {
  background: #555 !important; border: 1px outset;
}

.folder.selected {
  background: #555 !important;
}

.subfolder.selected {
  background: #444 !important;
}

.subfolder > a { color: #ccc !important; }
.subfolder.selected > a { color: #fff !important; }

.videoDiv[style*="background-color: rgb(204, 204, 204)"] { background: #555 !important; }

.column-details { background: #000 !important; }

.column-divider, #ab-divider { background: #444 !important; }
.vertical-tabs .tab.current, .fr-user-sel { background: #555 !important; }
.vertical-tabs .tab.current a { color: #fff !important; }


/* Static sidebar
-----------------*/
.yt-static-sidebar-list { background: #222 !important; }
.yt-static-sidebar-subhead { background: #333!important; }
#yt-static-sidebar-fade { visibility: hidden !important; }

/* Collapsable boxes
--------------------*/
.module-item-wrapper { background: #222 !important; border-bottom: 1px solid #444 !important; }
.statModule-item-line { background: transparent !important; }
.statModule-title { background: #333 !important; border: 1px solid #444 !important; }
.img-stat-top-cap, .img-stat-btm-cap-close, .img-stat-btm-cap-open { display: none !important; }
.module-item-wrapper > .statModule-item-line:first-child { border-top: none !important; }


/* Watch tabs
--------------*/
#watch-actions-area .watch-tab-contents {
  background: #222 !important;
}
#watch-actions-area .watch-tab-contents * {
  background-color: transparent !important;
}

/* Channel category tabs
------------------------*/
.main-tabs-subcategories { background: #333 !important; }
.videoModifiers div.subcategory { border-color: #666 !important; }

/* Menus
-------- */
/*
.dropdown, .dropdown div a, .dropdown div, .yt-dropdown-menu, .yt-dropdown-menu li a {
  background-color: #333 !important;
  border-color: #444 !important;
  color: #ccc !important;
}
.dropdown div a:hover, .yt-dropdown-menu li a:hover {
  background-color: #666 !important;
  color: #fff !important;
}
*/

.yt-menubutton-primary .yt-menubutton-menu, .yt-menulink-primary .yt-menulink-menu,
.yt-menubutton-urgent .yt-menubutton-menu, .yt-menulink-urgent .yt-menulink-menu {
  background-color: #555 !important;
  border-color: #555 !important;
}
.yt-menubutton-primary .yt-menubutton-menu li a,
.yt-menulink-primary .yt-menulink-menu li a,
.yt-menubutton-urgent .yt-menubutton-menu li a,
.yt-menulink-menu li a {
  background: #383838!important;
}

.yt-menubutton-primary .yt-menubutton-menu li a:hover,
.yt-menulink-primary .yt-menulink-menu li a:hover,
.yt-menubutton-urgent .yt-menubutton-menu li a:hover,
yt-menubutton-menu li a:hover, .yt-menubutton-primary .yt-menubutton-menu li a:hover, .yt-menulink-menu li a:hover, .yt-menulink-primary .yt-menulink-menu li a:hover {
  background: #555 !important;
}

.yt-menubutton-menu, .yt-menulink-menu {
  background: #444 !important;
}


#watch-flag-menu a, #watch-flag-menu a:visited,
#watch-flag-menu a.parent, #watch-flag-menu a.parent:visited,
#watch-flag-menu ul ul a.sub, #watch-flag-menu ul ul a.sub:visited, #watch-flag-menu ul ul ul a, #watch-flag-menu ul ul ul a:visited {
  background: #333 !important;
}
#watch-flag-menu a:hover, #watch-flag-menu :hover > a, #watch-flag-menu ul ul a.sub:hover, #watch-flag-menu ul ul :hover > a.sub, #watch-flag-menu ul ul ul a:hover {
  background: #666 !important;
}

/* Inbox and Contacts (work in progress)
---------------------*/
.message_title, .ab-users td { background: #333 !important; }
.open td, .ellipsis-dots, .ellipsis.m_nohighlight, .ellipsis.m_highlight { background: transparent !important; }
.sorterbar th, .ab-users-checks { background: #555 !important; }
.action-button .content, .default-button .content { background: #555 !important; border: 1px outset; }
.action-button img { display: none !important; }
.groupnamecont.selected, .ab-leftpane-sel { background: #333 !important; }
td[style*="width: 9px; background-color: rgb(204, 204, 204)"], .ab-divider { background: #444 !important; }
.m_highlight { background: #282828 !important; }
.m_nohighlight { background: #000 !important; }
.subject { background: transparent !important; }
[class*="button"] [class*="cap"] { display: none !important; }
.ab-cd, .ab-cd-info, .ab-cd-txtlbl, .ab-cd-details div[style*="background"] { background: #000 !important; }
.inner-dialog { background: #000 !important; }
.dialog { background: #333 !important; }
#headings td, #view-pane .footer { background: #555 !important; }
#mymedia #view-pane .splitter { background: #333 !important; }
.vert-bar { background: #555 !important; }

/* Video Insight
---------------- */
.gwt-TabPanelBottom, .gwt-Image { background-color: #000 !important; }
.insight-toptable > tbody > .even { background: #444 !important; }
img[src^="http://www.google.com/chart?"] { background-color: #fff !important; }
.insight-ErrorPanel { background: #900 !important; }
.insight-sourcestable .footer { background: #000 !important; }
.main .gwt-TabBarItem, .gwt-TabBarRest {  border-color: #666 !important; }
.insight-Popup { background: #444 !important; border: 1px solid #666 !important; }
.weekend-end, .weekend-start, .weekday, .week-names { color: #000 !important; }
.goog-date-picker .grid td.selected { background: #ffa!important; }
.goog-date-picker { background: #666 !important; }

/* Tweaks
----------------- */
/* expand thumbnail listings on videos page */
#mainContentWithNav {
  width: 705px !important;
}

/* horizontal menu at top right of page is an iframe sometimes */
iframe[src="/user_info"] {
  display: inline !important;
}

/* removed rounded corners on fieldsets */
.fieldsetTop, .fieldsetBottom {
  visibility: hidden !important;
}

/* QuickList current selection */
.playlistRowPlaying {
  background-color: #383838 !important;
  border-color: #555 !important;
}

/* "no video" icon */
img[src="/img/no_videos_140.jpg"] {
  opacity: 0.25 !important;
}

/* various buttons */
#upload-button .action-button-text, #subscribeDiv .action-button-text, a[href="/my_profile"] .action-button-text { color: #772600 !important; }
#unsubscribeDiv .action-button-text { color: #000 !important; }
#unsubscribeDiv { opacity: 0.2 !important; }
#unsubscribeDiv:hover { opacity: 1 !important; }

/* highlight left menu selected item */
.category-selected { background: #444 !important; }

.QLIconImg { opacity: 0.4 !important; }

/* hide feed icons on hp */
.feedmodule2-title img { display: none !important; }

.tooltip-wrapper-box * { color: #000 !important; }

/* make links in category side bar easier to click */
.browse-categories-side > li > a { display: block !important; }


/* Home page module containers
------------------------------*/
.fm2-title-border-box-gray, .fm2-title-border-box-blue {
  background: #333 !important;
  padding-top: 6px !important;
  border-top-left-radius: 6px !important;
  border-top-right-radius: 6px !important;
  border: 1px solid #444 !important;
  border-bottom: none !important;
}
.fm2-title-border-box-blue {
  background: #444 !important;
  border-color: #555 !important;
}

.fm2-top-cap { display: none !important; }

.feedmodule-normal { background: transparent !important; }

.img-gray-bottom-cap, .img-blue-bottom-cap {
  height: 0 !important;
  margin-bottom: 6px !important;
