css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("trakt.tv") {
  section{
    background-color: #1F1F1F !important;
    color: #FFFFFF;
  }
  
  .seasons{
    background-color: #3b3b3b !important;
    width: 85% !important;
    margin-left: 3% !important;
    border-radius: 15px;
    text-align: center;
  } 
  
  .season-toggle{
    color: #FFFFFF !important;
  }
  
  .poster{
    visibility: hidden;
  }
  
  .poster .real{
    visibility: visible;
    border-radius: 5px;
  }
  
  .fanart{
    visibility: hidden;
  }
  
  .fanart .shadow-base{
    visibility: visible !important;
  }
  
  .fanart .titles{
    visibility: visible !important;
  }
  
  .fanart .real{
    visibility: visible;
    border-radius: 5px !important;
  }
  
  .btn-default{
    background-color: transparent !important;
    color: #FFFFFF !important;
    border: 1px solid #ED1C24 !important;
  }
  
  .dropdown .btn-default{
    background-color: transparent !important;
    color: #FFFFFF !important;
    border: 1px solid #ED1C24 !important;
  }

  .dropdown .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active, .open > .btn-default.dropdown-toggle {
    background-color: #ED1C24 !important;
    color: #FFFFFF !important;
    border-color: transparent !important;
  }
  
  .quick-icons{
    border-width: 0px !important;
    margin-top: 5px !important;
    border-radius: 5px !important;
    background-color: transparent !important;
  }
  
  .pagination li a{
    background-color: transparent !important;
    color: #FFFFFF !important;
  }
  
  .alert{
    background-color: transparent !important;
    border: 1px solid #ED1C24 !important;
    color: #FFFFFF !important;
  }
  
  .popover{
    background-color: #2c2c2c !important; 
  }
  
  .popover-title{
    color: #ED1C24 !important;
  }
  
  .btn-cancel{
    color: #ED1C24 !important;
  }
  
  .users-wrapper{
    background-color: transparent !important;
    border-width: 0px !important;
  }
  
  textarea{
    background-color: transparent !important;
    color: #FFFFFF !important;
    border: 1px solid #ED1C24 !important;
    border-top-width: 0px !important;
  }
  
  .above-comment{
    background-color: transparent !important;
    border: 1px solid #ED1C24 !important;
  }
  
  .comment-wrapper{
    border-width: 0px !important;
    background-color: transparent !important;
  }
  
  #info-wrapper .sidebar .external li a{
    color: #FFFFFF !important;
  }
  
  #info-wrapper .sidebar .sections li a:hover{
    color: #FFFFFF !important;
  }
  
  .btn-list{
    background-color: transparent !important;
  }
  
  .btn-list:hover{
    background-color: #009EDF !important;
  }
  
  .btn-watch{
    background-color: transparent !important;
  }
  
  .btn-watch:hover{
    background-color: #8E44AD !important;
  }
  
  .btn-collect{
    background-color: transparent !important;
  }
  
  .btn-collect:hover{
    background-color: #16A085 !important;
  }
  
  .btn-checkin{
    color: #ED1C24 !important;
    background-color: transparent !important;
  }
  
  .btn-checkin:hover{
    color: #FFFFFF !important;
    background-color: #ED1C24 !important;
  }
  
  .season-links{
    background-color: transparent !important;
    border-top: 1px solid #ED1C24 !important;
    border-bottom: 1px solid #ED1C24 !important;
  }
  
  @keyframes fade-in{
    from {color: #FFFFFF;}
    to {color: #ED1C24;}
  }
  
  @keyframes fade-out{
    from {color: #ED1C24;}
    to {color: #FFFFFF;}
  }
  
  .fa-heart-o{
    animation-name: fade-out;
    animation-duration: 0.5s;
    color: #FFFFFF;
  }
  
  .fa-heart-o:hover{
    animation-name: fade-in;
    animation-duration: 0.5s;
    color: #ED1C24;
  }
  
  .selected{
    color: #ED1C24 !important;
  }
  
  .actions{
    color: #FFFFFF !important;
  }
  
  .quick-icons .actions a.watch:hover, .quick-icons .actions a.watch.selected{
    color: #FFFFFF !important;
  }
  
  .quick-icons .actions a.collect:hover, .quick-icons .actions a.collect.selected{
    color: #FFFFFF !important;
  }
  
  .quick-icons .actions a.list:hover, .quick-icons .actions a.list.selected{
    color: #FFFFFF !important;
  }
  
  .panel{
    background-color: transparent !important;
    border: 1px solid #ED1C24 !important;
  }
  
  .panel-heading {
    color: #FFFFFF !important;
    background-color: #ED1C24 !important;
    border-width: 0px !important;
  }
  
  .panel-body input, #list-name{
    background-color: transparent !important;
    border: 1px solid #ED1C24 !important;
    color: #FFFFFF !important;
  }
  
  #avatar-file-upload{
    background-color: transparent !important;
    color: #ED1C24 !important;
    border-color: #ED1C24 !important;
  }
  
  #avatar-file-upload:hover{
    background-color: #ED1C24 !important;
    color: #FFFFFF !important;
  }
  
  #user_about{
    border: 1px solid #ED1C24 !important;
    color: #FFFFFF !important;
  }
  
  .panel-body select{
    background-color: transparent !important;
    border: 1px solid #ED1C24 !important;
    color: #FFFFFF !important;
  }
  
  .close{
    color: #FFFFFF !important;
  }
  
  .checkin-modal{
    background-color: #1F1F1F !important;
  }
  
  .checkin-modal .form-signin .fanarts .fanart img{
    border-radius: 0px !important;
  }
  
  .checkin-modal .form-inputs textarea{
    border: 1px solid #ED1C24 !important;
  }
  
  #boxes-wrapper .row .last-comment h3 span{
    font-family: "Proxima Nova";
    font-size: 18px;
    margin-right: 35px;
  }
  
  #faq-wrapper p{
    color: #000 !important;
  }
  
  #schedule-wrapper .poster-wrapper .poster{
    visibility: visible !important;
  }
  
  #huckster-desktop-wrapper,#huckster-desktop-top,#huckster-desktop-bottom,.huckster-vip-info{
    position: fixed !important;
    color: transparent !important;
    background: none !important;
  }
  
  .titles-link h3{
    color: #FFFFFF !important;
  }
  
  .titles-link h4{
    color: #ABABAB !important;
  }
  
  .interactions,.above-comment{
    border: 1px solid transparent !important;
  }
  
  .username,.quote{
    color: #ABABAB !important;
  }
  
  .above-comment-bg{
    background-color: #454545 !important;
  }
  
  .popover{
    background-color: #CCCCCC !important; 
  }
  
  #cover-wrapper{
    background-image: url("/*[[cover]]*/") !important;
  }

  #header-search{
    background-color: #202020 !important;
  }
}
