@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("plus.google.com") {

  /* Page Background. */
  
  body.Td {
    background-color: #cde;
    background-image: url('/*[[BG_IMAGE]]*/') !important;
    background-size: cover;
    background-attachment: fixed;
    background-position: center;
  }
  
  .t3, .Bha, .Bha .jw, .Bha .Yp {
    background-color: transparent;
  }
  
  .gb_Xc .gb_6b {
    background-color: rgba(255,255,255,.85) !important;
  }
  
  /* Nav bar */
  
  .Uc {
    border-style: solid none;
    border-width: 1px;
    border-color: #ccc;
    height: 46px;
    background-color: rgba(240,240,250,.95) !important;
    color: white !important;
  }
  
  .fOa, .uoGTZe, .GY, .t3 {
    background-color: transparent !important;
  }
  
  .fOa .d-s {
    margin: 1px 0px;
  }
  
  /* Posts Background. */

  .Ee, .Sb {
    background-color: rgba(255,255,255,.85);
    border: none;
    box-shadow: 0px 2px 2px rgba(80,80,80,.75) !important;
  }
  
  .Xa, .ve, .hf {
    background-color: transparent;
  }
  
  .zj, .Xc {
    background-color: transparent;
  }

  .hf.DX {
    background-color: transparent;
  }
  
  /* Large Posts. */
  
  .bD, .ej {
    background-color: transparent;
  }
  
  
  /* Refed Posts. */
  
  .Wm, .Sk, .pf .yg, .hn {
    border: none !important;
    background-color: rgba(255, 255, 255, .6) !important;
    box-shadow: 0px 2px 2px rgba(80,80,80,.75) !important;
  }

  .pf .yg {
    margin: 0px -13px;
  }
  
  .cg {
    border-top: none;
  }
  
  /* Comments */
  
  .fr {
    background-color: rgba(120,140,180,.15);
    border-top: 1px solid rgba(180,180,180,.5);
  }
  
  .WR, .vy, .bS, .cS, .nB {
    background-color: transparent;
  }
  
  .TC {
    margin: 0px;
    padding: 12px 20px;
    margin-top: -18px;
    border-top: 1px solid #ccc;
    border-bottom: 1px solid #ccc;
  }

  .EP {
    padding-top: 10px;
  }
  
  .Qg {
    margin: 0;
    padding-top: 20px;
    background-image: linear-gradient(rgba(240,240,240,0) 0%, rgba(240,240,240,0.85) 50%, rgba(240,240,240,0.95) 100%);
  }
  
  /* Controls */
  .TR {
    background-color: transparent;
  }
  
  .Sb {
    background-color: rgba(255,255,255,.85);
  }
  
  .Sb.hd .cc, .Bc {
    background-color: transparent;
  }
  
  .eg {
    display: none !important;
  }

  .aIb {
    background-color: transparent;
  }

  .em {
    background-color: transparent;
  }

  .em:before {
    background: none;
  }
  
  /* Notification */
  
  #gbsfw.gb_tb {
    background-color: rgba(255,255,255,.85);
  }
  
  .f4a, .IO {
    background-color: rgba(255,255,255,.6);
  }
  
  .MNn0h, .MNn0h.RATVS, .MNn0h:hover, .z6vYse.ez0Ipb, .z6vYse.ez0Ipb:hover {
    background-color: rgba(120,140,180,.15);
  }
  
  .f4a.tta, .f4a.B70Reb, .tQb {
    background-color: rgba(220,230,240,.6);
  }
  
  .aac, .nfe, .Lub, .y9fV .MNn0h, .l4a {
    background-color: transparent;
  }
}
