css:
@-moz-document url-prefix("https://www.optyczne.pl") {
.wrapper {
    width: 1260px;
}
.boxed {
    background-color: #ebebeb;
}
.header .header-menu {
    background: #fff;
}
.header .header-menu li a {
    background: #fff;
}
.main-content-left {
    width: 685px;
    background-color: #fff;
    padding: 10px 20px;
    border: 1px solid rgb(234, 234, 234);
}
.articles-listing {
    background-color: #fff;
}
.main-content-right.sidebar-left12 {
    display: none;
}
.wrapper > .main-content-left {
    width: 865px;
}
.main-content-left.ratings-list {
    width: calc(100% - 40px)
}

.articles-listing .article-big-block .article-content {
    width: 595px;
}
.article-big-block .article-content p, .article-small-block .article-content p {
    text-align: left;
}
.shortcode-content table tbody td {
    font-size: 16px;
}
.default-form input[type=text], .default-form select {
    font-size: 14px;
}
.default-form p label {
    font-size: 14px;
}
.products-list .product {
    width: 32%;
    text-align: center;
}
.products-list .product .product-photo {
    width: 100%;
}
}
