css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("trello.com") {
body, textarea {
    font-family: 'IRANSans', 'Droid Arabic Naskh', tahoma !important;
    font-size: 95%;
}
#content, .window-wrapper, .action-comment {
    direction: rtl !important;
}
.list-card-title, .card-detail-item, .action-comment p {
    direction: rtl;
}
.new-comment-input, textarea.field, .js-text, .list-card-composer-textarea, .checklist-new-item-text {
    direction: rtl;
    font-family: 'IRANSans', 'Droid Arabic Naskh', tahoma !important;
}
/**/
.phenom-reactions {
    margin-right: 0 !important; 
}
.checklist-item-row {
	text-indent: 0 !important;
}
.checklist-item {
    padding-left: 0px !important;
    padding-right: 40px;
}
.checklist-item-details {
    margin-left: 32px;
    margin-right: -6px;
    direction: rtl;
}
.checklist-item-checkbox {
    left: initial !important;
    right: 0;
}
.checklist-item-controls
{
    margin-left: -32px;
	float: left !important;
}
/**/
.cardCounter {
    float: left !important;
    background: #199693 !important;
}
.totalCCounter {
    border-radius: 4px;
    background: #199693 !important;
}
.list-header, .card-label {
    text-align: center;
}
.list-header-name {
    font-family: 'IRANSans', 'Droid Arabic Naskh', tahoma !important;
    text-align: center;
    font-weight: normal;
    font-size: 17px;
}
.list-card-details {
    direction: rtl;
}
.list-card-details .list-card-edit-title {
    font-family: 'IRANSans', 'Droid Arabic Naskh', tahoma !important;
    font-size: 12px !important;
}
.list-card-members {
    float: left;
}
.list-card-labels .card-label {
    float: right;
}
.badges {
    float: right;
}
.badge-text {
    float: none !important;
}
.open-card-composer {
    font-size: 0;
    text-align: right;
}
.open-card-composer:before {
    font-size: 13px;
    content: "افزودن کارت جدید";
}
.open-card-composer:hover, .js-open-add-list:hover {
    background: #FFF;
    text-decoration: none;
}

/********** list **********/

.edit-heavy .field {
    text-align: center;
    font-weight: normal;
    border: 1px dashed #4B4B4B !important;
    height: 40px !important;
}
#board {
    direction: rtl;
    padding-right: 5px !important;
}
.list-wrapper:first-child {
    margin-left: 5px;
    margin-right: 8px!important;
}
.list-card {
    border-width: 2px !important;
    border-radius: 0px 3px;
}
.list-card-operation {
    left: 0 !important;
    top: 4px !important;
    right: inherit;
    width: 16px !important;
    border-radius: 3px 0 3px 0;
}
.list-header-menu-icon {
    left: -5px;
    right: inherit;
}
.checklist-item-details .checklist-item-menu-wrapper {
    left: 0 !important;
    right: initial !important;
}
.checklist-item-row {
    text-indent: -5%;
}
.clockify-button-inactive {
    left: 40px !important;
    top: 7px !important;
    right: unset !important;
}
.clockify-button-active {
    left: 40px !important;
    top: 7px !important;
    right: unset !important;
}
.home-container .all-boards .boards-page-board-section-list-item {
    margin-right: 1% !important;
}
.button-link>.icon-sm {
    margin: 0 -6px 0 6px;
}
.agile-spent-icon-cardtimer {
    margin-right: -4px !important;
    margin-left: 8px !important;
}
}
