css:
@-moz-document url-prefix("http://www.google.com/calendar"), url-prefix("https://www.google.com/calendar") {
@font-face {
  font-family: 'Lato';
  font-style: normal;
  font-weight: 400;
  src: local('Lato Regular'), local('Lato-Regular'), url(https://themes.googleusercontent.com/static/fonts/lato/v6/9k-RPmcnxYEPm8CNFsH2gg.woff) format('woff');
}

@font-face {
  font-family: 'Open Sans';
  font-style: normal;
  font-weight: 400;
  src: local('Open Sans'), local('OpenSans'), url(https://fonts.gstatic.com/s/opensans/v10/cJZKeOuBrn4kERxqtaUH3T8E0i7KZn-EPnyo3HZu7kw.woff) format('woff');
}

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), 
       local('Roboto-Regular'), 
       url(https://themes.googleusercontent.com/static/fonts/roboto/v11/2UX7WLTfW3W8TclTUvlFyQ.woff) 
       format('woff');
}

*{
    font-family: 'Roboto', 'Lato', 'Open Sans', sans-serif !important;
}

.applogo {
  display: none !important;
}


.mv-container {
  height: 90% !important;
  box-shadow: 0 1px 4px 0px rgba(0,0,0,.75);
  width: 95% !important;
}

.te {
  text-overflow: ellipsis;
}

.st-contents, .mv-container {
  font-size: 10px;
  line-height: 12px;
}

.st-c .te, .st-c .rb-n {
  line-height: 1em;
  height: 1em;
}

/* ############### Fleches ########################*/

.st-ad-ml, .st-ad-ml2 {
  border: 0 solid #000;
  border-width: 8px 4px 8px 0;
  position: absolute;
  top: -1px;
  left: -6px;
}
  .st-ad-ml2 {
  border-width: 7px 4px 7px 0;
  top: 0;
  left: -7px;
}

.st-ad-mr, .st-ad-mr2 {
  border: 0 solid #000;
  border-width: 8px 0 8px 4px;
  position: absolute;
  top: -1px;
  right: -6px;
}

.st-ad-mr2 {
  border-width: 7px 0 7px 4px;
  top: 0;
  right: -7px;
}


.cwci {
  height: 1em;
  width: 1em;
}

.month-row{
}

/* ############### AUJOURDHUI ########################*/

.dp-today {
  border-color: #D88 !important;
}
.dp-today-selected {
  border-color: #C00 !important;
}

.dp-weekday {
  background: none;
}

.st-bg-today,.st-bg-next, .st-dtitle-next {
  border-left: none;
}

.st-bg-today {
  background: linear-gradient(to bottom, rgba(255, 21, 0, 0.5) 0%,rgba(255, 84, 84, 0.5) 50%,rgba(234, 103, 103, 0.5) 100%);
}

.st-dtitle-today {
  background: linear-gradient(to bottom, rgba(255,48,25,1) 0%,rgba(207,4,4,1) 100%);
  border-left: none;
  border-top: none;
  border-bottom: 1px solid #A00;
  font-weight: bold;
  color: #FFF;
  text-shadow: 0 1px 0 #900000;
}

.st-dtitle-down {
  border-top: 1px solid rgba(170, 0, 0, 0.5);
}

.mv-dayname {
  line-height: 1.5em;
  text-shadow: 0 -1px 0 #FFF, 0 1px 1px rgba(0,0,0,.3);
  text-align: center;
  letter-spacing: .1em;
  font-size: 13px;
  background-image: linear-gradient(to bottom, rgba(230, 230, 230, 0.5) 0%, rgba(175, 175, 175, 0.5) 100%) !important;;
}

.st-dtitle:not(.st-dtitle-today){
  background: linear-gradient(to top, rgba(200,200,200,.5) 0%,rgba(150,150,150,.5) 100%);
}

.st-dtitle:nth-last-child(-n+2):not(.st-dtitle-today) {
  background: linear-gradient(to bottom, rgba(219, 219, 219, 1) 0%,rgba(238,238,238,1) 100%);
  border-bottom: 1px solid #eee;
  text-shadow: 0 1px 0 #fff, 0 0 2px rgba(0,0,0,.3);
}

.st-dtitle {
  padding: 0 4px !important;
}

.st-bg-table {
  min-height: 95% !important;
  height: 95% !important;
  max-height: 95% !important;
}

/*################# NAVIGATION VUES #####################*/

#Vues {
  position: absolute;
  right: 0;
  left:0;
}

#Vues:hover > div {
  z-index:9999 !important;
  display: block !important;
}

/*################# BOUTONS #####################*/

#calcontent.eui-t #vr-nav .goog-imageless-button, #calcontent.eui-t .date-nav-today .goog-imageless-button {
  min-width: 52px;
}

#vr-nav{
  border: none !important;
}

.goog-imageless-button, .navbuttonouter {
  height: 2em;
  line-height: 2em;
}

.qnb-container .goog-imageless-button {
  border: 1px solid #F000 !important;
}

[role="button"] {
  border-radius: 0;
}

#topRightNavigation [role="button"] * {
  text-shadow: 0 1px 0 #fff;
}

#topRightNavigation [role="button"],
#topLeftNavigation [role="button"]
 {
  background-image: linear-gradient(to bottom, rgba(230, 230, 230, 0.5) 0%, rgba(210, 210, 210, 0.5) 50%, rgba(195, 195, 195, 0.5) 51%, rgba(235, 235, 235, 0.5) 100%) !important;
}

.navForward, .navBack, #mg-settings .goog-imageless-button-outer-box  {
  height: 14px !important;
  background-size: 444px 126px !important;
}

.navBack {
  background-position: -183px -13px;
  width: 20px !important;
}

.navForward {
  background-position: -183px 0px;
  width: 20px !important;
}

#mg-settings .goog-imageless-button-outer-box {
  background-position: -207px -26px !important;
    width: 22px !important;
}

.goog-imageless-button-checked {
  box-shadow: inset 0 0 7px 0 rgba(150,150,250,.5);
}

/*########################### LEFT NAVIGATION ###############################*/

#clst_fav > span.calHeaderSpace {
  display: inline-block;
  margin: 0;
  height: 1.6em;
}

/* ######################### MINI CALENDAR ###################################*/

#nav {
  margin-left: 1em !important;
}

.dp-cell.dp-dayh {
  text-shadow: 0 1px 1px #000;
  font-size: .8em;
  border-bottom: 1px solid #ccc;
}

#dp_0_tbl, #lhscalinner_fav, #lhscalinner_my {
  padding-left: 0;
  padding: .2em;
  box-shadow: 0 0 4px 0 rgba(0,0,0,.5);
  background: linear-gradient(to bottom, rgba(238,238,238,1) 0%,rgba(204,204,204,1) 100%);
}


.calHeader, .goog-zippy-expanded .monthtableSpace {
  margin: 0;
}

#lhscalinner_my {
  background-image: linear-gradient(to bottom, rgba(230, 230, 230, 0.5) 0%, rgba(210, 210, 210, 0.5) 50%, rgba(195, 195, 195, 0.5) 51%, rgba(235, 235, 235, 0.5) 100%) !important;; 
  padding-bottom: 1em !important;
}


.calList{
  background: none;
}

.calListRow {
  height: auto !important;
  border: none !important;
}

.calListLabel, .calListLabel-sel {
  line-height: 14px !important;
  font-size: 10px !important;
  font-family: 'Roboto' !important;
}

.calListImg {
  top: 4px !important;
}

#weekViewAllDaywk:before {
  display: block;
  font-family: 'Roboto' !important;
  font-weight: bold;
  font-size: .8em;
  content: 'Jour entier';
  position: absolute;
  left: -55px;
  top: .5em;
  z-index:9999 !important;
}

.wk-tzlabel {
  padding-bottom: 0 !important;
  font-family: 'Roboto' !important;
}

#dp_0_tbl .dp-cell:not(.dp-dayh) {
  font-size: 65% !important;
}

#nav h2, .calHeaderSpace, #dp_0_header .dp-cell {
  font-weight: bold;
  font-variant: small-caps;
  letter-spacing: .1em;
  margin: .5em 0;
  overflow: hidden !important;
  white-space: nowrap;
  text-overflow: ellipsis;
  font-size: 1em;
}

.calHeaderSpace {
  width: 100px !important;
}

.dp-monthtable {
  table-layout: fixed;
}

.mv-event-container {
  border-right:none !important;
}

.sng-frame {
  box-sizing:  border-box !important;
  background-image: linear-gradient(to bottom, #F1F1F1 0%, #EEE 100%);
  border: 1px solid #ccc !important;
  margin: 5px;
}

.sn-container {
  background: linear-gradient(to bottom, rgba(210,210,210,.5) 0%, rgba(178,178,178,.5) 100%);
  border-radius: 2px !important;
  box-shadow: 0 0 20px 0 rgba(0,0,0,.5) !important;
}


#gadgetcell {
  width: 210px !important;
  margin-right: 10px !important;
  box-shadow: 0 0 10px rgba(0,0,0,.5);
}

#rhstogglecell.rhstogglecell-open {
  right: 230px !important;
}
}
