css:
@-moz-document regexp(".*tagpro-.*.koalabeast.com.*"), regexp(".*maptest.*.newcompte.fr.*") {
html.background {
    background: rgb(31, 31, 31);
}

h1 {
    margin-top: 100px;
    padding-bottom: 20px;
    width: 650px;
    height: 137px;
    background: url(http://i.imgur.com/O4uUPLD.png) 0% 0% no-repeat transparent;
}

article {
    width: 1000px;
    display: block;
}

.section.bottomPadding.tiny {
    display: none;
}

a.button, button.button {
    border: 3px solid rgb(255, 255, 255);
    background-color: rgb(239, 66, 62);
}

.buttons.smaller > a.button {
    min-width: 14%;
}

.section.smaller {
    padding-top: 25px!important;
}

a {
    color: white;
}

.hideIfExternal.section{
    display:none;
}

.section.lineup {
    display: none;
}
    
}

@-moz-document regexp(".*tagpro-.*.koalabeast.com\\/groups\\/.{8,}") {
h1 {
    margin-top: 0px;
    top: 20px;
    width: 166px;
    height: 35px;
    background: url(http://i.imgur.com/ehbQ2xv.png) 0% 0% no-repeat transparent;
}

a {
    color: white!important
}

div.teams > div.red {
    background-color: rgba(255,0,0,0.20);
    color: #FFCFCF;
    margin-left: 18%;
    width: 31%;
    margin-right: 18px;
    border: 1px solid rgba(255, 255, 255, 0.2);
    background-color: rgba(239, 66, 62, 0.498039);
}

div.teams > div.blue {
    border: 1px solid rgba(255, 255, 255, 0.2);
    background-color: rgba(79, 175, 255, 0.498039);
    width: 31%;
    margin-right: 18px;
}

div.teams > div.spectators {
    border: 1px solid rgba(255, 255, 255, 0.2);
    width: 18%;
    margin-left: 82.5%;
    margin-top: -16.4%;
}

div#waiting {
    border: 1px solid rgba(255, 255, 255, 0.2);
    width: 18%;
    margin-left: 82.5%;
    margin-top: 1%;
}

div.teams > div.gray {
    width: 81.1%;
    border: 1px solid rgba(255, 255, 255, 0.2);
}

label {
	padding-right: 5px;
}

div#chat {
    margin-right:18%;
}

div#settings {
    top: 70px;
}

div.teams ul > li.leader {
    color: yellow;
}

div.teams ul > li {
    cursor: default;
    display: inline-block;
    width: 145px;
    font-weight:bold;
}

form > div > .value {
    padding-top: 2px;
    padding-bottom: 2px;
    border: 1px solid rgba(255,255,255,1);
    font-family: monospace;
    margin-bottom: 2px;
}

button, a.button {
    background-color: #EF423E;
    border: solid 2px white;
    left: 0px;
    right: 0px;
    top: 0px;
    bottom: 0px;
    margin: auto;
}
}

@-moz-document regexp(".*newcompte.fr:.*"), regexp(".*koalabeast.*:.*") {
html.background {
    background: rgb(31, 31, 31);
}

div#mapInfo {
    float:none;
    margin-left: auto;
    margin-right: auto;
    width: 100%;
    text-align: center;
    font-size: 150%;
    font-weight: bold;
    padding-bottom: 55px;
}

div#optionsLinks {
    display:none;
}

div#optionsName {
    position: absolute;
    top: 28px;
    width: 96.8%;
    text-align: center;
}    

input#name {
    margin-left: 5px;
}

a#switchButton {
    position: absolute;
    right: 18px;
    top: 18px;
}

div#options {
    width: 1000px;
    box-shadow: 10px 10px 20px 0px rgba(0,0,0,0.35);
    border: none;
    background-color: rgba(50,50,50,0.75);
}

tbody.stats {
    font-family: monospace;
}

table#stats {
    font-size: 16px!important;
}
}

@-moz-document url-prefix("http://tagpro.gg/"), url-prefix("http://capturetheflag.us/"), url-prefix("http://tagpro.koalabeast.com/") {
html.background {
    background: rgb(31, 31, 31);
}

h1 {
    margin-top: 100px;
    padding-bottom: 20px;
    width: 650px;
    height: 137px;
    background: url(http://i.imgur.com/O4uUPLD.png) 0% 0% no-repeat transparent;
}

.right {
    display: none;
}

a.button.server {
    display: inline-block;
    margin-right: 5px;
    min-height: 0px;
}

a.button, button.button {
    border: 3px solid rgb(255, 255, 255);
    background-color: rgb(239, 66, 62);
}
}
