css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pmd-roleplay.forumotion.org") {

	body {
		background-attachment: fixed !important;
		background-image: url(http://i.imgur.com/9TaFQQA.png) !important;


		background-size: 1080px;
	}

	.bodylinewidth {
		border: 10px solid rgba(43,72,82, 0.75);
		outline: 10px solid rgba(43,72,82,0.5);
		margin-top: 20px;
	}

	#chatbox, #chatbox_members, .chatbox {
		background: linear-gradient(rgba(28, 56, 71, 1), rgba(28, 56, 71, 1), rgba(0,0,0,0)), url(http://i.imgur.com/9TaFQQA.png) !important;
		background-attachment: fixed !important;
	}

	#chatbox, #chatbox_members {
		margin-bottom: 0;
	}

	.chatbox_row_1,.chatbox_row_2{
		background-color: transparent !important;
	}

	.member-title {
		background: linear-gradient(rgba(255,255,255,0.05), rgba(0,0,0,0.25)) !important;
	}

	#chatbox_fotter {
		margin-top: 0;
	}

}
