  div.post.reply {
    background: url(http://i.imgur.com/qU2oRQy.png) !important;
    border: 0px solid #E5D959;
    border-color: -moz-use-text-color #E5D959 #E5D959 -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    padding: 2px;
  }

  textarea, input, button, select {
    background: white;
    color: black;
  }

  div.post.reply.highlighted {
    background: url(http://i.imgur.com/SPYm6pg.png) !important;
  }

  pre.prettyprint {
    padding: 10px !important;
    border: 0px solid #888 !important;
    background: rgba(0,0,0,0.8) !important;
    display: inline-block !important;
  }

  span.pln {
    color: white;
  }

  span.pun {
    color: yellow;
  }

  span.lit {
    color: #169281;
  }

  span.kwd {
    color: #3879D9;
  }

  span.com {
    color: #C20000;
  }

  a, a:visited {
    color: #00E;
  }

  #options_div {
    background: url(http://i.imgur.com/v7pk8AK.png);
    width: 900px;
    height: 650px;
    border: 0px;
  }

  #options_div textarea {
    min-height: 88%;
    max-height: 88%;
    min-width: 98% !important;
    max-width: 98% !important;
    border: 0px !important;
  }

  hr {
    border-width: 1px medium medium;
    border-style: solid none none;
    border-color: #117743 -moz-use-text-color -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
    height: 0px;
  }

  div.boardlist a, .fa, div.pages a {
    color: #054500 !important;
    text-decoration: none;
  }

  a, a:visited {
    color: #00E !important;
    text-decoration: none !important;
  }

  div.boardlist a:hover, div.pages a:hover {
    color: #00990B !important;
    text-decoration: none;
  }

  a:hover {
    color: #00990B !important;
  }

  #options_tablist {
    border-right: 0px solid #000;
  }

  div.banner {
    background: #054500 none repeat scroll 0% 0%;
    text-align: center;
    padding: 2px;
    color: #FFF;
    width: 100%;
  }

  div.banner a.unimportant {
    color: white!important;
  }

  .post-table-options th, .post-table th {
    background: #65AB6B none repeat scroll 0% 0%;
    color: #054500;
    font-weight: 600;
  }

  .post-table textarea {
    min-width: 450px;
    min-height: 200px;
    border: 0px;
  }

  .post-table input[type="text"], #post-form-outer  input[type="text"],  #post-form-outer input[type="password"], #quick-reply input[type="text"], div.delete input[type="text"], div.delete input[type="password"] {
    border: 0px !important;
  }

  .dropzone {
    color: black !important;
    background-color: rgba(255,255,255, .3);
    max-height: 400px;
    margin: 0px 5px 0px 0px !important;
    border: 0px dashed rgba(125, 125, 125, 0.4) !important;
  }

  .file-hint {
    color: transparent;
    border: 0px solid rgba(0,0,0,0)!important;
  }

  h1 {
    clear: both;
    text-align: center;
    color: #054500;
    width: 100%;
    font-family: Tahoma,sans-serif;
    font-size: 28px;
    font-weight: bold;
    letter-spacing: -2px;
    margin-top: 0px;
  }

  h2 {
    clear: both;
    text-align: center;
    color: #054500;
    width: 100%;
    font-family: Tahoma,sans-serif;
    font-size: 22px;
    font-weight: bold;
    letter-spacing: -2px;
    margin-top: -6px;
  }

  .desktop-style div.boardlist:nth-child(1) {
    background-color: #FFF18F;
    height: 18px;
    font-size: 10pt;
    border: 0px;
    text-align: center;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0);
  }

  .options_tab_icon.active .fa, .options_tab_icon.active {
    color: #00990B !important;
  }

  .options_tab_icon, .options_tab_icon .fa {
    color: #104D07 !important;
  }

  span.spoiler:hover {
    background: rgba(255,181,5,0);
    color: black;
    padding: 0 1px;
  }

  .theme-catalog div.thread {
    background: url(http://i.imgur.com/qU2oRQy.png);
    border: 5px solid rgba(0,0,0,0);
/*min-height: 225px;
min-width: 150px;*/
    ;
  }

  .theme-catalog div.thread:hover {
    background: url(http://i.imgur.com/SPYm6pg.png);
    border: 5px solid rgba(0,0,0,0);
  }

  theme-catalog div.grid-size-large img {
    max-height: 50%;
    max-width: 98%;
  }

  .theme-catalog img.thread-image {
    box-shadow: 0px 0px 0px rgba(0,0,0,0) !important;
    border: px solid rgba(0,0,0,1) !important;
  }

  div.styles {
    display: none;
  }

  img.banner, img.board_image {
    display: block;
    border: 0px solid #A9A9A9;
    margin: 12px auto 0px;
  }

  span.quote {
    color: #117743;
  }

  div.boardlist {
    color: #054500 !important;
  }

  div.pages {
    color: #054500;
    background: #FFF18F none repeat scroll 0% 0%;
    border: 0px #D9BFB7;
  }

  div.boardlist a.popular {
    font-weight: bold !important;
  }

  div.boardlist, .desktop-style div.boardlist:nth-child(1) {
    font-size: 10pt;
  }

  #quick-reply th {
    text-align: center;
    padding: 2px 0px;
    border: 0px solid #222 !important;
  }

  div.pages form input {
    border: 0px rgba(0,0,0,0) solid;
  }

  .options_tab button {
    border: 0px rgba(0,0,0,0) solid;
    margin-right: 2px;
  }

  div.options_tab input {
    max-width: 98%;
    border: 0px rgba(0,0,0,0) solid;
    margin: 0px 0px 6px 0px;
  }

  input:active, button:active {
    background: #E0E0E0;
  }

  .options_tab input {
    border: 0px;
    margin-left: 6px;
  }

  div.delete input {
    border: 0px;
    margin-bottom: 3px;
    margin-right: 3px;
  }

  .fa:hover {
    color: #054500 !important;
  }

  #options_div .fa:hover {
    color: #00990B !important;
  }

  span.heading {
    color: #FF0007;
    font-size: 14pt;
    font-weight: bold;
  }

  #quick-reply tr td:nth-child(2) input[type="submit"] {
    width: 100%;
    border: 0px;
    margin: 3px 4px!important;
  }

  #auto-update-fs {
    padding: 10px;
    margin: 10px;
    border: 1px solid;
  }

  td input {
    border: 0px;
  }

  #quick-reply th .close-btn {
    float: right;
    padding: 0px 5px;
    color: white !important;
  }

  #quick-reply th {
    color: white !important;
  }

  #quick-reply th .close-btn:hover {
    float: right;
    padding: 0px 5px;
    color: red !important;
  }
}
