css:
@-moz-document domain("wikipedia.org") {
/* URLs on the domain wikipedia.org */

table.metadata {
  display: none;
}
span.mw-editsection {
  display: none;
}
div.portal {
  display: none !important;
}

#mw-head, #mw-head-base, #mw-page-base, #siteSub {
  display: none;
}

#bodyContent {
  max-width: 50em;
}
.mw-body {
  color: black;
}
.mw-body-content {
  font-size: inherit;
}
}
