css:
@-moz-document domain("panda-community.com") {



div[id="uix_wrapper"] {display:block !important;
	
    background-image: url("https://clouds.a.cdnify.io/491596/90999.png") , url("https://clouds.a.cdnify.io/580450/56962.png"), url("https://clouds.a.cdnify.io/50046/29887.png");
   	background-size : 22% 110% , 400px 1065px , 80%;
	background-repeat: no-repeat;
    background-position: left , right, bottom 70px center;
    background-attachment: fixed, fixed, scroll;
    background-color: #61D0FD;
    
                        
                    }


#logoBlock .pageContent{display:block !important;

background-image: url("https://wiki.teamfortress.com/w/images/thumb/6/66/Hamstah.png/116px-Hamstah.png?t=20120628173939"), url("https://wiki.teamfortress.com/w/images/thumb/a/a6/Kitteh.png/120px-Kitteh.png?t=20120628173944") ,
url("http://cdn.squares.im/pony/pic/user/2014/02/3937cc092756d20292c6359ddcb7d0cf.png"),
url("http://www.wpclipart.com/weather/clouds/clouds_3/cloud_soft_T.png") , url("http://www.wpclipart.com/weather/clouds/clouds_3/cloud_soft_T.png") ,
url("http://www.wpclipart.com/weather/clouds/clouds_3/cloud_soft_T.png"),
url("http://fc00.deviantart.net/fs70/i/2013/321/c/2/the_crackpot___pyroland_background_by_hieronymus7z-d6umhm0.png");
    background-repeat: no-repeat; 
	background-size: 3% , 4% , 3%, 7% , 13%, 15%, 100%;
    background-position: top 10px left 180px ,  top 30px right 210px , top left 880px, top center , top 20px left , left 1700px top -5px, top center;
    background-attachment: fixed, fixed, fixed, fixed, fixed, fixed, scroll;
    
}

.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
   background: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/Message%20icons/lollichopnone_zpsfpj7bn4v.png");

}
.avatar img, .avatar .img, .avatarCropper{
	border-radius: 100px;
}    



.node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/Message%20icons/lollichopyes_zpsqqwlguzr.png")
}



.nodeList .node.level_2 .nodeInfo{
    background-color: #CEF6F5;
    border-color: #A9E2F3;
}

div[id="content"] {display:block !important;
	background-image: url("http://fc05.deviantart.net/fs71/f/2013/125/3/7/pyroland_by_ashleylange-d649hxd.png");
    
    background-size: 15%; 
    background-repeat:no-repeat; 
    background-position: left 40px bottom 11px; 
    background-attachment: fixed;
}


 .navTabs .navTab.PopupClosed .navLink {
	color: #086A87;
    border-top-color: #086A87;
    border: 0px;
    font-family: Verdana;
    font-size:12px;
    text-shadow: 0 0 0 transparent, 0 1px #01A9DB;    

          }

.navTabs{
    background-image: url("http://i1219.photobucket.com/albums/dd432/skiffaa/Panda-Community/custom%20navbar/Navbar%20Slide/navBar_zpsi5hvs9ev.png"); 
	background-repeat: no-repeat;
    background-size: 160% 120%;
    background-position: right -30px top -5px;
    border-top-color: #000; 
    border-bottom-color: #00BFFF; 
    
}


.navTabs .navTab.selected .tabLinks{
    background-color: #E0F8F7;
    border-bottom-color: #A9E2F3;
   
} 
.navTabs .navTab.selected .tabLinks a{
 color: #084B8A;
}

.navTabs .navTab.selected .navLink{
	background-color: #E0F8F7;
    border-color: #E0F8F7;
    border-bottom-color: #58D3F7;
}

#content .pageContent{
    background-color: #E0F8F7;
}


.nodeList .categoryStrip{
    border-bottom-color: #0489B1;
}

.node .nodeLastPost{
    background-color: #EFFBFB;

}

.sidebar .section .secondaryContent{
    background-color: #EFFBFB;
    border-radius: 10px;
    border-color: #A9E2F3;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3{
	background-color: #81DAF5;
    border-color: #2ECCFA;
    border-radius: 10px 10px 0px 0px;
}

.sectionMain{
 background-color: #EFFBFB; 
    border-color: #A9E2F3;
}

#taigachat_box{
	background-color: #EFFBFB;
    border: 0px;
}

.tabs{
    
    border-bottom: 0px;
}
.tabs li.active a, .tabs.noLinks li.active{
background-color: #81DAF5;
    border-radius: 10px 0px 0px 0px;
}

.tabs li a, .tabs.noLinks li{
    background-color: #81DAF5;
    border-radius: 0px 10px 0px 0px;
    border: 1px solid #A9E2F3; 
}

.uix_breadCrumb_toggleList li.toggleList_item{
background-color: #E0F8F7;
    border-color: #A9E2F3;
    border-radius: 20px;
}

.breadcrumb{
    background-color: #E0F8F7;
    border-color: #A9E2F3;
    border-radius: 10px;
    
}

.breadcrumb .crust a.crumb{
   background-color: #E0F8F7;
   border-color: #A9E2F3;
}

.breadcrumb .crust .arrow span{
    border-left-color: #CEF6F5;
    
}

.breadcrumb .crust .arrow{
 border-left-color: #A9E2F3;   
}

.nodeList .categoryStrip .nodeTitle{
     background: #0489B1;
     border-radius: 10px 10px 0px 0px;
}

.node.level_1>.nodeList{
 border-color:  #A9E2F3;  
}

.button{
    background-color: #A9E2F3;
    border-radius: 7px;
}

.hasTabLinks #navigation .navTabs .navTab.selected .navLink{
 border-bottom-color: #E0F8F7;   
}

.uix_collapseNodes .uix_icon{
    color: #01A9DB;
    top: 16px;
    left: 9px;
    
}

.uix_collapseNodes{
    background-color: #E0F8F7;
    height: 30px;
    width: 30px;
    border: 1px solid;
    border-color: #CEECF5;
    border-radius: 20px;
}

.profilePage .mainProfileColumn{
    border-left: dotted 1px #A9E2F3;
}

.secondaryContent{
   background-color: #EFFBFB;   
}

.messageSimple{
   border-bottom: 1px solid #A9E2F3;
}

.primaryContent{
    background-color: #EFFBFB;
    border: 1px solid #CEECF5;
}

.profilePage .tabs.mainTabs{
    background-color: #EFFBFB;  
}

.profilePage .mast .section.infoBlock{
   border: 1px solid #CEECF5;  
}

.profilePage .primaryUserBlock .lastActivity{
    border: 0px solid;
}

.subHeading{
 background-color: #CEF6F5; 
 font-family: "Courier New", Courier, monospace;
 border: 1px solid #A9E2F3;
 border-radius: 7px 7px 0px 0px;
}

.textWithCount.subHeading .count{
    background-color: #EFFBFB;
    border: 1px solid #A9E2F3;
    border-radius: 100px;
}

.sectionFooter{
    background-color: #A9E2F3;
    border: 1px solid #A9E2F3;
}

.profilePage .tabs.mainTabs li.active a{
    background-color: #A9E2F3;
}

.messageSimpleList .placeholder .placeholderContent{
 background-color: #E0F8F7;
    border: 1px solid #CEECF5; 
}

#QuickSearch:not(.active) #QuickSearchQuery{
    border-radius: 15px 15px 15px 15px;

}

#QuickSearch .primaryControls .uix_icon{
    background-color: #81DAF5; 
    color: #0489B1;
    text-shadow:0 1px #0B4C5F;
    border-radius: 0px 10px 10px 0px;
}

.textCtrl{
    border-radius: 0px 10px 10px 0px; 
}

.messageList .message{
 background-color: #EFFBFB;
 border: 1px solid #CEECF5;
}

.message .messageMeta .control{
 background-color: #EFF8FB; 
 border: 1px solid #CEECF5;
 border-radius: 100px;
}

.bbCodeBlock{
 border-radius: 0px 15px 15px 0px;
    border: 1px solid #CEECF5;
}


.bbCodeQuote .attribution{
 background-color: #A9E2F3; 
 border-radius: 0px 15px 0px 0px;
 border-left: 2px solid #A9D0F5;
}

.bbCodeQuote .quoteContainer{
 background-color: #EFF8FB;  
 border-radius: 0px 0px 15px 0px;
 border-left: 2px solid #A9D0F5;
}

.messageUserBlock div.avatarHolder{
    background-color:  #EFFBFB;
    border-radius: 10px 10px 0px 0px;
    }

.messageUserBlock{
    border: 1px solid #CEECF5; 
    border-radius: 10px 10px 10px 10px; 
}

.messageUserInfo{
 background-color: rgba(0, 0, 0, 0);   
}

.messageUserBlock .extraUserInfo{
 background-color:  #EFFBFB;  
     border-radius: 0px 0px 10px 10px;
}

.messageUserBlock h3.userText{
 background-color:  #EFFBFB;     
}

.messageUserBlock .arrow span{
  border-left-color: #EFFBFB;
}

.PageNav a{
 background-color: #EFFBFB;  
    border-radius: 50px;
}

.PageNav a.currentPage{
 background-color: #0174DF;   
}

.redactor_box{
 border: 1px solid #A9E2F3 ;   
}

.redactor_toolbar{
  background-color: #EFFBFB;  
  border-bottom: 1px solid #EFFBFB;  
}
}
