css:
@-moz-document url-prefix("http://bs.to/") {
html, body {
    /* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#3f4c6b+0,3f4c6b+100;Blue+Grey+Flat */
    background: #3f4c6b; /* Old browsers */
    background: -moz-linear-gradient(top, #3f4c6b 0%, #3f4c6b 100%); /* FF3.6+ */
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#3f4c6b), color-stop(100%,#3f4c6b)); /* Chrome,Safari4+ */
    background: -webkit-linear-gradient(top, #3f4c6b 0%,#3f4c6b 100%); /* Chrome10+,Safari5.1+ */
    background: -o-linear-gradient(top, #3f4c6b 0%,#3f4c6b 100%); /* Opera 11.10+ */
    background: -ms-linear-gradient(top, #3f4c6b 0%,#3f4c6b 100%); /* IE10+ */
    background: linear-gradient(to bottom, #3f4c6b 0%,#3f4c6b 100%); /* W3C */
}
.serie {
    min-height: 700px;
    overflow: hidden;
}
.home > h2 {
    display: none;
}
section, footer {
    background: rgba(236, 240, 241,1.0)
}
footer {
    margin-top: -20px;
}
#sp_right img {
    border: 1px solid black;
    -webkit-box-shadow: 10px 10px 22px -8px rgba(0,0,0,0.75);
    -moz-box-shadow: 10px 10px 22px -8px rgba(0,0,0,0.75);
    box-shadow: 10px 10px 22px -8px rgba(0,0,0,0.75);
}
#sp_left h2 {
    float: left;
    width: 100%;
    background: rgba(52, 73, 94,1.0);
    color: rgba(236, 240, 241,1.0);
}
#sp_left h2 small {
    float: right;
    display:block;
    font-size: 12px;
    margin-top: 14px;
    margin-right: 3px;
}
#sp_left div {
    border: 1px solid black;
    background: rgba(189, 195, 199,1.0);
}
#sp_left > strong {
    display: none;
}
.serie .watched {
    background-color: rgba(211, 84, 0,1.0) !important;
}
tr:nth-child(even), .home li:nth-child(even) {
    background: rgba(189, 195, 199,1.0);
}
nav a, table a, nav > ul > li > a, #nav > li > a, #nav > li > .title {
    text-decoration: none;
}
.user-center #nav li.title {
    color: rgba(236, 240, 241,1.0);
}
nav > ul, .user-center #nav {
    background: rgba(52, 73, 94,1.0);
}
nav > ul > li:hover {
    background: rgba(44, 62, 80,1.0);
}
nav > ul > li > a, #nav > li > a, #nav > li > .title {
    font-family: 'Roboto', sans-serif;
    color: rgba(236, 240, 241,1.0);
}
.user-center .col li {
    background: rgba(52, 73, 94,1.0);
    color: rgba(236, 240, 241,1.0);
}
.user-center .col {
    background: rgba(44, 62, 80,1.0);
}
nav > ul > li > ul > li > a {
    font-family: 'Roboto', sans-serif;
    color: rgba(236, 240, 241,1.0);
}
nav > ul > li > ul {
    background: rgba(52, 73, 94,1.0);
}
nav > ul > li > ul > li:hover {
    background: rgba(44, 62, 80,1.0);
}
.pages > .current {
    background: rgba(41, 128, 185,1.0);
}
.pages > li > a:hover {
    background: rgba(52, 152, 219,1.0);
}
#user, #user a {
    font-family: 'Roboto', sans-serif;
    border-radius: 0;
    background: rgba(44, 62, 80,1.0);
    color: rgba(149, 165, 166,1.0);
    text-decoration: none;
}
#user a:hover {
    text-decoration: underline;
}
#user br {
    border-bottom: 1px solid black;
}

section > header {
    background: rgba(44, 62, 80,1.0);
    color: rgba(236, 240, 241,1.0);
}

.home #shoutbox dl dt, .home #shoutbox dl dl a {
    background: rgba(44, 62, 80,1.0);
    color: rgba(236, 240, 241,1.0);
}
.admin {
    color: rgba(192, 57, 43,1.0);
    text-decoration: none;
}
.mod {
    color: rgba(41, 128, 185,1.0);
    text-decoration: none;
}
.cmod {
    color: rgba(142, 68, 173,1.0);
    text-decoration: none;
}
.smod {
    color: rgba(39, 174, 96,1.0);
    text-decoration: none;
}
.exadmin {
    color: rgba(127, 140, 141,1.0);
    text-decoration: none;
}
.tech {
    color: rgba(155, 89, 182,1.0);
    text-decoration: none;
}
.user {
    color: rgba(230, 126, 34,1.0);
    text-decoration: none;
}
.admin:hover, .mod:hover, .cmod:hover, .exadmin:hover, .user:hover, .tech:hover, .smod:hover {
    text-decoration: underline;
}
.rank {
    display: none;
}
#sbMsg, input[type=submit] {
    border-radius: 0;
}

.home #shoutbox time, .home #news time {
    float: right;
    line-height: 20px;
}
.home #shoutbox dl dt {
    height: 20px;
}
.home #shoutbox dl dt a {
    line-height: 20px;
}
article {
    padding-bottom: 3px;
    border-bottom: 1px solid black;
}

#user > fieldset input {
    background: rgba(189, 195, 199,1.0);
}

.messageBox.newMessages {
    border-color: rgba(44, 62, 80,1.0);
    background: rgba(52, 73, 94,1.0);
}
.messageBox.newMessages > a {
    color: rgba(236, 240, 241,1.0);
    text-decoration: none;
}
.messageBox.newMessages > a:hover {
    text-decoration: underline;
}

.socialOverlay {
    background: url("http://i.imgur.com/5gnruxP.png") no-repeat;
}
.socialThumb{
    background: url("http://i.imgur.com/5gnruxP.png") no-repeat;
}
}
