css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.memrise.com") {

body {
font-size: 14px !important;
  line-height: 1.0em;
}

.course-card-container {
    margin-bottom: 0px;
}

.course-card-container .course-progress-box {
    background-color: #ffffff;
    border: 1px solid #e4e4e4;
    border-radius: 0px;
    font-family: "Open Sans",sans-serif;
    position: relative;
}

.course-card-container .course-progress-box .card-top {
    padding: 0px;
}

.course-card-container .course-progress-box .card-bottom {
    background-color: #f8f8f8;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
    min-height: 0px;
    padding: 0px;
}

.course-card-container .course-progress-box .card-bottom .course-actions {
    display: inline-block;
    text-align: right;
    vertical-align: middle;
    width: 100%;
    position: relative;
    margin-top: -20px;
}
  
.course-card-container .course-progress-box .card-top .card-image-col .img-crop .course-photo {
    display: block;
    height: 70px;
    margin-left: -2px;
    max-width: none;
}

.button.large {
    font-size: 12px;
    min-width: 70px;
    padding: 2px 10px;
}

.course-card-container .course-progress-box .card-top .card-main-container .detail .title {
    font-size: 12px;
    font-weight: 700;
    letter-spacing: 0.2px;
    line-height: 0.9em;
    margin: 0;
    max-height: 2.6em;
    max-width: 100%;
    overflow: hidden;
    padding-top: 2px;
    position: relative;
    text-overflow: ellipsis;
}

.course-card-container .course-progress-box .onoff, .course-card-container .course-progress-box .points {
    color: #494444;
    display: inline-block;
    font-size: 10px;
    margin-left: 2px;
    padding: 2px 4px 2px 0;
    vertical-align: middle;
}

.course-card-container .course-progress-box .card-top .card-main-container .goal-setter-btn {
    color: #494444;
    cursor: pointer;
    display: inline-block;
    font-size: 12px;
    margin: 0px;
    min-width: 80px;
    padding: 2px;
    vertical-align: middle;
}

.course-card-container .course-progress-box .card-top .card-main-container .leaderboard-btn {
    cursor: pointer;
    display: inline-block;
    height: 16px;
    margin-right: 4px;
    position: relative;
    vertical-align: top;
    width: 16px;
}

.course-card-container .course-progress-box .card-top .card-main-container .leaderboard-btn .leaderboard-icon {
    background-image: url("https://d107cgb5lgj7br.cloudfront.net/garden/img/icons/course-card/sprites_course_card.png");
    background-position: -5px 0;
    background-size: 100px 20px;
    display: block;
    height: 16px;
    margin: 0px;
    width: 16px;
}

.course-card-container .course-progress-box .card-top .card-main-container .ctrl-btn .icon {
    background-image: url("https://d107cgb5lgj7br.cloudfront.net/garden/img/icons/course-card/sprites_course_card.png");
    background-position: 0 0;
    background-size: 100px 20px;
    display: block;
    height: 10px;
    margin: 8px 0 0 4px;
    width: 5px;
}
    
.course-card-container .course-progress-box .card-top .card-main-container .course-progress {
    margin-top: 8px;
}
    
}
