css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("4chan.org") {
 #delform {
   width: 44%;
margin: 0 22px 22px 22px !important;
}
.reply {
 display: block !important; 
}
.fileThumb img, .fileThumb {
    float: none !important;
}
.post .postMessage {
    display: inline-block !important;
    clear: both;
}

span.summary {
    display: block;
margin-bottom: 6px !important;
}
.reply {
    padding: 4px !important;
}
.post blockquote {
    display: block;
    margin: 0 !important;
}
blockquote > br {
display: none;
}
.fileText { font-size:0; }


.opContainer {
    display: block !important;
    padding: 2px;
}

.fileThumb img  {
    display: block !important;
    margin-left: auto !important;
    margin-right: auto !important;
}
.pagelist {
display: block;
clear: both;
width: 100%;
}
.postContainer.opContainer{
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    background-color: #FFFFCC;
    border-color: -moz-use-text-color #b7c5d9 #b7c5d9 -moz-use-text-color;
    border-image: none;
    border-style: none solid solid none;
    border-width: medium 1px 1px medium;
    display: table;
    padding: 2px;
}
.reply, .postContainer.opContainer{
border-radius: 6px;
padding: 11px !important;
-webkit-box-shadow: 2px 2px 3px 1px rgba(136,136,136,1);
-moz-box-shadow: 2px 2px 3px 1px rgba(136,136,136,1);
box-shadow: 2px 2px 3px 1px rgba(136,136,136,1);
    margin-bottom: 8px !important;
    margin-top: 8px !important;
}
.summary.desktop span {
opacity: 0;
}
.summary.desktop span:hover {
opacity: 1;
}
html {
    font-family: "Source Sans Pro",sans-serif;
    font-size: 18px !important;
    font-weight: 500;
    line-height: 1.5;
}
}
