css:
@-moz-document domain(plug.dj) {
/* ############################### BADGES ############################### */

/* Hide the grey background that badge boxes have */
.badge-box {
    background: rgba(0, 0, 0, 0) !important;
}

/* Make badges semi-transparent and grey */
.message .badge-box, .emote .badge-box, .mention .badge-box {
    opacity: 0.1 !important;
    -webkit-filter: grayscale(100%) !important;
    -webkit-transition: 0.3s !important;
}

/* Make them appear with color and full visibility when the message is hovered */
.message:hover .badge-box, .emote:hover .badge-box, .mention:hover badge-box {
    opacity: 1 !important;
    -webkit-filter: grayscale(0%) !important;
    -webkit-transition: 0.3s !important;
}

/* Adds colored border to mentions - USERS */
#chat .mention, #chat .mention:nth-child(2n+1) {
    border-left: rgba(255, 221, 111, 1) 3.5px solid !important
}

/* Adds colored border to mentions - SUBSCRIBERS */
#chat .mention.is-subscriber, #chat .mention.is-subscriber:nth-child(2n+1) {
    border-left: rgba(197, 152, 64, 1) 3.5px solid !important
}

/* Adds colored border to mentions - STAFF */
#chat .mention.is-dj, #chat .mention.is-staff, #chat .mention.is-dj:nth-child(2n+1), #chat .mention.is-staff:nth-child(2n+1) {
    border-left: rgba(172, 118, 255, 1) 3.5px solid !important
}

/* Removes weird ass gradient on mentions */
#chat .mention, #chat .mention:nth-child(2n+1), #chat .mention.is-subscriber, #chat .mention.is-subscriber:nth-child(2n+1), #chat .mention.is-dj, #chat .mention.is-staff, #chat .mention.is-dj:nth-child(2n+1), #chat .mention.is-staff:nth-child(2n+1) {
    background: linear-gradient(135deg,rgba(17, 19, 23, 0.15) 0%,rgba(17, 19, 23, 0.15) 0%,rgba(17, 19, 23, 0.15) 0%) !important;
}

/* Keep badges visible when mentioned */
#chat .mention.is-you .badge-box, #chat .mention.is-dj .badge-box, #chat .mention.is-staff .badge-box, #chat .mention.is-you:nth-child(2n+1) .badge-box,  #chat .mention.is-dj:nth-child(2n+1) .badge-box, #chat .mention.is-staff:nth-child(2n+1) .badge-box {
    opacity: 1 !important;
    -webkit-filter: grayscale(0%) !important;
}

/* Remove 1px from small badges */
.badge-box .bdg-animals-s02, .badge-box .bdg-style-s04, .badge-box .bdg-food-s02, .badge-box .bdg-ba-g, #footer-user .buttons .button .bdg {
    height: 29px !important;
}

/* ############################### CHANGING SUBSCRIBERS TO BE GREY AGAIN ############################### */

/* Replacing subscriber name color */
#chat .from.subscriber .un {
    color: #777f92 !important;
}

/* Replacing subscriber crown icon */
#chat .icon-chat-subscriber {
    background: url('https://cdn.radiant.dj/rcs/icons/radiant/subscriber.png') !important;
    -webkit-filter: grayscale(100%) !important;
    background-position: 0px;
}

/* Replacing subscriber crown icon, I'm using the RCS icon for that D: */
#waitlist .list .user .meta.is-subscriber .name i, #user-lists .list.room .user.is-subscriber .icon-chat-subscriber, #user-rollover .meta.subscriber .status i {
    background: url('https://cdn.radiant.dj/rcs/icons/radiant/subscriber.png') !important;
    -webkit-filter: grayscale(100%) !important;
}

/* Changing subscriber's names on the waitlist to white */
#waitlist .list .is-subscriber .name, #user-lists .list .is-subscriber .name { 
    color: #eee !important;
}
    
/* ############################### FOOTER ############################### */

/* Hide the Share Facebook and Twitter button */
#twitter-menu,
#facebook-menu {
    display: none !important;
}

/* Making the chevron on the footer darker */
#footer .bar-button .section .chevron {
    opacity: 0.3 !important;
}

/* Flipping colors on the footer */
#footer-user .buttons .button {
    box-shadow: inset 1px 0 0 0 #111 !important;
}

/* Flipping colors on the footer */
#footer {
    background: #0d0d0d !important;
}

#video-only-dj {
    background: #222224 !important;
}

/* ################################## PLAYLIST ######################### */

/* Changing the background of the action buttons on your playlist */
.media-list .row.selected, .media-list .selected .actions, .media-list .actions {
   background: #121214 !important;
} 

/* Changing the background of your playlist settings */
#playlist-edit-button, #playlist-shuffle-button, #playlist-delete-button {
    background: #0d0d0d !important;
}

/* Giving them a hover effect */
#playlist-edit-button:hover, #playlist-shuffle-button:hover, #playlist-delete-button:hover {
    background: #121214 !important;
}

/* Changing the background color of selected items */
#playlist-menu .menu .row.selected {
    background: #009cdd !important;
}    

/* Making the activated button grey */
#playlist-activate-button {
    -webkit-filter: grayscale(100%) !important;
    background: #0d0d0d !important;
}

/* Changing the color of the playlist button */
#playlist-button {
    background: #121214 !important;
}

/* Changing the color of the playlist background */
#playlist-panel {
    background: #0d0d0d !important;
} 

/* Changing the color of the playlist menu */
#playlist-menu {
    background: #121214 !important;
}

/* Changing the color of the playlist button */
#playlist-panel #search {
    background: #0d0d0d !important;
}

/* Making the icons grey */
#your-active-playlist i, #your-next-media i, #room-bar .favorite i, #playlist-menu .menu .row .activate-button .icon {
    -webkit-filter: grayscale(100%) !important;
}

/* Changing the color of the text that says how many songs are in a playlist */
#playlist-menu .menu .row .count {
    color: #FFF !important;
}

/* ###################################### HISTORY ###################################### */

/* Changing the background color of the history */
#history-panel {
    background: rgba(17,19,23,100) !important;
}

/* Changing the border color on history items */
#history-panel .media-list .meta {
    border-bottom: #0D0D0D 1px solid !important;
}

/* Modifying how action buttons look on the history */
#history-panel .media-list .selected .actions {
    background: #0d0d0d !important;
    width: 120px !important;
    height: 54px !important;
}

/* Modifying how action buttons look on the history */
#history-panel .media-list .actions div {
    left: 66px !important;
}

/* Modifying how action buttons look on the history */
#history-panel .media-list .actions {
    background: #101215 !important;
    width: 120px !important;
    height: 54px !important;
}

/* Modifying how action buttons look on the history */
#history-panel .media-list .row.selected {
    background: #0d0d0d !important;
}

/* #################################### HEADER ########################################### */

/* Changing the background color of the header */
.app-header {
    background: #0d0d0d !important;
}

/* Changing the hover effect on items in the menu that opens when you click the plug logo */
#app-menu .list .item:hover {
    background: #0d0d0d !important;
}

/* Making history button darker */
#history-button {
    background: #121214 !important;
}

/* Making the plug menu darker */
.app-right .friends.has-requests .header {
    background: #121214 !important;
}

/* Changing the background color on the menu that opens when you click the plug logo */
#app-menu, #app-menu .list {
    background: #121214 !important;
}

/* Removing the shadow on the menu that opens when you click the plug logo */
#app-menu .list .item {
    box-shadow: none !important;
}

/* ########################## CONTROLS ############################## */

#user-rollover .subscriber .thumb, #waitlist .is-subscriber .thumb {
    border-color: rgba(137, 190, 108, 0.5) !important;
} 

#user-rollover .actions .action.rcs-qban {
    display:none !important;
}

/* Smoothing out the border on buttons of controls */
.header-panel-button {
    border-right: #1A1818 1px solid;
}

/* Blending in with the other colors */
#vote .crowd-response .top {
    background: #222224 !important;
}

/* Removes the weird border around the button */
#vote .crowd-response.selected {
    box-shadow: inset 0 0 0px rgba(0,0,0,0) !important;
}

/* Woot button color */
#woot.selected .top {
    background: #90ad2f !important;
}

/* Grab button color */
#grab.selected .top {
    background: #aa74ff !important;
}

/* Meh button color */
#meh.selected .top {
    background: #c42e3b !important;
}

/* Changing the background color of the info that displays your xp and pp */
#footer-user .info.showing {
    background: #0A0A0A !important;
}

/* Changing the background color of the room settings menu */
#room-settings #room-settings-menu {
    background: #121214 !important;
}

/* Changing the background color of the room Description and settings */
#room-settings .general-settings {
    background: #0d0d0d !important;
}

/* Changing the color of selected items in the room settings menu */
#room-settings-menu .item.selected {
    background: #009cdd !important;
}

/* Rounds up the chat input box a bit */
#chat-input {
    border-radius: 5px !important;
}

/* Changes the background color of the menu in the profile */
#user-menu {
    background: #121214 !important;
}

/* Changes the background color  in the profile */
#user-view, #user-view .user-content {
    background: rgb(13, 13, 13) !important;
}

/* Makes the rollover a bit transparent */
#user-rollover {
    background: rgba(9, 9, 9, 0.7) !important;
}

/* Makes the rollover arrow a bit transparent */
#user-rollover .arrow-down, #user-rollover .arrow-down {
    border-top: 8px solid rgba(9, 9, 9, 0.7) !important;
}

/* Makes the rollover bar thingy that shows the rank a different color */
#user-rollover .info {
    background: rgba(19, 19, 19, 0.5) !important;
}

/* Makes the avatar background in the rollover a bit transparent */
.thumb.large .background {
    background: rgba(0, 0, 0, 0.3) !important;
}

/* Makes the avatar border in the rollover a different color if the user is staff */
#user-rollover.is-staff .thumb {
    border-color: rgba(172, 112, 255, 0.6) !important;
}

/* Makes the rollover arrow a bit transparent */
#user-rollover.expand.chat .arrow-down {
    border-left: 8px solid rgba(10, 10, 10, 0.8) !important 
}

/* Changes the background color in the profile */
#user-view {
    background: rgba(17, 19, 23, 0.99) !important;
}

/* Makes the gradient behind the avatar in the My Stuff Section a bit darker */
#user-menu .avatar {
    background: linear-gradient(to bottom,rgba(83, 93, 128, 0.5) 0, rgba(50, 50, 77, 0.5) 56%, rgba(26, 26, 26, 0.5) 73%, rgba(115, 113, 109, 0.5) 100%) !important;
}

/* Removes maintenance videos */
#video {
    display: none !important;
}

/* Scrollbar */
::-webkit-scrollbar {
    width: 12px !important;
    background-color: #000 !important;
}

::-webkit-scrollbar-thumb {
    background-color: #272727 !important;
}

/* Changing the waitlist button */
#dj-button {
    background: #222224 !important;
}

#dj-button.is-quit .left, #dj-button.is-leave .left, #dj-button.is-wait .left, #dj-button.is-locked .left, #dj-button.is-full .left {
    background: #121214 !important;
}

#dj-button.is-leave .left {
    border-left: 4px solid #f46b40 !important;
}

#dj-button.is-full .left, #dj-button.is-quit .left, #dj-button.is-locked .left {
    border-left: 4px solid #c42e3b !important;
}
}
