css:
@-moz-document domain("search.disconnect.me") {
div#no-results {
    margin-top: 1rem !important;
}

div#results #normal-results.image-results {
    width: 100% !important;
}
    
div#results #normal-results {
    width: 41rem !important;
}

/* ESI core v2.5.5 */
/* Author: Danila Isakov ◀http://dreamject.org▶ */
/* Most actual version: https://github.com/dreamject/ESI/blob/master/esi-core.css */
/* ESI (eyes-saving interface) is a project that allows you to improve quality of vision, */
/* prevent yourself from myopia progression (and, if possible, to reduce it), improve your posture and readability*/
/* Get more: http://dreamject.org/dreamjects/esi/ */
	html:not(.no-such-class):not(.nope)  {font-size: /*[[fs]]*/ !important; }
		html:not(.no-such-class):not(.nope)  *, html:not(.no-such-class):not(.nope) #normal-results li p {font-size: inherit !important; }
	html:not(.no-such-class):not(.nope)  {line-height: /*[[lh]]*/ !important; }
		html:not(.no-such-class):not(.nope) * {line-height: inherit !important; }
	html:not(.no-such-class):not(.nope) a {text-decoration: none !important; }		
	esi-text-block {width: xrem !important; height: yrem !important; }	
	html:not(.no-such-class):not(.nope) h1 {font-size: 1.7rem !important; font-size: calc(1.43rem + 8px) !important;
			line-height: 120% !important; line-height: calc(1.43rem * 1.052 + 8px * 1.052 + 5px) !important; }
	html:not(.no-such-class):not(.nope) h2 {font-size: 1.4rem !important; font-size: calc(1.1rem + 3px) !important;
			line-height: 125% !important; line-height: calc(1.1rem * 1.052 + 1.052 * 3px + 5px) !important; }
	html:not(.no-such-class):not(.nope) h3 {font-size: 1.17rem !important; font-size: calc(1.06rem + 2px) !important;
			line-height: 130% !important; line-height: calc(1.06rem * 1.052 + 2px * 1.052 + 5px) !important; }
	html:not(.no-such-class):not(.nope) sup, html:not(.no-such-class):not(.nope) sub{font-size: 0.68rem !important; font-size: calc(1rem - 5px) !important; line-height: 1rem !important; }
/*[[igh]]*/
/*ESI core end*/

html:not(.no-such-class):not(.nope) * {
     text-shadow: /*[[ss]]*/; font-family: /*[[ff]]*/;
}
.hdtb_mitem {
    height: unset !important;
}
}
