css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {

  /**************
  * General CSS *
  **************/

  body {
    background: url("http://i.imgur.com/VTbLY1A.jpg?1") !important;
    background-size: cover !important;
  }

  ::-moz-selection {
    background: #A5614C !important;
    color: #181818 !important
  }

  .ps-room.ps-room-light {
    background: transparent;
    border-color: #322F2A;
    color: #000;
  }

  .textbox {
    box-shadow: none;
  }

  .ps-popup {
    margin-top: 10px !important;
    border-radius: 3px;
    border-top-left-radius: 0px;
    border-color: #181818;
    background: #181818;
    box-shadow: 0px 0px 0px rgba(255, 255, 255, 0.2) inset, 0px 0px 0px rgba(228, 228, 228, 0.1) inset, 0px 0px 3px rgba(0, 0, 0, 0.5);
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .ps-popup b {
    color: #FFF;
  }

  .ps-overlay .ps-popup {
    position: static !important;
    margin: 80px auto 20px !important;
    max-width: 320px !important;
  }

  .popupmenu h3 {
    color: #0094FF;
    text-shadow: 0px -1px 0px #000;
  }

  .popupmenu button {
    color: #DDE9FF;
    text-shadow: 0px -1px 0px #000;
    width: 220px;
    padding: 1px 4px;
  }

  .popupmenu button:hover, .popupmenu button.sel {
    border-color: #000;
    background: rgba(0, 0, 0, 0.35);
  }

  .popupmenu button.sel:hover {
    border-color: #000;
    background: rgba(0, 0, 0, 0.5);
  }

  .ps-popup a {
    color: #0094FF;
  }

  .ps-popup a:hover {
    color: #0182E5;
  }

  .credits h2, .credits ul {
    color: #0094FF;
  }

  a.subtle, .credits ul li p {
    color: #F2F2F2;
  }

  .credits li small {
    color: #AAA;
  }

  .ps-popup select[name="format"] {
    color: #FFF;
  }

  .ps-popup .roomlist a.ilink {
    width: 250px;
    background: #000;
    font-family: sans-serif;
    font-size: 12px;
    font-weight: bold;
    opacity: 1;
    color: #AE6A53;
    text-transform: none;
    text-align: center;
    text-shadow: none;
    border: 1px solid #000;
    border-radius: 3px;
    background: #303030 none repeat scroll 0% 0%;
    width: 290px;
    margin: 2px 7px 4px;
    padding: 1px 4px 2px;
  }

  .ps-popup .roomlist a.ilink:hover {
    width: 250px;
    background: #000;
    font-family: sans-serif;
    font-size: 12px;
    font-weight: bold;
    opacity: 1;
    color: #AE6A53;
    text-transform: none;
    text-align: center;
    text-shadow: none;
    border: 1px solid #000;
    border-radius: 3px;
    background: #252525 none repeat scroll 0% 0%;
    width: 290px;
    margin: 2px 7px 4px;
    padding: 1px 4px 2px;
  }

  .ps-popup .roomlist a.ilink small {
    color: #B27268;
  }

  .ps-overlay {
    background: rgba(0, 0, 0, 0.35);
  }

  .closebutton, .minimizebutton {
    color: #322F2A;
  }

  /*******************
  * General CSS ~end *
  *******************/

  /*************
  * Header CSS *
  *************/

  .header {
    background: rgba(220, 237, 247, 0.6);
  }

  .maintabbarbottom {
    background: #899DA4;
    border-color: #322F2A;
  }

  .tabbar a.button.subtle-notifying {
    border-color: #322F2A;
    background-color: #A5B8C6;
    text-shadow: none;
    color: #F00;
  }

  .tabbar a.button.subtle-notifying:hover {
    border-color: #322F2A;
    background-color: #ABC0D0;
    color: #F00;
  }

  .tabbar a.button.notifying {
    background: #0094FF;
    border-color: #181818;
    color: #181818;
  }

  .tabbar a.button.notifying:hover {
    background: #FD9191;
    border-color: #0B1361;
    color: #0B1361;
  }

  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    background: #899DA4;
    border-color: #322F2A;
    color: #322F2A;
    text-shadow: none;
  }

  .tabbar a.button {
    border-color: #322F2A;
    background: #A5B8C6;
    text-shadow: none;
    color: #4B4740;
  }

  .tabbar a.button:hover, .tabbar a.button:focus {
    background: #ABC0D0;
    border-color: #322F2A;
    text-shadow: none;
  }

  /************
  * Rooms CSS *
  ************/

  .ps-room.ps-room-light.scrollable {
    background: rgba(220, 237, 247, 0.35);
    color: #000;
  }

  .utilichart:after, .ladder table:after, .pm-window:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/wleX36y.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: 0px;
    top: -7px;
  }

  .infobox:after, .ps-popup:after, .teambuilder-results h3:after, .teambuilder-results .utilichart h3:after, .setchart-nickname:after, #tooltipwrapper .tooltip:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/wleX36y.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
  }

  .infobox, .ladder table, .pm-window, .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    position: relative; 
    border-top-left-radius: 0px;
    border-top-right-radius: 2px;
    margin-top: 5px;    
  }

  .setchart-nickname { 
    border-top-left-radius: 0px;
    border-top-right-radius: 2px;
    margin-top: 2px;
  }

  .infobox {
    background: #181818;
    border-color: #181818;
    color: #F2F2F2;
    text-shadow: none;
  }

  .infobox strong {
    color: #FFF;
  }

  .infobox a {
    color: #0094FF;
  }

  .infobox a:hover {
    color: #006DC0;
  }

  .infobox hr {
    border: none;
    border-bottom: 1px solid #F2F2F2;
  }

  .infobox font[color="#AAAAAA"] {
    color: #DAE9FC !important;
  }

  .infobox button {
    border: 1px solid #F2F2F2;
    background: #000;
    color: #FFF;
    border-radius: 3px;
    padding: 2px 5px;
    margin: 3px;
    text-shadow: none;
  }

  .infobox button:hover {
    border: 1px solid #F2F2F2;
    background: #000;
    color: #0094FF;
  }

  .ladder table, .ladder td, .ladder th {
    border-color: #181818;
  }

  .ladder th {
    background: #181818;
    color: #0094FF;
  }

  .ladder td {
    background: rgba(0, 0, 0, 0.7);
    color: #CDE7F4;
    text-shadow: 1px 1px #181818;
  }

  .userlist {
    background: rgba(220, 237, 247, 0.8);
    border-right: 1px solid #322F2A;
    color: #322F2A;
  }

  .userlist li {
    border-bottom: 1px solid #322F2A;
  }

  .userlist li em.group {
    color: #322F2A;
  }

  .chat-log-add {
    background: rgba(220, 237, 247, 0.8);
    border-top: 1px solid #322F2A;
  }

  .chat-log-add .textbox {
    border: 1px solid #322F2A;
  }

  .chat-log {
    background: rgba(235, 235, 235, 0.9);
  }

  .chat-log .chat small {
    color: #666;
  }

  .roomlist a.ilink {
    font-size: 14px;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight: bold;
    opacity: 0.9;
    color: #fbfbfb;  
    text-transform: uppercase;
    text-align: left;
    text-shadow: 1px 1px 1px #4d4d4d, 1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/DqmewkB.png);
    box-shadow: none; 
    border-radius: 0px; 
    border-width: 0; 
    width: 642px;
    padding-left: 20px;
    padding-top: 10px;
    padding-bottom: 12px;
  }

  .roomlist a.ilink:hover {
    font-size: 14px;
    opacity: 0.9;
    font-family: Tahoma, Verdana, Segoe, sans-serif;
    font-weight: bold;
    color: #fbfbfb;  
    text-transform: uppercase;
    text-shadow: 1px 1px 1px #4d4d4d, 1px 0px 1px #4d4d4d;
    background: url(http://i.imgur.com/UoUnGNL.png);
    box-shadow: none; 
    border-radius:0px; 
    border-width: 0; 
    width: 642px;
    padding-left: 20px;
    padding-top: 10px;
    padding-bottom: 12px;
  }

  .roomlist a.ilink small {
    color: #fbfbfb; 
  }

  .roomlist h2 {
    text-shadow: none;
  }

  /*****************
  * Rooms CSS ~end *
  *****************/

  /**********************
  * Mainmenuwrapper CSS *
  **********************/

  .pm-window h3 {
    background: #181818;
    border-color: #181818;
    border-top-left-radius: 0px;
    border-top-right-radius: 3px;
    color: #0094FF;
    transition: All 1.1s ease;
  }

  .pm-window h3:hover {
    color: #006DC0;
    background: #181818;
    border-color: #181818;
  }

  .pm-window.focused h3, .pm-window.focused h3:hover {
    background: #181818;
    border-color: #181818;
    border-top-left-radius: 0px;
    border-top-right-radius: 3px;
    color: #006DC0;
  }

  .pm-window h3 small {
    color: #004A7F;
  }

  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #DDF0FE;
    transition: All 1.1s ease;
  }

  .pm-window h3 .closebutton:hover {
    color: #A5614C;
  }

  .pm-window h3 .minimizebutton:hover {
    color: #D7C77C;
  }

  .pm-window h3.pm-minimized {
    border-radius: 3px;
    border-top-left-radius: 0px;
  }

  .pm-window h3.pm-notifying {
    background: #0094FF;
    border-color: #181818;
    color: #181818;
  }

  .pm-window h3.pm-notifying:hover {
    background: #007FE1;
    border-color: #181818;
    color: #181818;
  }

  .pm-window.news-embed {
    opacity: 0.5;
  }

  .pm-window.news-embed:hover {
    opacity: 1;
  }

  .pm-log {
    border-color: #181818;
    background: url("http://i.imgur.com/KpRUrnS.png");
    color: #F1C974;
  }

  .pm-window.focused .pm-log {
    border-color: #181818;
  }

  .pm-log .chat small {
    color: #DAB767;
  }

  .pm-log .username {
    text-shadow: 1px 1px #000;
  }

  .pm-log a {
    color: #0094FF;
  }

  .pm-log a:hover {
    color: #006DC0;
  }

  .challenge {
    border-color: #181818;
    background: #181818;
    color: #DDF0FE;
  }

  .challenge .select {
    border-color: #000;
    box-shadow: none;
    background: -moz-linear-gradient(center top , #151515, #080808);
    background: linear-gradient(to bottom,hsl(0, 0%, 8%),hsl(0, 0%, 3%));
    color: #FFF
  }

  .challenge .select:hover {
    background: #090909;
    color: #0094FF;
  }

  .challenge .select:disabled {
    background: rgba(8, 8, 8, 0.5);
    color: #CEDBEC !important;
  }

  .challenge .select::after {
    color: #DDF0FE;
  }

  .challenge button {
    border: 1px solid #000;
    background: #181818;
    color: #0094FF;
    border-radius: 3px;
  }

  .buttonbar button {
    border: 1px solid #000;
    background: #121212;
    color: #FFF;
    border-radius: 3px;
    padding: 2px 5px;
  }

  .buttonbar button:hover {
    color: #0094FF;
  }

  .pm-log-add {
    border-color: #181818;
    background: #181818; 
  }

  .pm-window.focused .pm-log-add {
    border-color: #181818;
    background: #181818; 
  }

  .pm-log-add .textbox {
    box-shadow: none;
    border: none;
    background: #181818;
    color: #F1C974;
  }

  .pxInta, .pxInta:focus, .pxInta:link, .pxInta:visited, .pxInta:active, .pxInta:hover {
    border-width: 0px 0px 0px !important;
    border-style: none none none !important;
    border: none !important;
    -moz-border-top-colors: none !important;
    -moz-border-right-colors: none !important;
    -moz-border-bottom-colors: none !important;
    -moz-border-left-colors: none !important;
    border-image: none !important;
    background-color: transparent !important;
    color: #F1C974 !important;
    font-weight: normal !important;
    text-decoration: none !important;
    padding-bottom: 0px !important;
    display: inline !important;
    pointer-events: none !important;
    cursor: default !important;
  }

  .menugroup {
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
    border: 1px solid #000;
    background: rgba(220, 237, 247, 0.2);
  }

  .menugroup .button, .roomcounters button {
    background: rgba(0, 0, 0, 0.78) !important;
    border-color: #000 !important;
    border-radius: 3px;
    color: #DDEAFD;
    text-shadow: 0px -1px 0px #000;
    font-family: sans-serif;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    padding: 3px 7px 5px;
    transition: All 1.1s ease;
  }

  .menugroup .button:hover, .roomcounters button:hover {
    background: rgba(0, 0, 0, 0.85) !important;
    color: #0094FF;
  }

  .menugroup .button:focus {
    box-shadow: 0px 0px 3px #0094FF, 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
  }

  .select {
    background: rgba(0, 0, 0, 0.78) !important;
    border-color: #000 !important;
    border-radius: 3px;
    color: #DDEAFD;
    text-shadow: 0px -1px 0px #000;
    font-family: sans-serif;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    transition: All 1.1s ease;
  }

  .select::after {
    color: #DDEAFD;
  }

  .select:hover {
    background: rgba(0, 0, 0, 0.85) !important;
    color: #0094FF;
  }

  .select:focus {
    box-shadow: 0px 0px 3px #0094FF, 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
  }

  .select:disabled {
    background: rgba(0, 0, 0, 0.5) !important;
    color: #CCC !important;
  }

  .mainmenufooter {
    background: rgba(220, 237, 247, 0.2);
    position: fixed;
    bottom: 0px;
    left: 0px;
    padding: 10px;
    border-width: 1px 1px 0px 0px;
    border-style: solid solid none none;
    border-color: #000;
    border-top-right-radius: 7px;
    color: #2C4049;
    text-shadow: 0px 0px 1px #769295;
  }

  .mainmenufooter a {
    color: #2C4049;
    text-shadow: 0px 0px 1px #769295;
  }

  .mainmenufooter a:hover {
    color: #1F2C33;
  }

  /***************************
  * Mainmenuwrapper CSS ~end *
  ***************************/

  /******************
  * Teambuilder CSS *
  ******************/

  .ps-room.ps-room-light.scrollable ul li button {
    border-radius: 3px;
    border: 1px solid #000;
    margin-bottom: 2px;
    background: #181818;
    color: #F2F2F2;
    padding: 2px 5px;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .ps-room.ps-room-light.scrollable ul li button:hover, .ps-room.ps-room-light.scrollable ul li button:focus {
    color: #0094FF;
  }

  .teamchartbox ::-moz-selection {
    background: #000 !important;
    color: #0094FF !important;
  }

  .teambuilder-results h3:after, .teambuilder-results .utilichart h3:after, .setchart-nickname:after, .statform:after, .detailsform:after {
    display: block;
    content: "";
    background: url(http://i.imgur.com/wleX36y.png);
    width: 45px;
    height: 7px;
    position: absolute;
    left: -1px;
    top: -7px;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3, .statform, .detailsform {
    position: relative; 
    border-top-left-radius: 0px;
    margin-top: 5px;    
  }

  .setchart-nickname { 
    border-top-left-radius: 0px;
    border-top-right-radius: 2px;
    margin-top: 2px;
  }

  .teamchartbox {
    border-top: none;
    font-family: sans-serif;
  }

  .teambuilder-pokemon-import .textbox {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .teambuilder-pokemon-import .textbox:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .teambuilder-pokemon-import .textbox:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    border-color: #181818;
    background: #181818;
    color: #FFF;
    text-shadow: 1px 1px #000;
    box-shadow: none;
    margin: 10px 0px 5px 0px;
    font-family: sans-serif;
  }

  .setchart {
    border-color: #000;
    background: #181818;
    box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.2), 0px -2px 0px rgba(0, 0, 0, 0.2) inset, 0px 1px 1px rgba(255, 255, 255, 0.2) inset;
    margin-top: 9px;
    border-radius: 3px;
  }

  .setchart label, .setchart-nickname label {
    color: #FFF;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .setchart input, .setchart-nickname input, .setdetails, .setstats, .statform input.inputform {
    box-shadow: none;
    border-color: #000;
    background: #303030;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
  }

  .setchart input:hover, .setchart-nickname input:hover, .setdetails:hover, .setstats:hover, .statform input.inputform:hover {
    box-shadow: none;
    border-color: #000;
    background: #222;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .setchart input:focus, .setchart-nickname input:focus, .setdetails:focus, .setstats:focus, .statform input.inputform:focus {
    box-shadow: 0px 0px 2px rgba(0, 0, 0, 0.75);
    border-color: #000;
    background: #191919;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
  }

  .setdetails .detailcell {
    border-left: 1px solid #000;
  }

  .setchart-nickname {
    border-color: #181818;
    background: #181818;
    box-shadow: none;
    border-radius: 3px;
    border-top-left-radius: 0px;
  }

  .teambuilder-results .firstresult a {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
  }

  .teambuilder-results .firstresult a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.25) !important;
  }

  .teambuilder-results .result a:hover {
    border-color: #181818;
    background: rgba(0, 0, 0, 0.2);
  }

  .utilichart b {
    color: #000;
  }

  .utilichart {
    font-family: sans-serif;
  }

  .utilichart .numcol {
    color: #713624;
  }

  .utilichart .typecol img {
    opacity: 0.85;
  }

  .setstats .statrow-head em {
    color: #EEE;
  }

  .teambar button {
    border-bottom-right-radius: 3px;
    border-bottom-left-radius: 3px;
    overflow-x: hidden;
    background: #181818;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;
    font-family: sans-serif;
    border-color: #000;
  }

  .teambar button:hover {
    background: #303030;
    color: #F2F2F2;
    border-color: #000;
  }

  .teambar button:disabled, .teambar button:disabled:hover, .teambar button:disabled:active {
    background: rgba(0, 0, 0, 0.75);
    opacity: 1;
    border-color: #000;
    color: #F2F2F2;
    text-shadow: 1px 1px #000;    
  }

  .setmenu {
    top: 3px;
  }

  .setmenu button {
    font-family: sans-serif;
  }

  .setmenu button:hover {
    background: rgba(0, 0, 0, 0.4); 
    border-color: #000;
    color: #000;
    text-shadow: none;  
  }

  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em {
    color: #555;
    font-size: 10px;
  }

  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
    color: #555;
  }

  .utilichart .col {
    color: #222;
  }

  div[style="position: absolute; margin: 0px; top: 123px; left: 495.667px;"] {
    border: 1px solid #000;
