css:
@-moz-document domain("empeopled.com") {
html, body {
    color: #f8f8f8;
    background-color: #222222;
}

a {
    color: #f8f8f8;
}

.comment
.comment-content {
    /*background: #444444;*/
    background: none;
}

.post .stickie {
    background: #454545;
}

.post
.stickie-hide-right {
    background: #555555;
}

.post
.stickie-hide-left {
    background: #555555;
}

.post-form
.post-select a {
    background: #656565;
}

.govern-header {
    background: #454545;
}

.section-header {
    background: #454545;
}

.modal-content {
    background-color: #454545;
}

.input-group-addon {
  	background-color: #757575;
}

.close {
  	color: white;
}

.topics-menu li.menu-group {
  	background: #a5a5a5;
}

.topic-header {
	background: #555555;
}

h3 > a {
  color: white;
}

.post .post-media-wrap .post-media {
  	background: #454545;
}

.comment .comment-content.highlight {
  	background: #454545;
}

.profile .profile-header {
  background: #454545;
}

.btn-default {
  background: #959595;
}

.profile-topic-wrap > a {
      color: #959595;
}

.fa-inverse {
  color: #656565;
}

#levelup_modal .level {
  color: #454545;
}

.post .post-poll .poll table tr td a.poll-vote {
  color: #757575;
}

.account .account-section {
  background: #353535;
}

.topic .description {
  background: #353535;
}

.feed-notifications li > a, .feed-messages li > a {
  color: #f8f8f8;
}

.live-xp .points {
  color: #353535;
}














