css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developpez.com"), domain('developpez.net') {

/*Nettoyage*/

	/*Colone gauche*/
    #colonneDroite, #RubriqueDroite,
    /*Offre emploie*/
    #actu > div:nth-child(4),      
    /*colone droite*/
    #colonneDroite,
    /*Pides de page*/
    #gabarit_responsable, #gabarit_pied, #gabarit_copyright,
    /*Msg cnx*/
    #navbar_notice_2,
    #gabarit_recherche,
    body > header:nth-child(1) > div:nth-child(3),
    #gabarit_barre2 {
    display: none;
  }
  
  #listing {
    width: 50%;
  }
  
  .propose {
    text-align: left;
  }
  
  /*dark*/
    html, body, .contenuPage {
    background: #1c2126;
    color: #BDC3C7;
  }
    
  #gabarit_menu > li a.gabarit_selection, #gabarit_menu > li > a:hover, #gabarit_menu > li:hover > a {
    background: #39424d !important;
    color: #BDC3C7 !important;
  }
  
  .gabarit_sousmenu li a, .annonce:nth-child(odd) {
    background: #39424d !important;
    color: #BDC3C7 !important;
    border-bottom: none;
  }
  
  .alaune {
    background: none;
  }
  
  .alaune2, .alauneTitle a, .alauneContent {
    color: #BDC3C7 !important;
    background: #1C2126;
  }
  
  .annonce:nth-child(even) {
    background: #1c2126 !important;
    color: #BDC3C7 !important;
    border-bottom: none;
  }
  
  .gabarit_sousmenu li a:hover, .annonce:hover {
    background: #14171A !important;
    color: #FFF !important;
  }
  
  .above_body, .footer {
    box-shadow: none;
  }
  
  .annonceLink, .annonceLink:link {
    color: #59ABE3;
  }
  
  body a, #forums a.username {
    color: #59ABE3;
  }
  
  /*Pseudo commentaire*/
  #nrreview .header {
    background: none;
  }
  
  /*texteditor commentaire*/
  .texteditor.forum .cke_skin_kama textarea.cke_source, .texteditor.forum textarea, .formcontrols .blockrow, .row, .postUser {
    background-image: none;
    background: #39424d;
    color: #BDC3C7;
  }
  
  /*Citation commentaire*/
  .bbcode_container div.bbcode_quote {
    background-color: #333333;
  }
  
  /*Fleche haute gauche Citation commentaire*/
  .bbcode_container .bbcode_quote_container {
    display: none;
  }
  
  /*Infos et balise utilisable commentaire*/
  .formcontrols .section {
    padding: 0;
  }
  
  /*Infos et balise utilisable commentaire*/
  #forumrules {
    background: #39424D;
  }
  
  .postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    background: #39424d;
    color: #BDC3C7;
  }
  
  .postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
    color: #BDC3C7;
  }
  
  #gabarit_barre2 {
    background: #39424d;
    border-style: none;
  }
  
  #gabarit_barre1, #gabarit_barre1 li, #gabarit_menu, #gabarit_barre2 li {
    background: #14171a;
    border-style: none;
  }
  
  .contenuPage a:hover {
    color: #59ABE3;
    background: none;
  }
  
  .contenuPage a, .contenuPage a:visited {
    background: none;
  }
  
  .cellule_categorie {
    color: #BDC3C7;
    background: none !important;
    
  }
  
  /*auteur article*/
  div.date:nth-child(2) > a:nth-child(2) {
    color: #019875 !important;
  }
  
  /*sources + questions*/
  .content > a[target="_blank"] {
    color: #59ABE3 !important;
  }
  
  /*auteur comm*/
  .fn {
     color: #59ABE3 !important;
  }
  
  
  a {
    color: #59ABE3;
  }
  
  a:visited {
    color: #913D88;
  }
  
  a:hover{
    color: #1BA39C
  }
  
  #pollinfo .polltitle {
    color: whitesmoke;
}

  /*sondage*/
  #pollinfo {
      margin: initial;
  }

  #pollinfo {
      border: none;
  }
  .pollresultsblock {
      background: #1C2126 none repeat scroll 0% 0%;
  }
  /*sondage fin*/
    
   /*span {
    background: #39424d;
    color: #BDC3C7;
  }*/
  
}
