@-moz-document domain("empeopled.com") {

/*-------------------------------------------------------------------------*/  
/*                             Lightbulb icons                             */
/*                                                                         */
/*                                                                         */
  div.feed-posts > div:nth-child(2n+2) div.hidden-xs.col-sm-3.col-md-2,
  div.topic-content > div:nth-child(2n+2) div.hidden-xs.col-sm-3.col-md-2,
  .topic-stickies .hidden-xs.col-sm-3.col-md-2 {
   transition: background-color 0.25s ease;
    background-color: #242932 !important;
  }
  div.feed-posts > div:nth-child(2n+2) div.hidden-xs.col-sm-3.col-md-2:hover,
  div.topic-content > div:nth-child(2n+2) div.hidden-xs.col-sm-3.col-md-2:hover {
    transition: background-color 0.25s ease;
    background-color: #353D48 !important;
  }
  
  div.feed-posts > div:nth-child(2n+1) div.hidden-xs.col-sm-3.col-md-2,
  div.topic-content > div:nth-child(2n+1) div.hidden-xs.col-sm-3.col-md-2 {
    transition: background-color 0.25s ease;
    background-color: #1D2026 !important;
  }
  
  div.feed-posts > div:nth-child(2n+1) div.hidden-xs.col-sm-3.col-md-2:hover,
  div.topic-content > div:nth-child(2n+1) div.hidden-xs.col-sm-3.col-md-2:hover {
    transition: background-color 0.25s ease;
    background-color: #2A2F39 !important;
  }

  div.topic div.hidden-xs.col-sm-3.col-md-2,
  div.feed div.hidden-xs.col-sm-3.col-md-2 {
    padding-top:23px;
    margin-top:-23px;
    padding-bottom:60px;
    margin-bottom:-60px;
    border-top:#101112 solid 3px;
  }
/*
  .post .post-info {
    padding-bottom: 15px !important;
  }*/
  
/*-------------------------------------------------------------------------*/
/*-------------------------------------------------------------------------*/
  
/*--------------------------------------------*/
/*            Vote Button Spacing             */
/*                                            */
/*                                            */

/*[[slimTweaks]]*/
  
  .row.stickie .vote {
    left:-70px !important;
  }
  
  .topic-stickies .vote {
    margin-left: 50px !important;
  }
  
  .feed-posts .fa.fa-arrow-circle-up,
  .row.stickie .fa.fa-arrow-circle-up,
  .topic-content .fa.fa-arrow-circle-up {
    position: absolute;
    left: -24px;
    top: -1px;
  }
  
  .feed-posts .fa.fa-arrow-circle-down,
  .row.stickie .fa.fa-arrow-circle-down,
  .topic-content .fa.fa-arrow-circle-down {
    position:absolute;
    left: 40px;
    top: -1px;
  }
  
  .feed-posts .vote-sum,
  .row.stickie .vote-sum,
  .topic-content .vote-sum{
    position: absolute !important;
    width: 40px;
    left: 0px;
  }
/*---------------------------------------------*/
/*---------------------------------------------*/
  
  .row.search-result .media-image-link{
    background-color: #7C96A2 !important;
  }
  
  .tooltip-arrow {
	border-bottom-color: #242932 !important;
  }
  
/*main bg and text*/
  html, 
  body,
  .feed .hero{
    background: #101112 !important;
    color: #AAA;
  }
  
  body {
    overflow-y: hidden;
  }
  
  .tooltip-inner {
    background: #242932 !important;
    color: #DDD;
  }
  
  .post-form .post-select a,
  .post .stickie,
  div.row.stickie a.post-link.media-image-link{
    background-color: #242932 !important;
    background-size: 100%;
  }
  
  .row .stickie{
    border-top: solid 5px #242932 !important;
    padding-bottom: 2px;
    margin-bottom: -2px;
  }
  
  div.feed-posts div.future img{
    margin-top: 0;
  }
  
  div.feed-posts div.future div.row div.hidden-xs.col-sm-3.col-md-2{
    border-top: none;
  }
  
  body div.discussion-content,
  .feed .hero{
    border-bottom: #495A60 dashed 1px !important;
  }
  
/*color background for every other post on frontpage*/
  div.feed-posts > div:first-child,
  div.feed-posts div:nth-child(2n+3),
  div.topic-content > div:first-child,
  div.topic-content div:nth-child(2n+3){
    background-color: #1D2026 !important;
    border-bottom: #101112 3px solid !important;
    border-radius: 10px;
  }
  
  .topic-header, 
  .navbar-default,
  .section-header,
  .govern-header,
  .section-nav{
    background: #101112 none repeat scroll 0% 0%;
    border-bottom: #495A60 dashed 1px;
  }
  
  .feed-posts,
  .topic-content {
    padding-left: 10px !important;
    padding-right: 10px !important;
    border-left: 2px dashed #212327 !important;
    border-right: 2px dashed #212327 !important;
  }
  
  .feed-stickies {
    padding-left: 10px;
  }
  
  .topic-stickies .hidden-xs.hidden-sm.col-md-2.text-center.comments-count-wrap {
    margin-right: 8px !important;
  }
  
/*link colors*/
  a,
  h3.topic-name a,
  .faq p a:hover,
  header .payout-rate-inline,
  .richtext a:hover,
  .comments-wrap a.load-more:hover,
  header .nav-wrap .logo,
  a#mytopics,
  .comment-form .attach a,
  .profile .comment-icon:hover,
  .formatting-tips a.tip-toggle,
  ul.notification-dropdowns > li > a,
  .checkbox.checked,
  .checkbox.checked .second-icon,
  .checkbox:hover .second-icon,
  .more-nav {
    color: #7C96A2 !important;
  }
  
  li.dropdown a.new {
    color: #E74C3C !important;
  }
  
  a:hover,
  h3.topic-name a:hover,
  .faq p a,
  header .payout-rate-inline:hover,
  .richtext a,
  .comments-wrap a.load-more,
  header .nav-wrap .logo:hover,
  .comment-form .attach a:hover,
  a.poll-vote,
  .vote a,
  .comment .actions .votes a,
  ul.notification-dropdowns > li > a:hover,
  .navbar-default .navbar-nav > li > a,
  .btn-default:hover,
  a#mytopics:hover,
  .formatting-tips a.tip-toggle:hover,
  .checkbox,
  .checkbox .icons,
  .account .info .edit{
    color: #313C42 !important;
  }
  
  .vote a:hover,
  .comment .actions .votes a:hover,
  .post .post-poll .poll table tr td a.poll-vote:hover,
  .post .post-poll .poll table tr td a.poll-vote{
    color: #7C96A2 !important;
  }
  
  a:focus,
  h3.topic-name a:focus,
  .faq p a:focus
  .comments-wrap a.load-more:focus,
  header .nav-wrap .logo:focus,
  a#mytopics:focus,
  .comment-form .attach a:focus,
  ul.notification-dropdowns > li > a:focus,
  .navbar-default .navbar-nav > li > a:focus,
  .vote a:focus,
  .comment .actions .votes a:focus,
  .post .post-poll .poll table tr td a.poll-vote:focus,
  .formatting-tips a.tip-toggle:focus{
    color:#43535A !important;
  }
  
  .vote a.active,
  .comment .actions .votes a.active,
  .poll-vote.active{
    color: #7C96A2 !important;
  }
  
  .list-group-item:hover{
    background-color: #101112 !important;
    color: #EBEBEB !important;
  }
  
  .fa-inverse {
    transition: color 0.25s ease;
    color: #101112;
  }
  
  .fa-inverse:hover {
    transition: color 0.5s ease;
    color: #EBEBEB;
  }
   
/*Visited post color*/
  .row .stickie a:visited h3.post-title,
  div.feed-posts a:visited h3.post-title,
  div.topic-content a:visited h3.post-title{
    color: #3A464E !important;
  }
  
/*Submit button*/
  .btn-primary{
    background-size: 100%;
    background-color: #1D2026 !important;
    color: #DADADA !important;
  }
  
/*Cancel Button*/
  .btn-default,
  .post .post-media-wrap .post-media{
    background: #4C6472 !important;
    color: #1C1C21 !important;
  }
  
  div.govern-future img,
  div.govern-treasury img,
  div.govern-topics img,
  div.govern-rules img{
    transition: background-color 0.25s ease, border 0.25s ease;
    background-color:#7C96A2 !important;
    border: 5px solid #7C96A2;
    border-radius: 5px;
  }
  
  div.govern-future img:hover,
  div.govern-treasury img:hover,
  div.govern-topics img:hover,
  div.govern-rules img:hover {
    background-color:#323E44 !important;
    border: 5px solid #323E44;
  }
  
  .media-image-link.photobox:hover,
  .post .post-media-wrap .embedded-img:hover,
  .btn-default:hover, 
  .open .dropdown-toggle.btn-default{
    background-color:#323E44 !important;
    color: #DADADA !important;
  }

  .btn-primary:focus,
  .btn-default:focus,
  .modal .form-actions .button:active{
    background: #EBEBEB !important;
    color: #43535A !important;
  }
  
  .btn-primary:hover {
    background-color: #7C96A2 !important;
    color: #101112 !important;
  }
  
/*--------------------------------*/
/*          Post Buttons          */
/*                                */
/*                                */
  .post-form .post-select a:hover {
    background:#242932 !important;
    color: #EBEBEB !important;
  }
  
  .post-form .post-select a:focus,
  .post-form .post-select a.active{
    background:#7C96A2 !important;
    color: #242932 !important;
  }
/*---------------------------------*/
/*---------------------------------*/
  
  /*Side Panel Button*/
  .btn-offcanvas {
    background-color: #101112 !important;
  }
  
  .btn-offcanvas:hover,
  .btn-offcanvas:focus {
    color: #7C96A2 !important;
    background-color:#7C96A2 !important;
  }
  
  .btn-offcanvas i {
    transition: color 0.25s ease;
    color: #7C96A2 !important;
  }
  
  .btn-offcanvas:hover i,
  .btn-offcanvas:focus i {
    color: #101112 !important;
  }
 
  .navbar-default .navbar-nav > .dropdown > a .caret {
    border-top-color:#7C96A2 !important;
    border-bottom-color:#7C96A2 !important;
  }
  
  .navbar-default .navbar-nav > .dropdown > a:hover .caret {
    border-top-color:#43535A !important;
    border-bottom-color:#43535A !important;
  }
  
  .navbar-default .navbar-nav > .dropdown > a:focus .caret {
    border-top-color:#43535A !important;
    border-bottom-color:#43535A !important;
  }

  
  .richtext blockquote,
  .post .post-info,
  .post .stickie .post-info,
  .future .future-info,
  .choice .choice-info,
  .treasury .treasury-info,
  .topic-nomination-banner .topic-nomination-info,
  .moderators .moderators-info,
  .rules .rules-info,
  .punishments .punishments-info,
  .post .post-poll .poll table tr td:last-child {
    border-left: #495A60 solid 2px !important;
  }
  
  
  .comment .children {
    border-left: #495A60 dotted 2px !important;
  }
  
  .comment .children:hover {
    border-color: #7C96A2 !important;
  }
  
  .container {
    width: 80%;
  }
  
  .comment-body.richtext p{
    width: 1200px;
  }
  
  /*
  .discussion-comments .comments {
    border-left: 2px dotted #7C96A2;
  }
  */
  
/*context*/
  .post .post-info .contextualinfo, 
  .comment .contextualinfo .etc,
  .randomContext{
    color: #4C4C4C !important;
  }
  
  
  a#mytopics,
  .profile .comment-icon{
  color: #7C96A2 !important;
  }
  
  h3.topic-name a:hover{
    color: #43535A !important;
  }
  
  .profile .profile-header, 
  .feed-posts,
  .account-section,
  .post .stickie-hide-left, 
  .post .stickie-hide-right{
    background: transparent;
  }
  
  /*comment form bg*/
  .form-control {
     color:#AAAAAA !important;
     background-color: #101112 !important;
  }
  
  .form-control{
    border: 2px solid #1D2026 !important;
  }
  
  /*comment form focus border*/
  .form-control:focus,
  .offcanvas.in,
  .xp-user {
    border-color: #7C96A2 !important;
  }
  
  .xp-user {
    background: #101112;
    color: #EBEBEB !important;
  }
  
  .xp-user-display {
    background-color: #7C96A2 !important;
  }
  
  .input-group-addon{
    background-color: #7C96A2 !important;
    border: 1px solid #7C96A2 !important;
    color: #1C1C21 !important;
  }
  
  /*Topics title text*/
  .navbar-default .navbar-nav > .open > a, 
  .navbar-default .navbar-nav > .open > a:hover, 
  .navbar-default .navbar-nav > .open > a:focus{
    color: #43535A !important;
  }
  
  /*dropdown main bg*/
  .dropdown-menu,
  .modal-content{
    background: #202327 !important;
  }
  
  /*dropdown banners bg*/
  .topics-menu li.menu-group {
    background: #2A2E35;
    border-bottom: #101112 solid 2px !important;
  }
  
  .modal-header{
    border-bottom-color: #495A60 !important;
  }
  
  /*dropdown text*/
  .dropdown-menu li > a,
  .feed-notifications li > a{
    color: #525F6C !important;
  }
  
  /*dropdown hover bg*/
  .dropdown-menu li > a:hover,
  .dropdown-menu li > a:active,
  .dropdown-menu li > a:focus,
  .feed-notifications li > a:hover,
  .feed-notifications li > a:active,
  .feed-notifications li > a:focus {
  background: #2A2E35 !important;
  color: #CCCCCC !important;
  }
  
  /*load new comments button*/
  a.comment-live,
  .comment .comment-media{
    background: #101112 !important;
    border: 2px solid #2A2E35 !important;
    color: #4E5F6B !important;
  }
   
  a.comment-live:hover,
  .comment .comment-media:hover{
    border: #101112 2px solid !important;
    background: #242E35 !important;
    color: #EBEBEB !important;
  }
  
  .media-image-link.photobox,
  .post .post-media-wrap .embedded-img,
  .future img,
  .treasury img,
  .topic-nomination-banner img,
  .rules img{
    background-color: #1D2124 !important;
  }
  
  .post .post-media-wrap .post-media:hover{
    background-color: #374D5A !important;
    color: #EBEBEB !important;
  }
  
  .post-media,
  .post-media:hover{
    color: #101112 !important;
  }
  
  .profile .comment-icon-box,
  .govern-header img{
    background: #2A2E35 !important;
    background-size: 100%;
  }
  
  li.notification.unread {
    background: #202327 !important;
  }
  
  li.notification {
    background: #2A2E35 !important;
  }
  
  ul.dropdown-scrollable.notifications-menu li a:hover,
  .navbar-default .navbar-nav > li > a:hover,
  .topics-menu li a:hover {
    color: #CCCCCC !important;
  }
  
  .comment .comment-content.highlight {
    background: transparent;
    border: #495A60 dashed 1px;
    color: #DDDDDD !important;
  }
  
  strong.fa-stack-1x{
    color: #101112 !important;
  }
  
  li.notification a{
    border-bottom-color: #101112 !important;  
  }
  
  .dropdown-menu .dropdown-footer {
    border-top: 2px solid #101112 !important;
    background: #2A2E35 !important;
  }
  
  .see-all:hover {
    color: #CCCCCC !important;
  }
  
  div.comments > div > div.row.comment:nth-of-type(n+1):after{
    content:''; 
    width:1000px; 
    height:0px; 
    background:transparent; 
    position:relative; 
    left: 18px;
    margin-bottom: 10px;
    border-bottom: 1px #495A60 dotted !important;
  }
  
  .formatting-tips table td,
  .faq table td{
    border: #495A60 1px solid;
  }
  
  .table-bordered > thead > tr > th, 
  .table-bordered > thead > tr > td, 
  .table-bordered > tbody > tr > th, 
  .table-bordered > tbody > tr > td, 
  .table-bordered > tfoot > tr > th, 
  .table-bordered > tfoot > tr > td,
  .table > thead:first-child > tr:first-child > th{
    border: #242E35 1px solid;
  }
  
  .richtext table thead tr th,
  .faq table th{
    background: #7C96A2 !important;
    border: #242E35 1px solid !important;
    color:#101112 !important;
  }

  .topic-header .topic-follow{
    background:#4E5F6B !important;
    color: #EBEBEB !important;
  }

  .topic-header .topic-unfollow{
    background:#2A2E35 !important;
    color:#4E5F6B !important;
  }
    
  .topic-header .topic-unfollow:hover,
  .topic-header .topic-follow:hover{
    background-color: #374D5A !important;
    color: #EBEBEB !important;
  }

  .close, .close:hover{
    color: white !important;
    text-shadow: white !important;
  }
  
  tbody:hover {
    background: #101112 !important;
  }
  
  .feed-stickies .post {
    padding: 20px 0 0;
  }
  
  .feed-stickies,
  .topic-stickies {
    padding-bottom: 4px;
  }
  
  .dropdown-menu.notif.notifications-dropdown {
    border: #101112 1px solid !important;
  }
  
  .dropdown-arrow {
    border-color: transparent transparent #43535A !important;
  }
  
  .container-fluid.govern-header h3,
  .container-fluid.section-header .col-xs-12,
  .govern-icon{
    color: #70838F !important;
  }
  
  .table-hover > tbody > tr:hover > td{
    background-color:#141517 !important;
  }
  
  .progressbar-wrap .progressbar {
    background-color: #7C96A2 !important;
  }
  
  .topic .description {
    background-color: #101112 !important;
    border: #495A60 dashed 1px !important;
  }
  
  .profile .media-image-link {
    background-color: #2A2E35 !important;
    border: 10px #2A2E35 solid !important;
    box-sizing: border-box;
  }
  
  .feed-posts .post,
  .feed-posts {
    padding: 5px 0;
  }
  
  .discussion-content .post-link.media-image-link {
    background-color: #7C96A2 !important;
    border: 5px solid #7C96A2 !important;
    transition: background-color 0.25s ease, border-color 0.25s ease;
    border-radius: 5px;
  }
  
  .row.account-section {
    background: #101112;
    border: #313C42 1px dashed;
  }
  
  .discussion-content .post-link.media-image-link:hover {
    background-color: #313C42 !important;
    border-color: #313C42 !important;
    transition: background-color 0.25s ease, border-color 0.25s ease;
  }
  
  .discussion-comments {
    margin-left: -140px;
  }

  .topic-avatar {
	  background: #7C96A2;
  }
  
  /*For random topics script*/
  .randBtn {
    background: #2A2E35 !important;
  }
  
  .linkDiv{
    border-bottom: #495A60 dashed 1px !important;
  }
  
  .infoDiv{
    color: #4C4C4C !important;
  }
  
  /*For highlight OP script*/
  .OPmain, .OPcom {
    background-color: #2B5A74 !important;
    color: #FFF !important;
  }
  
  .OPmain:hover, .OPcom:hover {
    background-color: #122F41 !important;
  }
}

