css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("empeopled.com") {

html,
body {
  overflow-x: hidden;
  /* Prevent scroll on narrow devices */
  padding: 30px 0 0 0;
  color: #222222;
  background-color: #f8f8f8;
  font-size: 120%;
  font-family: "Times New Roman", serif;

}

body.signup main,
body.signup #panel {
  margin-top: 66px;
}

@media (max-width: 767px) {
  body main,
  body #panel {
    margin-top: 27px;
  }

  body.signup main,
  body.signup #panel {
    margin-top: 115px;
  }
}
p {
  font-size: 1rem;
}

a {
  color: #222222;
}
a:hover {
  color: #2980b9;
}

.sep {
  padding: 0 10px;
}

.container-fluid.sep {
  border-bottom: 2px #ecf0f1 solid;
  padding: 30px 0;
}

.btn-default {
  background: #ECF0F1;
  color: #222222;
}

.btn-primary {
  background: #2980b9;
}

button i {
  margin-right: 5px;
}

i.expert {
  color: #f1c40f!important;
}

strong.expert {
  color: #222!important;
}

.attach-image-btn {
  cursor: pointer;
}

.new-comment {
  color: #c0392b;
}

/* form */
.has-error .input-group .input-group-addon {
  background: #e74c3c;
  color: #fff;
}

.has-error .form-error-msg {
  color: #e74c3c;
}
.has-error .form-error-msg a {
  color: #2980b9;
}

.has-error .tagit.ui-widget {
  border: 2px solid #e74c3c;
  color: #e74c3c;
}

.form-group label {
  font-weight: bold;
  margin-bottom: 0;
}

/* Headers */
h1 {
  font-family: "Times New Roman", serif;
  font-size: 2.7rem;
}

h2 {
  font-family: "Times New Roman", serif;
  font-size: 2.0rem;
}

h6 {
  font-family: "Times New Roman", serif;
  font-size: 1.75rem;
}

h5 {
  font-family: "Times New Roman", serif;
  font-size: 1.5rem;
  font-weight: normal;
}

h3 {
  font-family: "Times New Roman", serif;
  font-size: 1.2rem;
  margin: 0;
  font-weight: normal;
}
h3 > a {
  color: #222222;
}
h3 > a:active, h3 a:hover {
  outline: 0 none;
  color: #428BCA;
}

/* Section styling */
.section-nav {
  background: #f8f8f8;
}
.section-nav p {
  margin: 15px 0;
}

.section-header {
  background: #ddd;
  padding-top: 30px;
  padding-bottom: 30px;
  font-size: 24px;
}

.section-body {
  margin-top: 30px;
}

/* Container centering */
.container-height {
  display: table;
  padding-left: 0;
  padding-right: 0;
}

.row-height {
  display: table-row;
}

.col-height {
  display: table-cell;
  float: none;
}

.col-middle {
  vertical-align: middle;
}

/* loading & noresults */
.loading-placeholder {
  text-align: center;
  margin-top: 40px;
  font-size: 28px;
}

.loading {
  text-align: center;
}
.loading span {
  font-size: 32px;
}

/*
.richtext span {
  margin-bottom: 4px;
}*/

.noresults {
  text-align: center;
  margin-top: 30px;
}

/* user */
.user-level {
  font-size: 10px;
}
.user-level i {
  color: #999;
}
.user-level strong {
  color: #e1e1e1;
}

/* vote */
.vote .vote-sum {
  position: relative;
  top: -5px;
  padding: 5px 5px 5px 2px;
}

.vote a.active {
  color: #2980b9;
}

/* media image */
.media-image-wrap {
  width: 125px;
  height: 125px;
}
.media-image-wrap img {
  width: 100%;
  height: 100%;
}

.media-image-link {
  display: inline-block;
  width: 80px;
  height: 80px;
  background-position: center center;
  background-size: cover;
}

/* discussion */
.discussion-content {
  margin-bottom: 4px;
  border-bottom: 2px #ecf0f1 solid;
}

.discussion-suggestions {
  display: none;
  padding-bottom: 30px;
  padding-left: 110px;
  margin-top: -10px;
  margin-bottom: 30px;
  border-bottom: 2px #ecf0f1 solid;
}
.discussion-suggestions .suggestion-header {
  margin-bottom: 10px;
  padding-left: 100px;
  color: #777;
}
.discussion-suggestions .suggestion {
  width: 28%;
}
.discussion-suggestions img {
  width: 125px;
  height: 125px;
}
.discussion-suggestions .suggestion-title {
  margin: 10px auto 0 auto;
  overflow: hidden;
  font-size: 18px;
  line-height: 24px;
}
.discussion-suggestions table {
  max-width: 500px;
  margin: 0 auto;
}
.discussion-suggestions table td {
  vertical-align: top;
  font-size: 18px;
  line-height: 18px;
}
.discussion-suggestions table td img {
  width: 80px;
  height: 80px;
  margin-right: 10px;
}

.discussion-comments {
  margin-bottom: 4px;
}
.discussion-comments p {
  margin-bottom: 4px;
  font-size: 22px;
  line-height: 28px;
}

@media (max-width: 1199px) {
  .discussion-suggestions .suggestion-header {
    padding-left: 65px;
  }
}
@media (max-width: 991px) {
  .discussion-suggestions {
    padding-left: 0;
  }

  .discussion-suggestions .suggestion-header {
    padding-left: 0;
  }

  .discussion-suggestions .suggestion {
    width: 33.33333333%;
  }
}
@media (max-width: 767px) {
  .discussion-suggestions .suggestion {
    width: 100%;
    margin-top: 20px;
  }
}
.shareme-btn {
  width: 100%;
  margin-bottom: 20px;
}
.shareme-btn.facebook {
  background: #3b5999;
}
.shareme-btn.twitter {
  background: #55acee;
}
.shareme-btn i {
  padding-right: 5px;
}

.discussion-invite .suggested-invites {
  display: none;
  margin-top: 10px;
}
.discussion-invite .suggested-invites a {
  padding-left: 5px;
  color: #2980b9;
}

/* feed */
body.auth .feed {
  margin-top: 20px;
}

.feed .hero {
  background: #dedede;
  border-bottom: 2px solid #ecf0f1;
}
.feed .hero .subtitle {
  font-size: 22px;
}
.feed .hero .feed-signup-wrap {
  margin: 20px 0 40px 0;
}
.feed .hero .feed-signup-wrap button {
  background: #222;
  font-size: 22px;
}

.feed-sort-option {
  padding: 20px 0 0 0;
}
.feed-sort-option .sep {
  padding: 0 5px;
}
.feed-sort-option a.active {
  font-weight: bold;
}

@media (max-width: 767px) {
  .feed .hero h1 {
    font-size: 3.7rem;
  }
}
/* account */
.account {
  margin-bottom: 40px;
}
.account .account-section {
  background: #f1f1f1;
  padding: 20px;
  margin-top: 20px;
  border-radius: 5px;
}
.account .sub {
  color: #777;
  font-size: 18px;
  font-weight: normal;
}
.account .info {
  line-height: 38px;
}
.account .info .edit {
  font-size: 12px;
  color: #2980b9;
}
.account ul {
  padding-left: 0;
  list-style-type: none;
}

/* search */
.search-results {
  margin-top: 20px;
}
.search-results .search-result {
  padding: 20px 0;
}
.search-results .search-result .title {
  margin-top: 0;
}
.search-results .search-result .contextual {
  color: #7f8c8d;
}

/* faq */
.faq .browser-img {
  width: 200px;
  margin-right: 10px;
  margin-bottom: 10px;
}

.faq p a {
  color: #2980b9;
}

.faq table {
  border-collapse: collapse;
  border: 1px #f1f1f1 solid;
}
.faq table th {
  background: #e2e2e2;
}
.faq table th, .faq table td {
  padding: 5px 10px;
  border: 1px #f1f1f1 solid;
}
.faq table tbody tr td:first-child {
  text-align: center;
}

.faq-header {
  padding-bottom: 30px;
  margin-bottom: 30px;
  border-bottom: 2px #f1f1f1 solid;
}

.faq-nav {
  margin-bottom: 10px;
}
.faq-nav .faq-nav-header {
  font-weight: bold;
}
.faq-nav .faq-nav-item {
  margin-left: 30px;
}

/* formatting tips */
.formatting-tips {
  display: none;
  position: relative;
}
.formatting-tips a.tip-toggle {
  position: absolute;
  right: 25px;
  top: 5px;
  color: #999;
}
.formatting-tips a.tip-future {
  position: absolute;
  right: 5px;
  top: 5px;
}
.formatting-tips a.tip-future img {
  height: 15px;
  width: 15px;
}
.formatting-tips table {
  display: none;
  padding: 0;
  margin-bottom: 10px;
}
.formatting-tips table th {
  padding: 5px 50px 5px 5px;
}
.formatting-tips table td {
  border: 1px #eeeeee solid;
  padding: 5px;
}
.formatting-tips blockquote p {
  font-size: 16px;
}

@media (max-width: 767px) {
  .formatting-tips a.tip-toggle {
    position: static;
    display: block;
  }
  .formatting-tips table th, .formatting-tips table td {
    padding-right: 20px;
  }
}
.social-more .social-more-box {
  padding: 20px;
  background: #e2e2e2;
  font-weight: bold;
  font-size: 20px;
  height: 150px;
  margin-bottom: 10px;
}
.social-more iframe {
  -ms-zoom: 1.5;
  -ms-transform-origin: 0.0;
  -moz-transform: scale(1.5);
  -moz-transform-origin: 0 0;
  -o-transform: scale(0.5);
  -o-transform-origin: 0px 50px;
  -webkit-transform: scale(1.5);
  -webkit-transform-origin: 0 0;
}

.read-more a,
.read-less a {
  text-decoration: underline;
}

.live-xp {
  font-size: 8px;
  position: fixed;
  bottom: 0;
  right: 20px;
  display: none;
}
.live-xp .points {
  color: #fff;
  font-size: 14px;
}
.live-xp .topic {
  max-width: 100px;
  font-size: 14px;
}

#levelup_modal .fa-3x {
  font-size: 28px;
}
#levelup_modal .level {
  font-size: 20px;
  color: #ffffff;
}
#levelup_modal p {
  margin-top: 20px;
  font-size: 22px;
}

.discover .sort-option {
  margin-bottom: 40px;
}
.discover .sort-option a.active {
  font-weight: bold;
}

.discover-topic {
  padding: 0 10px;
}
.discover-topic .topic-wrap {
  position: relative;
  height: 200px;
}
.discover-topic .topic-wrap .topic-avatar {
  position: absolute;
  top: 0;
  left: 0;
}
.discover-topic .topic-wrap .topic-avatar img {
  width: 105px;
  height: 105px;
}
.discover-topic .topic-wrap .topic-info {
  min-height: 105px;
  padding-left: 115px;
}
.discover-topic .topic-wrap .topic-info span {
  font-size: 20px;
}
.discover-topic .topic-wrap .topic-info p {
  color: #999;
  font-size: 14px;
  margin-bottom: 5px;
}

@media (max-width: 767px) {
  .discover-topic .topic-wrap {
    height: inherit;
    margin-bottom: 40px;
  }
}
header button i {
  margin-right: 0;
}
header .nav-wrap .logo {
  color: #222222;
}
header .nav-wrap .logo:hover {
  color: #2980b9;
}
header .nav-wrap .logo.for-xs {
  padding-left: 10px;
  padding-right: 0;
}
header .nav-wrap .logo.for-xs h5 {
  margin: 8px 0;
}
header .nav-wrap .header-notifications {
  padding-left: 0;
}
header .signup-bar {
  background: #bbb;
  font-size: 20px;
  padding: 10px 0;
  border-bottom: 1px #cccccc solid;
}
header .signup-bar button {
  font-size: 18px;
  margin-left: 20px;
}
header .signup-bar .for-xs table {
  width: 100%;
}
header .signup-bar .for-xs table td:last-child {
  text-align: right;
}
header .signup-bar .for-xs button {
  margin-left: 0;
}
header .payout-rate-bar {
  background: #222;
  color: #f8f8f8;
  text-align: center;
}
header .payout-rate-inline {
  position: absolute;
  top: 15px;
  right: 100px;
  color: #999;
}

header button i {
  margin-right: 0;
}

header .nav-wrap .logo {
  color: #222222;
}
header .nav-wrap .logo:hover {
  color: #2980b9;
}
header .nav-wrap .logo.for-xs {
  padding-left: 10px;
  padding-right: 0;
}
header .nav-wrap .logo.for-xs h5 {
  margin: 8px 0;
}
header .nav-wrap .header-notifications {
  padding-left: 0;
}

.navbar-default .navbar-nav > li > a {
  color: #222;
  font-size: 18px;
}
.navbar-default .navbar-nav > li > a:hover {
  color: #2980b9;
}

.navbar-default .navbar-nav > .dropdown > a .caret {
  border-top-color: #222;
  border-bottom-color: #222;
}

.navbar-default .navbar-nav > .dropdown > a:hover .caret {
  border-top-color: #2980b9;
  border-bottom-color: #2980b9;
}

@media (max-width: 767px) {
  .navbar-default .navbar-nav {
    margin: 0 -10px;
  }

  .nav > li {
    float: left;
  }
}
.btn-offcanvas {
  margin-top: 5px;
  background: transparent;
}
.btn-offcanvas i {
  color: #222222;
}

.dropdown-scrollable {
  overflow: hidden;
  overflow-y: auto;
}

li.dropdown {
  position: relative;
}
li.dropdown a.new {
  color: #e74c3c;
}
li.dropdown a .new-indicator {
  position: absolute;
  color: #e74c3c;
  font-size: 12px;
  top: 20px;
  left: 32px;
  display: none;
}

.topics-menu li.menu-group {
  padding: 5px 10px;
  background: #e1e4e7;
  font-weight: bold;
  display: none;
  border-bottom: 2px solid #dddddd;
}
.topics-menu li a {
  padding: 10px 20px;
}
.topics-menu li a img {
  width: 25px;
  height: 25px;
}
.topics-menu li a span {
  padding-left: 5px;
}

.dropdown-menu.notif {
  width: 300px;
}
.dropdown-menu.notif ul {
  padding: 0;
}

@media (max-width: 767px) {
  .dropdown-menu.notif {
    width: 250px;
  }
}
.notifications-menu li > a {
  display: block;
  clear: both;
  font-weight: normal;
  line-height: 1.428571429;
  color: #333333;
}

li.notification.unread {
  background: #e1e4e7;
}

li.notification a {
  position: relative;
  padding: 10px 20px 10px 30px;
  border-bottom: 1px solid #dddddd;
}
li.notification a .notification-icon {
  position: absolute;
  top: 10px;
  left: 10px;
}
li.notification a .since-dt {
  color: #AAAAAA;
}

ul.notification-dropdowns {
  margin-top: 15px;
  list-style-image: none;
  list-style-position: outside;
  list-style-type: none;
  margin: 0 -15px 0 0;
  padding-left: 0;
  float: right!important;
}
ul.notification-dropdowns > li {
  display: block;
  float: left;
}
ul.notification-dropdowns > li:first-child {
  margin-right: -20px;
}
ul.notification-dropdowns > li > a {
  color: #222;
  font-size: 18px;
  padding: 15px 21px;
  line-height: 23px;
  font-weight: 700;
  position: relative;
  display: block;
}
ul.notification-dropdowns > li > a:hover {
  color: #2980b9;
}
ul.notification-dropdowns > li > .dropdown-menu {
  min-width: 100%;
  border-radius: 4px;
  right: 0;
  left: auto;
}

#create_message {
  margin-right: 50px;
}

.message-create-user {
  font-size: 22px;
}
.message-create-user i {
  margin-right: 10px;
}

.no-messages,
.no-notifications {
  margin: 20px 0;
}

.dropdown-menu .dropdown-footer {
  text-align: center;
  padding: 5px;
  border-top: 2px solid #dddddd;
  background: #e1e4e7;
}

.offcanvas {
  height: 100%;
  background-color: #222222;
  font-size: 18px;
}
.offcanvas .list-group-item {
  padding-left: 20px;
  background-color: #222222;
  color: #FFFFFF;
  border-radius: 0;
  border: none;
}
.offcanvas .list-group-item i {
  width: 30px;
  text-align: left;
}
.offcanvas .list-group-item:hover {
  color: #222222;
}

#search_form {
  position: relative;
}
#search_form .search-icon-wrap {
  position: absolute;
  top: 6px;
  left: 20px;
}
#search_form .search-icon-wrap i {
  color: #fff;
}
#search_form input {
  border: 0;
  border-radius: 0;
  padding-left: 50px;
  background: #666666;
  color: #F1F1F1;
  font-size: 18px;
}

.xp-user {
  background: #444;
  padding: 5px 20px 15px 20px;
  color: #f1f1f1;
  font-size: 18px;
  border-bottom: 2px #4e4e4e solid;
}
.xp-user .xp-user-profile {
  font-size: 20px;
  color: #f1f1f1;
}
.xp-user .xp-user-profile .xp-user-avatar {
  width: 39px;
  height: 39px;
  margin-right: 5px;
}
.xp-user .xp-user-info {
  margin-top: 5px;
  font-size: 14px;
}
.xp-user .xp-user-info a {
  color: #bbb;
}
.xp-user .xp-user-info .xp-user-display-wrap {
  width: 220px;
  background: #23262E;
  padding: 5px;
}
.xp-user .xp-user-info .xp-user-display-wrap .xp-user-display {
  background: #2980b9;
  padding: 3px 0;
  width: 0px;
}
.xp-user .xp-user-info .xp-user-percent {
  position: absolute;
  top: 68px;
  right: 20px;
}

@media (max-width: 767px) {
  .navmenu,
  .navbar-offcanvas {
    width: 250px;
  }

  .xp-user .xp-user-profile {
    font-size: 16px;
  }
  .xp-user .xp-user-profile .xp-user-avatar {
    width: 25px;
    height: 25px;
  }

  .xp-user .xp-user-info .xp-user-display-wrap {
    width: 150px;
  }
}
#sidebarTopics {
  display: none;
}
#sidebarTopics ul.topics-menu {
  background: #999;
  list-style-type: none;
  padding: 0;
}
#sidebarTopics ul.topics-menu li {
  padding: 10px 0;
}
#sidebarTopics ul.topics-menu li.menu-group {
  padding: 5px 10px;
  background: #777;
  font-weight: bold;
  display: none;
  border-bottom: 2px solid #888888;
}

.modal .checkbox {
  font-size: 16px;
}
.modal .checkbox.checked {
  color: #222222;
}
.modal .checkbox.checked .second-icon {
  color: #2980b9;
}

.modal .form-actions {
  text-align: right;
  margin-top: 20px;
}
.modal .form-actions button {
  margin-left: 10px;
}

.post {
  position: relative;
  padding: 6px 0;
}
.post .comments-count-wrap-inline .fa-bookmark {
  font-size: 22px;
}
.post .comments-count-wrap .fa-bookmark {
  margin-top: -15px;
  font-size: 75px;
}
.post .expert-robot {
  width: 15px;
  height: 15px;
}
.post .stickie {
  background: #FAFAD2;
  border-radius: 5px;
}
.post .stickie-hide-left {
  position: absolute;
  background: #f8f8f8;
  height: 100%;
  top: 0;
  left: -15px;
  width: 60px;
}
.post .stickie-hide-right {
  position: absolute;
  background: #f8f8f8;
  height: 100%;
  top: 0;
  right: -15px;
  width: 60px;
}
.post .stickie-dtf {
  position: absolute;
  bottom: 25px;
  right: 60px;
  color: #999999;
}
.post .stickie-dtf img {
  width: 15px;
  height: 15px;
}
.post .for-xs table tbody tr td {
  vertical-align: top;
}
.post .for-xs table tbody tr td:last-child {
  padding-left: 10px;
}
.post .for-xs table tbody tr td img {
  height: 80px;
  width: 80px;
}
.post .for-xs table tbody tr td h3 {
  font-size: 1.5rem;
}
.post .for-xs table tbody tr td p {
  margin-top: 10px;
  line-height: 1.1rem;
}
.post .comments-count-wrap-inline {
  border-left: 2px #999999 solid;
  padding-left: 20px;
  font-size: 20px;
  display: none;
}
.post .comments-count-wrap {
  padding-top: 8px;
  padding-left: 80px;
}
.post .comments-count-wrap .comments-count-label {
  margin-bottom: 0;
}
.post .votes {
  padding-right: 20px;
  font-size: 14px;
}
.post .post-info {
  padding: 4px 30px;
  border-left: 2px solid #ecf0f1;
}
.post .post-info .contextualinfo {
  color: #7f8c8d;
  margin: -4px 0 2px;
}
.post .actions {
  margin: 0;
}
.post .actions .actions-row a {
  padding-right: 10px;
}
.post .actions .actions-row .contextual {
  padding-left: 5px;
}
.post .actions .actions-row .contextual a {
  color: #999;
