css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("userstyles.org") {
	html {
		overflow-x: hidden !important;
		position: relative !important;
		min-height: 100% !important;
	}
	body {
		margin: 0 0 50px !important;
		background-color: #222 !important;
		color: #aaa !important;
		line-height: 1.5em !important;
		padding-bottom: 10px !important;
		font-family: Verdana, sans-serif !important;
		font-size: smaller !important;
		margin-left: auto !important;
		margin-right: auto !important;
	}
	
	pre, input, textarea, code {
		background-color: #222 !important;
		color: #aaa !important;
	}
	
	a.Button:hover, a.Button:active, a.NavButton:hover, a.NavButton:active, input.Button:hover, inpute.Button:active, a.btn-password:hover, a.btn-password:active {
		background: #333 none repeat scroll 0% 0% !important;
	}
	
	a.Button, a.NavButton, input.Button, a.btn-password {
    
    line-height: 1em !important;
    padding: 6px !important;
    background: #222 !important;
    border: 1px solid #555 !important;
    text-shadow: none !important;
    box-shadow: none !important;
    color: #aaa !important;
	}
	
	input#FormGo.Button {
		background: #404040 url("https://forum.userstyles.org/application/dashboard/design/images/sprites.png") no-repeat scroll 0px -196px !important;
		width: 16px !important;
		height: 16px !important;
	}
	
	.FileHover, .ProfileOptions.NavButton {
		background-color: #404040 !important;
	}
	
	iframe, .ad {
		display: none !important;    
	}
	
	#main-header, #Head {
		background-color: #404040 !important;
		position: absolute !important;
		top: 0 !important;
		right: 50px !important;
		left: 50px !important;
		height: 50px !important;
		padding: 5px !important;
		text-align: center !important;
		border-radius: 0 0 5px 5px !important;
	}
	
	#Head {
		margin-left: 5px !important;
	}
	
	#main-header>h1 {
		margin: 10px 0 !important;
		text-align: center !important;
	}
	
	.Row {
		margin-top: 10px !important;
	}
	
	.ItemDiscussion:nth-child(odd) {
		background-color: #333 !important;
	}
	
	.ItemDiscussion:nth-child(even) {
		background-color: #303030 !important;
	}
	
	.Discussions li:first-child {
	  border-top-right-radius: 5px !important;
	  border-top-left-radius: 5px !important;
	}
	
	.Discussions li:last-child {
	  border-bottom-right-radius: 5px !important;
	  border-bottom-left-radius: 5px !important;
	}
	
	.ItemComment:nth-child(even) {
		background-color: #333 !important;
	}
	
	a, .TextColor {
		color: #ccc !important;
	}
	
	a:hover, .TextColor:hover {
		color: #aaa !important;
	}
	
	a:visited, .TextColor:visited {
		color: #aaa !important;
	}
	
	a:visited:hover, .TextColor:visited:hover {
		color: #ccc !important;
	}
	
	#left-sidebar, #Panel {
		float: none !important;
		position: absolute !important;
		background-color: #404040 !important;
		color: #aaa !important;
		top: 70px !important;
		right: 250px !important;
		bottom: 70px !important;
		left: 50px !important;
		margin-left: 5px !important;
		padding: 5px !important;
		border-radius: 5px !important;
	}
	
	#left-sidebar>* {
		background-color: transparent !important;
		margin: 0 !important;
		padding: 0 !important;
		border-radius: 0 !important;
	}
	
	#content-wrapper, #Content {
		float: left !important;
		position: absolute !important;
		width: auto !important;
		top: 70px !important;
		right: 50px !important;
		bottom: 70px !important;
		left: 255px !important;
		background-color: #222 !important;
		color: #aaa !important;
		margin: 0 !important;
		padding: 5px !important;
		border-radius: 5px !important;
		overflow: auto !important;
	}
	
	#Content {
		margin-left: 20px !important;
	}
	
	#errorExplanation {
		background-color: #800000 !important;
		border-radius: 5px !important;
	}
	
	#main-article {
		width: 80% !important;
		margin: 0 10% 0 10% !important;
	}
	
	body>footer, #Foot {
		background-color: #404040 !important;
		position: absolute !important;
		right: 50px !important;
		bottom: 0 !important;
		left: 55px !important;
		height: 50px !important;
		padding: 5px !important;
		text-align: center !important;
		border-radius: 5px 5px 0 0 !important;
	}
	
	#front-page-best ul, #front-page-newest ul {
		list-style-type: none !important;
	}
	
	#front-page-best {
		float: left !important;
		margin-left: 10% !important;
		text-align: left !important;
	}
	
	#front-page-best ul li {
		margin-left: -40px !important;
	}
	
	#front-page-newest {
		float: right !important;
		margin-right: 10% !important;
		text-align: left !important;
	}
	
	#front-page-newest ul li {
		margin-left: -40px !important;
	}
	.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active {
    color: #aaa !important;
	}
	.MenuItems a:hover {
		background-color: #aaa !important;
    color: #111 !important;
	}
	.Flyout {
		background-color: #111 !important;
		color: #aaa !important;
	}
	.Flyout::before, .Flyout::after {
    border-bottom: 7px solid #aaa !important;
	}
	.Flyout::after {
    border-bottom: 7px solid #111 !important;
	}
}
