css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
#yt-masthead-container {
    background: none !important;
    border-bottom: 0 !important;
}
#masthead-positioner-height-offset {
    height: 0 !important;
}
#body-container:hover #watch7-sidebar-contents {
    display: block !important;
}
.watch-title {
    font-size: 50%;
}
.comments .comment-item {
    margin-left: 0 !important;
}
.comments .comment-item.reply {
    padding: 12px 0 0 12px !important;
}
#yt-comments-sb-container,
.search-btn-component,
#masthead-search-terms,
#yt-masthead-user,
.user-name,
.g-hovercard,
.spacer,
.comment-footer-actions,
.sprite_dropdown,
.comment-header,
#watch7-sidebar-contents {
    display: none !important;
}
.comment-item .content {
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    -moz-border-right-colors: none;
    -moz-border-top-colors: none;
    background-color: #FFFFCC;
    border-color: -moz-use-text-color #b7c5d9 #b7c5d9 -moz-use-text-color;
    border-image: none;
    border-style: none solid solid none;
    border-width: medium 1px 1px medium;
    padding: 2px;
    border-radius: 6px;
    padding: 11px !important;
    -webkit-box-shadow: 2px 2px 3px 1px rgba(136, 136, 136, 1);
    -moz-box-shadow: 2px 2px 3px 1px rgba(136, 136, 136, 1);
    box-shadow: 2px 2px 3px 1px rgba(136, 136, 136, 1);
    margin-bottom: 8px !important;
    margin-top: 8px !important;
}
.comment-entry {
    padding: 0 !important;
}
.comments .comment-item.reply {
    padding: 0 !important;
}
#watch-discussion {
    width: 88%;
}
.comment-item.reply .content {
    background-color: #d6daf0;
}
#yt-comments-sb-container.constrain {
    width: auto;
}
.comment-replies-header {
    font-size: 0;
    margin: 0 !important;
    line-height: 0 !important;
}
.sprite_caret down {
    height: 0;
}
}
