css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forbiddense.com") {

  
  #content .pageContent {
    background-color: rgb(34, 34, 34);
    border-left: 2px solid rgb(86, 86, 86);
    border-right: 2px solid rgb(86, 86, 86);
    padding: 10px 20px;
}
  .username .style16 {
    color: #ffffff;
    font-weight: bold;
}
  .username .style2 {
    color: #ffffff;
    font-style: italic;
    font-weight: normal;
}
  .username .style15 {
    color: #ffffff;
    font-weight: bold;
}
.secondaryContent a {
    color: rgb(255, 255, 255);
}
  body {
    background-image: url("https://forbiddense.com/background.png");
    color: rgb(255, 255, 255);
    font-family: "Open Sans",Helvetica,Arial,sans-serif;
    font-size: 13px;
    line-height: 1.28;
    word-wrap: break-word;
}
  .sectionMain {
    background-color: rgb(34, 34, 34);
    border: 1px solid rgb(86, 86, 86);
    border-radius: 0;
    margin: 10px auto;
    padding: 10px;
}
  .nodeList .node.level_1 {
    background-color: rgb(34, 34, 34);
    border: 1px solid #d0d0d0;
    padding: 6px 6px 0;
}
.navTabs {
    background-color: rgb(67, 66, 66);
}
  html .navTabs .navTab.selected .navLink {
    background-color: rgb(67, 66, 66);
    color: rgb(243, 243, 243) !important;
    font-weight: bold;
    position: relative;
}
  .sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading {
    color: #ffffff;
}
  .Menu .primaryContent {
    background-color: rgba(251, 251, 251, 0.96);
}
  .nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    background-color: rgb(34, 34, 34);
    border-bottom: 1px solid rgb(230, 230, 230);
    padding: 0;
}
  .primaryContent a {
    color: rgb(118, 118, 118);
  }
  
  .nodeList li.level_2:nth-child(2n+1) .primaryContent:hover, .nodeList li.level_2:nth-child(2n+1) .linkNodeInfo:hover, .nodeList li.level_2:nth-child(2n+1) .pageNodeInfo:hover {
    background-color: rgb(56, 56, 56);
}
  .nodeList li.level_2:nth-child(2n) .primaryContent:hover, .nodeList li.level_2:nth-child(2n) .linkNodeInfo:hover, .nodeList li.level_2:nth-child(2n) .pageNodeInfo:hover {
    background-color: rgb(56, 56, 56);
}
  #brmsNav .brmsTabContent {
    background-color: rgb(34, 34, 34);
    border: 1px solid #ddd;
    box-shadow: 0 0 35px 5px #fafbfd none;
  }
  #brmsNav .brmsTabContent {
    box-shadow: 0 0 0px -1px #fafbfd inset;
}
  a:link, a:visited {
    color: rgb(118, 118, 118);
    text-decoration: none;
}
  .Messagetext {
    color: #ffffff;
}
.Messagescounter {
    color: #ffffff;
}
.likestext {
    color: #ffffff;
}
.likescounter {
    color: #ffffff;
}
.pointstext {
    color: #ffffff;
}
.pointscounter {
    color: #ffffff;
}
  .nodeLastPost a.avatar img {
    border: 0 solid #c4c4c4 !important;
    border-radius: 3px !important;
    float: left;
    height: 23px;
    margin-left: -7px;
    margin-right: 4px;
    padding: 1px;
    width: 23px;
}
  .avatar img, .avatar .img, .avatarCropper {
    background-color: rgb(252, 252, 255);
    border: 1px solid rgb(230, 230, 230);
    border-radius: 0;
    padding: 0;
}
  #taigachat_box {
    background: rgb(56, 56, 56) none repeat scroll 0 0;
    border: 1px solid rgb(197, 197, 197);
    font-size: 11px;
    height: 300px;
    margin: 5px -2px 0;
    overflow-x: auto;
    overflow-y: scroll;
    padding: 5px;
}
  body .muted, body a.muted, body .muted a {
    color: rgb(118, 118, 118);
}
  #header {
    background-image: url("http://i.imgur.com/lm7wSw2.png");
    background-repeat: repeat;
}
 img[src*="logo"]{

    width:0;

    height:100px;

    padding-right:385px;

    background:transparent url(http://i.imgur.com/JiDnmDK.png) top left no-repeat;
  }
 .breadcrumb {
    background-color: rgb(56, 56, 56);
    border: 1px solid rgb(230, 230, 230);
    font-size: 13px;
    height: 37px;
    max-width: 100%;
    overflow: hidden;
}
  .XenBase .navTabs .navTab.selected .tabLinks {
    background-color: rgb(32, 32, 32);
    margin-top: 3px;
    text-transform: uppercase;
}
  .navTabs .navLink, .navTabs .SplitCtrl {
    color: rgb(255, 255, 255);
    display: block;
    float: left;
    height: 50px;
    line-height: 50px;
    outline: 0 none;
    text-align: center;
    vertical-align: text-bottom;
}
  html .navTabs .navTab.selected .navLink {
    background-color: rgb(32, 32, 32);
    color: rgb(57, 175, 36) !important;
    font-weight: bold;
    position: relative;
}
  .breadcrumb .crust a.crumb {
    background-color: rgb(56, 56, 56);
    border-bottom: 1px solid rgb(230, 230, 230);
    color: rgb(255, 255, 255);
    cursor: pointer;
    display: block;
    line-height: 37px;
    margin-bottom: -1px;
    outline: 0 none;
    padding: 0 10px 0 20px;
    text-decoration: none;
}
  .formPopup .controlsWrapper {
    background: rgb(56, 56, 56) repeat-x scroll center top;
    border-radius: 5px;
    font-size: 11px;
    margin: 5px 0;
    padding: 5px;
}
}
