@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");




/*  FAVICON  for places open in a tab*/
.tabbrowser-tab[label="Biblioteca"] .tab-icon-image {
  width: 16px !important;
  height: 16px !important;
  list-style-image: url("chrome://browser/skin/places/editBookmark.png");
  -moz-image-region: rect(0px 16px 16px 0px) !important;
}/*  --  */




@-moz-document
    url(chrome://browser/content/bookmarks/bookmarksPanel.xul),
    url(chrome://browser/content/history/history-panel.xul),
    url(chrome://browser/content/places/places.xul),
    url(chrome://browser/content/places/bookmarkProperties.xul) {

page,
toolbar, #detailsDeck,
#bookmarkproperties {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(200,200,200);
    border: 0px solid black !important;
}
}


@-moz-document
    url(chrome://browser/content/bookmarks/bookmarksPanel.xul),
    url(chrome://browser/content/history/history-panel.xul),
    url(chrome://browser/content/places/places.xul),
    url(chrome://browser/content/places/bookmarkProperties.xul),
    url(chrome://browser/content/browser.xul) {

/*  INPUT TEXT  SINGLE LINE  */
#bookmarksPanel textbox:not([multiline="true"]),
#history-panel textbox:not([multiline="true"]),
#places textbox:not([multiline="true"]),
#editBookmarkPanelContent textbox:not([multiline="true"]) {
    -moz-appearance: none !important;
    border-radius: 0px !important;
    padding: 3px !important;
    min-height: 22px !important;
    max-height: 22px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(200,200,200) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image: linear-gradient(
                                  rgba(66,73,79,1),
                                  rgba(66,73,79,0)
                                  ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset;
    transition: all 700ms ease 0s !important;
}
#bookmarksPanel textbox:hover:not([multiline="true"]),
#history-panel textbox:hover:not([multiline="true"]),
#places textbox:hover:not([multiline="true"]),
#editBookmarkPanelContent textbox:hover:not([multiline="true"]) {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: border 500ms ease 0s !important;
}
#bookmarksPanel textbox[focused="true"]:not([multiline="true"]),
#history-panel textbox[focused="true"]:not([multiline="true"]),
#places textbox[focused="true"]:not([multiline="true"]),
#editBookmarkPanelContent textbox[focused="true"]:not([multiline="true"]) {
    border-color: rgb(51,159,255) !important;
    color: rgb(255,252,255) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset;
    transition: all 500ms ease 0s !important;
}/*  --  */

/*  SEARCHBAR  ICONS  */
.textbox-search-icon { list-style-image: url(
                data:image/png;base64,
                ) !important;
    -moz-image-region: rect(0, 0px, 0px, 0) !important;}
.textbox-search-clear,
.textbox-search-clear:hover
.textbox-search-clear:active { list-style-image: url(
                chrome://mozapps/skin/plugins/contentPluginClose.png
                ) !important;
    width: 16px !important;
    height: 16px !important;
    margin: 0px 0px 0px 0px !important;
    -moz-image-region: rect(0px,16px,16px,0px) !important;}
.textbox-search-clear:hover {
    -moz-image-region: rect(0px,32px,16px,16px) !important;}
.textbox-search-clear:active {
    -moz-image-region: rect(0px,48px,16px,32px) !important;}
#bookmarksPanel #search-box ,
#history-panel  #search-box ,
#places #searchFilter {
    padding: 0px 2px 0px 3px !important;}
/*  --  */





/*  TREE  LISTBOX  */
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) tree {
    -moz-appearance: none !important;
    border: 1px solid rgb(0,0,0) !important;
    color: rgb(200,200,200) !important;
    background-color: rgb(20,23,26) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-row {
    background-color: transparent !important;
    overflow-y: scroll  !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-row(selected) {
    background-color: rgba(51,159,253,0.15) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-cell-text(selected) {
    color: rgb(51,159,253) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-row(current, focus) {
    border: 1px solid rgb(2,83,155) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-row(hover) {
    background-color: rgba(51,159,253,0.15) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-cell-text(hover) {
    text-decoration: none !important;
}

:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) tree[seltype="cell"] > treechildren::-moz-tree-line,
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) tree[seltype="text"] > treechildren::-moz-tree-line,
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-line {
    border: 1px dotted rgba(255,255,255,0.3) !important;
}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) tree[seltype="cell"] > treechildren::-moz-tree-line(active, selected, focus),
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-line(selected, focus) {
    border: 1px dotted rgba(0,255,255,1) !important;
}

:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-twisty {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAYklEQVQY033PywmAQAxF0RtwKQo2qFiZWy1JwRa0AIfnxoFhiHm7wMkPikiaJQ04aaq6A0ZJi5ldEQToganGHnTxH8w4n3FHEKAFBiCED7Ca2RmtzuiInknAViIPpm/SXne/bAklqD8BRaEAAAAASUVORK5CYII=
                ) !important;
    -moz-padding-end: 4px;
    padding-top: 1px;
    width: 9px;}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-twisty(open) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQY02P8//8/AzGAhRhFjCd+/WchRhFBE2GK8CpEVoRTIboirAqxKcJQiEsRikJ8ihgYGBgYiQ1wAJ56HC2vHF1bAAAAAElFTkSuQmCC
                ) !important;
    width: 9px;}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-checkbox(checked) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAQklEQVQI13WM0QnAUAwCL5CN3H+Dl5muX4FSXgVBPLRUbpoZ+w8A9MyYpL4AoLdIUm+QpPq22KdSrwAAFZVzjpvXD72LOZBnLCA/AAAAAElFTkSuQmCC
                ) !important;}
:-moz-any(#bookmarksPanel, #history-panel, #places, #editBookmarkPanelContent) treechildren::-moz-tree-checkbox(selected, checked) {list-style-image: url(
            data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAARUlEQVQI13WMwQnAMAwDz+CNmoG8WTOQd7q+DKWkAoHQIYXKSWtj/gGAXBu7iC8AyCm6iDfoIvK0mKdQjwAAFZXr1snjB+wLL0QDjQNrAAAAAElFTkSuQmCC
                ) !important;}
/*  --  */

/*  TREE SEPARATORS  */
:-moz-any(#bookmarksPanel, #places) treechildren::-moz-tree-separator {
    -moz-appearance: none !important;
    border-top: 1px solid rgb(0,0,0) !important;
    border-bottom: 1px solid rgb(48,60,70) !important;
}/*  --  */

/*  TREE DROP  */
:-moz-any(#bookmarksPanel, #places) treechildren::-moz-tree-row(dropOn) {
    background-color: rgba(51,159,253,0.15) !important;
}
:-moz-any(#bookmarksPanel, #places) tree[seltype="cell"] > treechildren::-moz-tree-cell-text(primary, dropOn),
:-moz-any(#bookmarksPanel, #places) tree[seltype="text"] > treechildren::-moz-tree-cell-text(primary, dropOn),
:-moz-any(#bookmarksPanel, #places) treechildren::-moz-tree-cell-text(primary, dropOn) {
    color: rgb(51,159,253) !important;
}
:-moz-any(#bookmarksPanel, #places) treechildren::-moz-tree-drop-feedback {
    background-color: rgb(51,159,253) !important;
    width: 50px;
    height: 2px;
  -moz-margin-start: 5px;
}/*  --  */




}





@-moz-document
    url(chrome://browser/content/places/places.xul),
    url(chrome://browser/content/places/bookmarkProperties.xul),
    url(chrome://browser/content/browser.xul) {


/*  TAGS BOX  */
:-moz-any(#places, #editBookmarkPanelContent) listbox {
    -moz-appearance: none !important;
    background-color: rgb(20,23,26) !important;
    color: rgb(220,220,220) !important;
    margin: 2px 4px;
    border: 1px solid rgba(255,255,255,0.3) !important;
}
:-moz-any(#places, #editBookmarkPanelContent) listbox:focus > listitem[selected="true"][current="true"] {
    outline: 0px !important;
}
:-moz-any(#places, #editBookmarkPanelContent) listbox:focus > listitem[current="true"] {
    outline: none !important;
}
:-moz-any(#places, #editBookmarkPanelContent) listitem[selected="true"] {
    background-color: rgb(50,50,50) !important;
    color: rgb(220,220,220) !important;
}
:-moz-any(#places, #editBookmarkPanelContent) listbox > listitem:hover,
:-moz-any(#places, #editBookmarkPanelContent) listbox:focus > listitem[selected="true"] ,
:-moz-any(#places, #editBookmarkPanelContent) listbox:focus > listitem[current="true"] {
    background-color: rgba(51,159,253,0.15) !important;
    color: rgb(51,159,253) !important;
}/*  --  */


/*  TAGS CHECKBOX  */
:-moz-any(#places, #editBookmarkPanelContent) .listcell-check {
    -moz-appearance: none !important;
    border: 1px solid !important;
    border-color: rgba(255,255,255,0.04) !important;
    border-bottom-color: rgba(255,255,255,0.08) !important;
    -moz-border-top-colors: transparent ;
    -moz-border-right-colors: transparent ;
    -moz-border-bottom-colors: transparent ;
    -moz-border-left-colors: transparent ;
    min-width: 14px;
    min-height: 14px;
    background: transparent no-repeat 50% 50%;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.0) !important;
    transition: border-color, box-shadow 500ms ease-out 0s !important;
}
:-moz-any(#places, #editBookmarkPanelContent) listitem:hover .listcell-check:not([checked="true"]),
:-moz-any(#places, #editBookmarkPanelContent) listbox:focus listitem[selected="true"] .listcell-check:not([checked="true"]) {
    border-color: rgba(128,255,255,0.4) !important;
    transition: border-color 500ms ease-out 0s !important;
}
:-moz-any(#places, #editBookmarkPanelContent) .listcell-check[checked="true"] {
    border-color: rgb(128,255,255) !important;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.45) !important;
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPUlEQVQI13WMQQrAQAwCR8i/03357ClQSioI4qBR2XTA+gMAdcCGfAFATdGQN2hIbYt5iroCAFRUHnXy+AKf3Cn4sMNREQAAAABJRU5ErkJggg==
                ) !important;
}/*  --  */



/*  INPUT TEXT  MULTILINE (Description Field)  */
:-moz-any(#places, #editBookmarkPanelContent) #editBMPanel_descriptionField {
    -moz-appearance: none !important;
    background-color: rgb(20,23,26) !important;
    color: rgb(220,220,220) !important;
    margin: 2px 4px;
    border: 1px solid rgba(255,255,255,0.3) !important;
    -moz-border-top-colors: transparent;
    -moz-border-right-colors: transparent;
    -moz-border-bottom-colors: transparent;
    -moz-border-left-colors: transparent;
    padding: 2px 2px 3px !important;
    -moz-padding-start: 4px !important;
    transition: all 500ms ease 0s !important;
}
:-moz-any(#places, #editBookmarkPanelContent) #editBMPanel_descriptionField:hover{
    border-color: rgba(255,255,255,0.5) !important;
}
:-moz-any(#places, #editBookmarkPanelContent) #editBMPanel_descriptionField[focused="true"] {
    border-color: rgb(51,159,255) !important;
}/*  --  */




/*  CHECKBOX (For open in sidebar)  */
:-moz-any(#places, #editBookmarkPanelContent) .checkbox-check {
    -moz-appearance: none !important;
    border: 1px solid !important;
    border-color: rgba(255,255,255,0.04) !important;
    border-bottom-color: rgba(255,255,255,0.08) !important;
    -moz-border-top-colors: transparent ;
    -moz-border-right-colors: transparent ;
    -moz-border-bottom-colors: transparent ;
    -moz-border-left-colors: transparent ;
    min-width: 14px;
    min-height: 14px;
    background: transparent no-repeat 50% 50%;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.0);
    transition: border-color, box-shadow 500ms ease-out 0s !important;
}
:-moz-any(#places, #editBookmarkPanelContent) checkbox:-moz-any(:hover, :-moz-focusring) > .checkbox-label-box {
    border: 1px solid transparent;
    color: rgb(128,255,255) !important;
}
:-moz-any(#places, #editBookmarkPanelContent) checkbox:-moz-any(:hover, :-moz-focusring) > .checkbox-check {
    border-color: rgba(128,255,255,0.4) !important;
    transition: border-color 500ms ease-out 0s !important;
}
:-moz-any(#places, #editBookmarkPanelContent) checkbox[checked="true"] > .checkbox-check {
    border-color: rgb(128,255,255) !important;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.45);
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPUlEQVQI13WMQQrAQAwCR8i/03357ClQSioI4qBR2XTA+gMAdcCGfAFATdGQN2hIbYt5iroCAFRUHnXy+AKf3Cn4sMNREQAAAABJRU5ErkJggg==
                ) !important;
}
#places #editBMPanel_loadInSidebarCheckbox {
    margin: 0px 0px -14px 0px !important;
}/*  --  */





/*  EXPANDER ARROW BUTTONS  */
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-up {list-style-image: url(
            data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAgklEQVQ4y+2ToQ2AUAxEH/57DIJNEEgEAyCqOkLHQzABKyAwKAweBBUkH8jHc6ppc9drLoUf2cssALnXC7B9EQ5Aa2ajmY1A6710sqquu0NV11SRiPxFJCKLyC4iySJ3tjugezgnQvOwKXIGNHcxlkDlhBnoL9EFoAYK7w3A9H/BiQNvt3cvCGMFFwAAAABJRU5ErkJggg==
            ) !important;}
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-down {list-style-image: url(
            data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAi0lEQVQ4y+2RIRKDQAxFH349pqI3qahE7AEQUTlCjlfBCbgCAoPC4BcTZoAutHi+yuTnv2QmcKtY1U/gBQSgBz7A5F4A3sDDew3Q7WFVcqnqCEQPBiCq6rj4QJW7ZjPkkBqoM/2YA3xtEpEkIungMv6CXAkfQq6ENxAza82sPQsXPyCl18Pqpbd2mgE1rHcvmHXRwAAAAABJRU5ErkJggg==
            ) !important;}
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-up:-moz-any(:hover, :focus) {list-style-image: url(
            data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAjElEQVQ4y+3TIQ6DUBAE0IfH11T0JhWViG9wCBISjsDxKnoCDAeoqKnCVENFv2jyoSmeUZvZzMxuNsuO7EcvxyHWT7y2GOcImq7XdD1C5DaIy3bMhmnOhmlWtuO/Jol4i0maHOpZqP82WRq7QrWyToJiJSmdjGLpjCeco+CB69fpclxwjNwN9/0LPngDxbNaxazfjQoAAAAASUVORK5CYII=
            ) !important;}
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-down:-moz-any(:hover, :focus) {list-style-image: url(
            data:image/png;base64,
            ) !important;}
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-up,
:-moz-any(#detailsDeck, #editBookmarkPanelContent) .expander-down {
  border: 0px solid transparent;
    padding: 0px 0px 0px 0px !important;
  -moz-padding-start: 0px !important;
  -moz-padding-end: 0px !important;
}/*  --  */



}





/*  HISTORY BUTTON BACKGROUND  */
@-moz-document
    url(chrome://browser/content/bookmarks/bookmarksPanel.xul),
    url(chrome://browser/content/history/history-panel.xul) {

#history-panel button, #bookmarks-dustbox
 {
    -moz-appearance: none !important;
    height: 22px !Important;
    min-width: 26px !Important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 5px !important;
    background-color: rgba(58,68,79,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
    color: rgb(215,241,255) !important;
    transition: background-color, box-shadow, color !important;
    transition-duration: 700ms !important;
    transition-timing-function: ease !important;
    transition-delay: 0s !important;
}
#history-panel :-moz-any(#viewButton:hover, #viewButton:focus),
#bookmarks-dustbox:hover
{
    background-color: rgba(48,58,69,1) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
	  color: rgb(152,217,252) !important;
    transition-duration: 250ms !important;
}
#history-panel :-moz-any(#viewButton:active, #viewButton[open="true"]),
#bookmarks-dustbox:active
{
    background-color: rgba(5,15,26,1) !important;
    background-image: 
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	  color: rgb(101,191,255) !important;
    transition-duration: 250ms !important;
}
#bookmarks-dustbox .toolbarbutton-text {
    padding: 0px 5px 0px 0px !important;
}
#history-panel dropmarker,
#history-panel :-moz-any(#viewButton:hover, #viewButton:focus, #viewButton:active, #viewButton[open="true"]) dropmarker {
    list-style-image: url(
                data:image/png;base64,
                ) !important;
    padding: 3px 2px 0px 2px !important;
    width: 11px !important;
    height: 11px !important;
    -moz-image-region: rect(0px,9px,7px,0px) !important;}
#history-panel :-moz-any(#viewButton:hover, #viewButton:focus) dropmarker {
    -moz-image-region: rect(0px,18px,7px,9px) !important;}
#history-panel :-moz-any(#viewButton:active, #viewButton[open="true"]) dropmarker {
    -moz-image-region: rect(0px,27px,7px,18px) !important;}
}/*  --  */







/*  SPLITTER FOR 2PANE BOOKMARKS ADDON  */
@-moz-document
    url(chrome://browser/content/bookmarks/bookmarksPanel.xul) {

#bookmarks-panes-splitter {
    border: 0px solid transparent;
    background-color: rgb(63,71,86) !important;
}
}/*  --  */







@-moz-document
    url(chrome://browser/content/places/places.xul) {


/*  BACK-FORWARD BUTTONS  */
#back-button, #forward-button {
    -moz-appearance: none !important;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 5px 0px 2px !important;
    border: 0px !important;
    list-style-image: url(
      data:image/png;base64,
  ) !important;}
#back-button {
    -moz-image-region: rect(0px,16px,16px,0px) !important;}
#forward-button {
    -moz-image-region: rect(0px,32px,16px,16px) !important;}
#back-button:hover {
    -moz-image-region: rect(0px,48px,16px,32px) !important;}
#forward-button:hover {
    -moz-image-region: rect(0px,64px,16px,48px) !important;}
#back-button[disabled="true"] {
