*/




/* AGENT_SHEET */


@-moz-document
    url(chrome://sessionmanager/content/session_prompt.xul) {

#sessionmanagerPrompt {
    -moz-appearance: none !important;
    background-color: rgb(45,53,63) !important;
    color: rgb(200,200,200) !important;
    border: 0px solid black !important;
}


/*  BUTTONS BACKGROUND  */
#sessionmanagerPrompt button 
{
    -moz-appearance: none !important;
    background-color: rgba(58,68,79,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(122,137,152,0.47),
                rgba(129,139,150,0.14)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    border-radius: 3px !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 1px 0px 1px rgba(140,158,176,0.40),
                   0px 0px 0px 1px rgba(66,75,86,0.6)
          !important;
    color: rgb(215,241,255) !important;
    padding: 2px 2px 1px 4px;
    text-shadow: 0px -1px 0px rgba(11, 12, 13, 0.45) !important;
    transition: all 300ms ease-out 0s !important;
}

#sessionmanagerPrompt button:-moz-any(:hover, :focus, :focus:hover)
{
    background-color: rgba(48,58,69,1) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(111,126,140,0.45),
                rgba(93,105,118,0.20)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 0px 1px rgba(0,0,0,0.7),
          inset 0px 0px 3px 2px rgba(10,18,25,0.25),
                   0px 0px 0px 1px rgba(66,75,86,0.9)
          !important;
   color: rgb(152,217,252) !important;
}
#sessionmanagerPrompt button:not([disabled="true"]):-moz-any(:active, :active:hover, [open="true"]) {
    background-color: rgba(5,15,26,0.05) !important;
    background-image:
            linear-gradient(to bottom,
                rgba(255,255,255,0.0),
                rgba(0,0,0,0.0)
                ) !important;
    border-width: 0px !important;
    border-style: solid !important;
    border-color: rgb(29,37,46) !important;
    box-shadow:
          inset 0px 0px 4px 1px rgba(0,0,0,0.95),
          inset 0px 0px 0px 1px rgba(5,15,26,0.2),
                   0px 0px 0px 1px rgba(233,244,255,0.10)
          !important;
	  color: rgb(101,191,255) !important;
}
#sessionmanagerPrompt button dropmarker,
#sessionmanagerPrompt button:-moz-any(:hover, :focus, :active, [open="true"]) dropmarker {
    list-style-image: url(
                data:image/png;base64,
                ) !important;
    padding: 3px 2px 0px 2px !important;
    width: 11px !important;
    height: 11px !important;
    -moz-image-region: rect(0px,9px,7px,0px) !important;}
#sessionmanagerPrompt button:-moz-any(:hover, :focus) dropmarker {
    -moz-image-region: rect(0px,18px,7px,9px) !important;}
#sessionmanagerPrompt button:-moz-any(:active, [open="true"]) dropmarker {
    -moz-image-region: rect(0px,27px,7px,18px) !important;}

#sessionmanagerPrompt button > .button-box,
#sessionmanagerPrompt button:-moz-focusring > .button-box {
    border: 0px solid transparent !important; }
/*  ----  */


/*  CHECKBOX  */
.checkbox-check {
    -moz-appearance: none !important;
    border: 1px solid !important;
    border-color: rgba(255,255,255,0.04) !important;
    border-bottom-color: rgba(255,255,255,0.08) !important;
    -moz-border-top-colors: transparent ;
    -moz-border-right-colors: transparent ;
    -moz-border-bottom-colors: transparent ;
    -moz-border-left-colors: transparent ;
    min-width: 14px;
    min-height: 14px;
    background: transparent no-repeat 50% 50% !important;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.0) !important;
    transition: border-color, box-shadow 500ms ease 0s !important;
}
checkbox:hover > .checkbox-label-box,
checkbox:-moz-focusring > .checkbox-label-box {
    color: rgb(128,255,255) !important;
}
checkbox:hover > .checkbox-check,
checkbox:-moz-focusring > .checkbox-check {
    border-color: rgba(128,255,255,0.4) !important;
    transition: border-color 500ms ease 0s !important;
}
checkbox[checked="true"] > .checkbox-check {
    border-color: rgb(128,255,255) !important;
    box-shadow:
            inset 4px 4px 5px 0px rgba(0,0,0,0.75),
            inset -4px -4px 3px -3px rgba(128,255,255,0.45);
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPUlEQVQI13WMQQrAQAwCR8i/03357ClQSioI4qBR2XTA+gMAdcCGfAFATdGQN2hIbYt5iroCAFRUHnXy+AKf3Cn4sMNREQAAAABJRU5ErkJggg==
                ) !important;
}/*  ----  */


/*  RADIO CHECKBOX  */
#sessionmanagerPrompt .radio-check-box1 {
    -moz-appearance: none !important;
    margin: 1px 0px !important;
    border: 0px solid transparent !important;
    border-radius: 50% !important;
    width: 16px !important;
    height: 16px !important;
    background: none no-repeat scroll 0px 0px rgba(30,30,30,0.7) !important;
    box-shadow: inset 1px 1px 3px 0px rgba(0,0,0,1),
                          inset -1px -1px 0px 0px rgba(100,100,100,0.7);
    transition: all 250ms ease-in-out 0s !important;
}
#sessionmanagerPrompt .radio-check-box2 {
    border: 0px solid transparent !important;
    border-radius: 50% !important;
    padding: 0px !important;
    margin: 3px 3px !important;
    height: 10px !important;
    width: 10px !important;
    max-height: 10px !important;
    max-width: 10px !important;
    list-style-image: none;
    transition: all 500ms ease-out 0s !important;
}

#sessionmanagerPrompt radio:not([disabled="true"]):not([selected="true"]):hover > .radio-check-box1 > .radio-check-box2 ,
#sessionmanagerPrompt radio:not([disabled="true"]):not([selected="true"])[focused="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(28,155,155);
    transition: none !important;
}
#sessionmanagerPrompt radio:not([disabled="true"]):hover:active > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(28,155,155);
}

#sessionmanagerPrompt radio[selected="true"]:not([disabled="true"]) > .radio-check-box1 {
    box-shadow: inset 1px 1px 3px 0px rgba(0,0,0,1),
                          inset -1px -1px 0px 0px rgba(128,255,255,0.7);
}
#sessionmanagerPrompt radio[selected="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(128,255,255);
}
#sessionmanagerPrompt radio[selected="true"][disabled="true"] > .radio-check-box1 > .radio-check-box2 {
    list-style-image: none !important;
    background-color: rgb(60,60,60);
}
/*  ----  */

/*  ----  */
#sessionmanagerPrompt checkbox:not([checked="true"]) > .checkbox-check {
    color: rgb(192,192,192) !important;
}
#sessionmanagerPrompt radio[focused="true"] > .radio-label-box,
#sessionmanagerPrompt checkbox:-moz-focusring > .checkbox-label-box {
    border: 1px solid transparent !important;
    color: rgb(128,255,255) !important;
}/*  ----  */



/*  TEXTBOXES  */
#sessionmanagerPrompt textbox {
    -moz-appearance: none !important;
    margin: 0px 0px 0px 0px !important;
    min-height: 22px !important;
    max-height: 22px !important;
    border-radius: 0px !important;
    padding: 3px !important;
    border: 1px solid rgb(27,31,35) !important;
    color: rgb(200,200,200) !important;
    background-color: rgba(105,114,122,1) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 24px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 700ms ease-in-out 0s !important;
}
#sessionmanagerPrompt textbox:hover {
    -moz-appearance: none !important;
    border: 1px solid rgba(164,167,172,0.5) !important;
    transition: all 300ms ease 0s !important;
}
#sessionmanagerPrompt textbox[focused="true"] {
    color: rgb(255,252,255) !important;
    background-color: rgba(105,114,122,1) !important;
    background-image:
            linear-gradient(
                rgba(66,73,79,1),
                rgba(66,73,79,0)
                ) !important;
    box-shadow:
            0 1px 10px rgba(66,73,79,1) inset,
            0 1px 0 hsla(210,16%,76%,0.15),
            0px 0px 0px 0px rgba(66,73,79,1) inset
            !important;
    transition: all 300ms ease 0s !important;
}
#sessionmanagerPrompt ::-moz-selection {
    background: rgb(20,20,20) !important;
    color: hsl(208,100%,60%) !important;
}
/*  ----  */




/*  TREE  LISTBOX  */
#sessionmanagerPrompt tree {
    -moz-appearance: none !important;
    border: 1px solid rgb(0,0,0) !important;
    color: rgb(200,200,200) !important;
    background-color: rgb(20,23,26) !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-row {
    background-color: transparent !important;
    overflow-y: scroll  !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-row(selected) {
    background-color: rgba(51,159,253,0.15) !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-cell-text(selected) {
    color: rgb(51,159,253) !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-row(current, focus) {
    border: 1px solid rgb(2,83,155) !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-row(hover) {
    background-color: rgba(51,159,253,0.15) !important;
}
#sessionmanagerPrompt treechildren::-moz-tree-cell-text(hover) {
    text-decoration: none !important;
}

#sessionmanagerPrompt tree[seltype="cell"] > treechildren::-moz-tree-line,
#sessionmanagerPrompt tree[seltype="text"] > treechildren::-moz-tree-line,
#sessionmanagerPrompt treechildren::-moz-tree-line {
    border: 1px dotted rgba(255,255,255,0.3) !important;
}
#sessionmanagerPrompt tree[seltype="cell"] > treechildren::-moz-tree-line(active, selected, focus),
#sessionmanagerPrompt treechildren::-moz-tree-line(selected, focus) {
    border: 1px dotted rgba(0,255,255,1) !important;
}

#sessionmanagerPrompt treechildren::-moz-tree-twisty {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAYklEQVQY033PywmAQAxF0RtwKQo2qFiZWy1JwRa0AIfnxoFhiHm7wMkPikiaJQ04aaq6A0ZJi5ldEQToganGHnTxH8w4n3FHEKAFBiCED7Ca2RmtzuiInknAViIPpm/SXne/bAklqD8BRaEAAAAASUVORK5CYII=
                ) !important;
    -moz-padding-end: 4px;
    padding-top: 1px;
    width: 9px;}
#sessionmanagerPrompt treechildren::-moz-tree-twisty(open) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAKCAYAAACNMs+9AAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQY02P8//8/AzGAhRhFjCd+/WchRhFBE2GK8CpEVoRTIboirAqxKcJQiEsRikJ8ihgYGBgYiQ1wAJ56HC2vHF1bAAAAAElFTkSuQmCC
                ) !important;
    width: 9px;}
#sessionmanagerPrompt *::-moz-tree-checkbox(checked) {list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAQklEQVQI13WM0QnAUAwCL5CN3H+Dl5muX4FSXgVBPLRUbpoZ+w8A9MyYpL4AoLdIUm+QpPq22KdSrwAAFZVzjpvXD72LOZBnLCA/AAAAAElFTkSuQmCC
                ) !important;}
#sessionmanagerPrompt *::-moz-tree-checkbox(selected, checked) {list-style-image: url(
            data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAHCAYAAADEUlfTAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAARUlEQVQI13WMwQnAMAwDz+CNmoG8WTOQd7q+DKWkAoHQIYXKSWtj/gGAXBu7iC8AyCm6iDfoIvK0mKdQjwAAFZXr1snjB+wLL0QDjQNrAAAAAElFTkSuQmCC
                ) !important;}
/*  --  */


/*  TREE HEADERS  */
#sessionmanagerPrompt treecol {
    -moz-appearance: none !important;
    border: 0px solid !important;
    border-bottom: 1px solid !important;
    border-right: 1px solid rgb(0,0,0) !important;
    -moz-border-bottom-colors: rgb(0,0,0) !important;
    background:
            linear-gradient(to bottom, 
                rgba(56,70,82,1) 0%,
                rgba(35,39,48,1) 100%) !important;
    color: white !important;
    padding: 0px 4px !important;
    height: 18px !important;
}
#sessionmanagerPrompt treecol:last-child:not([hidden="true"]) {
    border-right: 0px solid transparent !important;
}
#sessionmanagerPrompt :-moz-any(treecol, treecolpicker):hover {
    background:
            linear-gradient(to bottom, 
                rgba(194,116,0,1) 0%,
                rgba(185,49,0,1) 100%)
      !important;
}
#sessionmanagerPrompt :-moz-any(treecol, treecolpicker):hover:active {
    background:
            linear-gradient(to bottom, 
                rgba(224,146,32,1) 0%,
                rgba(215,79,37,1) 100%)
      !important;
}


#sessionmanagerPrompt treecolpicker{
    -moz-appearance: none !important;
    border: 0px solid !important;
    border-bottom: 1px solid !important;
    border-left: 1px solid rgb(0,0,0) !important;
    -moz-border-bottom-colors: rgb(0,0,0) !important;
    background:
            linear-gradient(to bottom, 
                rgba(56,70,82,1) 0%,
                rgba(35,39,48,1) 100%) !important;
    color: white !important;
    padding: 0px 4px !important;
    height: 18px !important;
}
#sessionmanagerPrompt .tree-columnpicker-icon {
  list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA4AAAAMCAYAAABSgIzaAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAPElEQVQoz2NgoDdghDH+////n6BiRkZGDEF0jYT49LeRheJQIsapRNuIzTCYP3EGDkwBsjjWwKGmN/ACAA6wM+xPEB+mAAAAAElFTkSuQmCC
                );
}
#sessionmanagerPrompt treecol:not([hideheader="true"]) > .treecol-sortdirection[sortDirection="ascending"] {
    list-style-image: url(
        data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAFCAYAAACXU8ZrAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAO0lEQVQI13XK4QkAIAiE0XOzRrBJG6E2+/qliNTBwSlPgGp9Q/8ZoMg8ymMNWexEFXRowBNUaL75gsgFIc0v/sn2bmEAAAAASUVORK5CYII=
    ) !important;}
#sessionmanagerPrompt treecol:not([hideheader="true"]) > .treecol-sortdirection[sortDirection="descending"] {
    list-style-image: url(
        data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAkAAAAFCAYAAACXU8ZrAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAPUlEQVQI14WMwQ2AIADEepsxAk7KCLBZ+YhRorHJfS5NU7vyQ1SOwafYColn6E1shVylxV1cAgDqY7Xr/k1Pby/+5aH9qwAAAABJRU5ErkJggg==
    ) !important;}
/*  --  */



/*  SPLITTER  */
#sessionmanagerPrompt splitter[orient="vertical"] {
    -moz-appearance: none !important;
    -moz-border-left-colors: transparent transparent  !important;
    -moz-border-right-colors: transparent transparent  !important;
    -moz-border-top-colors: transparent transparent  !important;
    -moz-border-bottom-colors: transparent transparent  !important;
    border: 0px !important;
    background-color: rgb(37,44,54) !important;
}
#sessionmanagerPrompt splitter[orient="vertical"] > grippy {
    border-top: none;
    border-right: 1px solid rgb(58,70,80) !important;
    border-bottom: none;
    border-left: 1px solid rgb(58,70,80) !important;
    min-height: 6px !important;
}
#sessionmanagerPrompt grippy {
    background-color: rgba(51,159,253,0.4) !important;
    transition: background-color 2s ease-in-out 1s !important;
}
#sessionmanagerPrompt grippy:hover {
    background-color: rgba(51,159,253,1) !important;
    transition: background-color 300ms ease-in-out 0s !important;
}

#sessionmanagerPrompt splitter[collapse="before"][orient="vertical"] > grippy ,
#sessionmanagerPrompt splitter[collapse="after"][state="collapsed"][orient="vertical"] > grippy {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAfElEQVQ4y+2UsQ2AIBQF7yfu4ADSWsHe1g5gJyPQ/jWwIjGiIoaSq8mFvCOIqlJgByzgAXd3wDmHqhJjpFOHiFBoUNw/MXyQJCwQ36TGmF7zR89W+z/FvEbxJ3GSZpcYp7mnqSSE0Gz/oSC6voLza8mE27pIz1P/zbba/wDquishwo97qgAAAABJRU5ErkJggg==
                ) !important; }
#sessionmanagerPrompt splitter[collapse="after"][orient="vertical"] > grippy ,
#sessionmanagerPrompt splitter[collapse="before"][state="collapsed"][orient="vertical"] > grippy {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAi0lEQVQ4y+2UsQ0DIQxFnyV2yAChTQV7p84A6XIjUAJjOEVykUUSJE6UvMqWrG/pf4OUUmhQU29ANP0DCKYXgBhfIzlnFmOICKpKrfWw/zvul74RDO96a0S+hAC897riGc9zlv+us8BeQehcy4fT+bKiGSSlNM1/19kTm6f9V2TnfrvKimf8m53l/xOuHSeRI6pkMAAAAABJRU5ErkJggg==
                ) !important; }
#sessionmanagerPrompt splitter[collapse="before"][orient="vertical"] > grippy:hover ,
#sessionmanagerPrompt splitter[collapse="after"][state="collapsed"][orient="vertical"] > grippy:hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAbklEQVQ4y+2Uuw2AMAwFzxI7MABpqcL+E5ARMoupLKHwCQkpfbX1ZL2zLNTZgQgkYLsbUFWcPkTkd//G9CHEiIC+hYYQ3GqHz1H9P8kspaRTsIVelpiX1dU0knMe1r9UgsorKK9F/M0Of7PN/RsHF48hPXgiD9wAAAAASUVORK5CYII=
                ) !important; }
#sessionmanagerPrompt splitter[collapse="after"][orient="vertical"] > grippy:hover ,
#sessionmanagerPrompt splitter[collapse="before"][state="collapsed"][orient="vertical"] > grippy:hover {
    background-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAHMAAAAECAYAAAC9ZACfAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAeklEQVQ4y+2UsQ2AMAwE7yV2YADSUoX9J4ARMotpCLIIIIJS5iq7OUtvy6LEXL0Bi+tXILpeAGaGJMyMTh05N0m/888Md34njEe9XSSFCCCE0Lf5Y5+t8h9eBvgriC/XcjJOc19NJSmlZvnrw7wsfZT0N9v0zVbnn9kB58UrQ+H+aigAAAAASUVORK5CYII=
                ) !important; }
/*  --  */







/*  GROUPS MENU  */
#group-text-container {
    margin: 3px 0px 0px 0px !important;
}
menulist {
    width:auto !Important;
    height: 22px !Important;
    -moz-appearance: none !important;
    padding: 0px 0px 0px 0px !important;
    margin: 0px 0px 2px -2px !important;
    font-size: 13px !important;
    background-color:rgb(64,72,82) !important;
    border: 1px solid rgba(179,182,186,0.4) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 500ms ease 0s !important;
}
menulist:hover,
menulist:focus {
    -moz-appearance: none !important;
    background-color:rgb(37,44,51) !important;
    border-color: rgb(255,255,255) !important;
    box-shadow:
              0 0 0 1px rgba(66,79,95,0) inset
              !important;
    transition: all 300ms ease 0s !important;
}

.menulist-editable-input {
    background-color: transparent !important;
    color: rgb(179,182,186) !important;
}


menulist:-moz-any(:hover, focus) .menulist-editable-input {
    color: rgb(255,255,255) !important;
}

menulist[open="true"] {
    border-color: rgb(23,185,247) !important;
    transition: all 300ms ease 0s !important;
}
menulist[open="true"] .menulist-label-box {
    color: rgb(23,185,247) !important;
    text-shadow: 0px 0px 2px rgba(8,159,218,0.7) !important;
    transition: all 300ms ease 0s !important;
}


menulist dropmarker{
    -moz-appearance: none !important;
    width: 12px !important;
    height: 12px !important;
    background-color: transparent !important;
    border: 0px solid transparent !important;
    padding: 7px 0px 0px 0px !important;
    opacity: 0.6  !important;
    list-style-image: url(
                data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAwAAAAMCAYAAABWdVznAAAACXBIWXMAAA7EAAAOxAGVKw4bAAAAMUlEQVQoz8WRyQ0AMAzCQMr+K7srxI+o/C2uAjGaSJ0AJOkWwDhgImE6YEpjV+r/4x7lPgknh6mMxAAAAABJRU5ErkJggg==
                ) !important;
}
menulist:hover dropmarker:not([disabled="true"]) ,
menulist:focus dropmarker:not([disabled="true"]) {
    opacity: 1  !important;
}
menulist dropmarker:hover:active:not([disabled="true"]),
menulist:hover:active > .menulist-dropmarker {
    padding: 7px 0px 0px 0px !important;
}
menulist[open="true"] dropmarker { list-style-image: url(
                data:image/png;base64,
                ) !important;
    opacity: 1 !important; }
menulist dropmarker[disabled="true"] { list-style-image: url(
