css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
 
  /********************************************************
  The Headers and the Layout Section
  ********************************************************/
  
  body {
    background: url("http://images7.alphacoders.com/315/315094.jpg");
    background-size: cover;
  }
  
  .ps-room.ps-room-light {
    background: blue;
    border-color: #4F4D4D;
  }
  
  .ps-overlay {
    background: rgba(0, 0, 0, 0.4);
  }
  
  
  .header {
    background: skyblueblue;
  }
  
  .header .logo {
    /* You can insert your custo logo here if you want, and if you dont have a custom logo, leave it as it is */
  }
  
  .userbar button.icon {
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: lightblue;
  }
  
  .userbar button.icon:hover {
    border: 1px solid #111;
    color: #111;
    background: #FFF;
  }
  
  .userbar .username {
    text-shadow: none;
  }
  
  .maintabbarbottom {
    border: none;
    box-shadow: none;
    border-bottom: 1px solid #050026;
    background: #FFF;
  }
  
  .tabbar a.button.notifying {
    border-color: #FF1101;
    color: #FF1101;
    background: #FFD9D8;
    text-shadow: none !important;
  }
  
  .tabbar a.button.notifying:hover {
    border-color: #FF1101;
    color: #FF1101;
    background: #FDCFCD;
    text-shadow: none !important;
  }
  
  .tabbar a.button.subtle-notifying {
    color: #FF1101;
    background: #FFF;
    border: 1px solid #4F4D4D;
  }
  
  .tabbar a.button.subtle-notifying:hover {
    color: #FF1101;
    border: 1px solid #111;
    background: #FFF;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border: 1px solid #050026;
    color: #050026;
    background: #FFF;
    height: 26px;
    margin-top: 1px;
  }
  
  .tabbar a.button {
    margin-left: 5px;
    border-radius: 3px;
    margin-bottom: -50px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
    height: 28px;
  }
  
  .tabbar a.button:hover {
    border: 1px solid #111;
    color: #111;
    background: #FFF;
  }
  
  .tabbar.maintabbar {
    margin-left: 180px;
  }
  
  .tabbar {
    position: absolute;
    bottom: 1px;
    left: 0px;
    right: 0px;
    display: block;
    list-style: outside none none;
    margin: 0px;
    height: 43px;
    text-align: left;
    color: #FFF;
    text-shadow: 0px 1px 0px #000;
    font-size: 10pt;
    line-height: 100%;
  }
  
  .tabbar li:last-child a.button {
    border-top-right-radius: 3px;
  }
  .tabbar li:first-child a.button {
    border-top-left-radius: 3px;
  }
    
/*******************************************************************************
  This part is for the customization of rooms of PS
  ******************************************************************************/
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(255, 255, 255, 0.5);
    color: blue;
  }
  
  .roomlist a.ilink {
    border-color: black;
    color: #4F4D4D;
    background: lightblue;
    font-face: bold;
  }
  
  .roomlist a.ilink:hover {
    border-color: #111;
    color: #111;
    background: #FFF;
  }
  
  .userlist {
    background: rgba(255, 255, 255, 0.9);
    color: #222;
    border-right: 1px solid #4F4D4D;
  }
  
  .userlist li {
    border-bottom: 1px solid #4F4D4D;
  }
  
  .userlist li button:hover {
    background: #EEE;
  }
  
  .chat-log-add {
    background: rgba(255, 255, 255, 0.9);
    color: #222;
    border-top: 1px solid #4F4D4D;
  }
  
  .chat-log {
    background: rgba(255, 255, 255, 0.8);
  }
  
  .infobox {
    background: rgba(255, 255, 255, 0.5);
    border-color: #060127;
  }
  

/****************************************************************
  CSS for Mainmenu
  ***************************************************************/
  
  .menugroup {
    background: rgba(255, 255, 255, 0.75);
    border: 1px solid #050026;
  }
  
  .label {
    color: yellow;
    text-shadow: none;
  }
  
  .menugroup .button {
    background: #F7E080;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
    border-radius: 3px;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.3) inset;
  }
  
  .menugroup .button:hover, .menugroup .button:focus {
    background: #E9CE5F;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .menugroup .button.disabled, .menugroup .button.disabled:hover, .menugroup .button.disabled:active {
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026 !important;
    background: rgba(247, 224, 128, 0.6);
  }
  
  button[name="roomlist"], button[name="finduser"] {
    width: 230px !important;
  }
  
  .select {
    border-radius: 3px;
    background: #F7E080;
    color: #050026;
    border: 1px solid #050026;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.3) inset;
  }
  
  .select:hover, .select:focus {
    background: #E9CE5F;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
  }
  
  .select::after {
    color: #050026;
  }
  
  .select:disabled, .select:disabled:hover {
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026 !important;
    background: rgba(247, 224, 128, 0.6);
  }
  
 /*******************************************************
  CSS for PM
  *****************************************************/
  
  .pm-window.news-embed {
    opacity: 0.2;
  }
  
  .pm-window.news-embed:hover {
    opacity: 0.3;
  }
  
  .pm-window h3 {
    border-color: #000;
    background: #01F5FF;
    color: #000;
  }
  
  .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    border-color: #000;
    color: #000;
    background: #00E7EF;
  }
  
  .pm-window h3.pm-notifying {
    border-color: #FF1101;
    color: #FF1101;
    background: #FFD9D8;
    text-shadow: none !important;
  }
  
  .pm-window h3.pm-notifying:hover {
    border-color: #FF1101;
    color: #FF1101;
    background: #FDCFCD;
    text-shadow: none !important;
  }
  
  .pm-window h3 .closebutton, .pm-window h3 .minimizebutton {
    color: #000;
  }
  
  .pm-window h3 small {
    color: #333;
  }
  
  .pm-log {
    background: rgba(255, 255, 255, 0.8);
    border-color: #060127;
  }
  
  .pm-log-add, .pm-window.focused .pm-log-add {
    border-color: #000;
    background: #01F5FF;
  }
  
  .pm-log-add .textbox {
    box-shadow: none;
    border-color: #000;
  }
  
/**************************************
  Team Builder CSS
  *********************************/
  
  .teamchartbox {
    border-top: 1px solid #4F4D4D;
    background: rgba(255, 255, 255, 0.75);
  }
  
  .setchart, .setchart-nickname {
    background: #FFF;
    border-color: #4F4D4D;
  }
  
  .setchart label, .setchart-nickname label {
    color: #222;
    text-shadow: none;
  }
  
  .setchart input, .setchart-nickname input, .statform input.inputform, .setstats, .setdetails, .setchart input:hover, .setchart-nickname input:hover, .statform input.inputform:hover, .setstats:hover, .setdetails:hover {
    border-color: #4F4D4D;
    box-shadow: none;
    color: #4F4D4D;
  }
  
  .setchart-nickname input:focus, .statform input.inputform:focus, .setstats:focus, .setdetails:focus {
    border-color: #060127;
    box-shadow: none;
    color: #060127;
  }
  
  .setchart input:focus {
    border-color: #060127;
    box-shadow: none;
    color: #FF0400;
  }
  
  .teambuilder-results h3, .teambuilder-results .utilichart h3 {
    background: #FFF;
    border-color: #4F4D4D;
    color: #222;
    text-shadow: none;
  }
  
  .teambar button {
    background: #FFF;
    border-color: #4F4D4D;
    color: #222;
    text-shadow: none;
  }
  
  .teambar button:hover {
    background: #EEE;
  }
  
  .ps-room.ps-room-light.scrollable button {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .pad ul li button {
    margin-bottom: 2px;
  }
  
  .pad .textbox {
    border: 1px solid #4F4D4D;
    box-shadow: none;
  }
  
  .setmenu button {
    border: none !important;
    background: transparent !important;
    color: #000 !important;
  }
  
  .setmenu button:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D !important;
    color: #000 !important;
    background: #FFF !important;
  }
  
  .teambuilder-results .firstresult a, .teambuilder-results .firstresult a:hover, .teambuilder-results .result a:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .utilichart b {
    color: #FF1101;
  }
  
  .utilichart .itemdesccol, .utilichart .abilitydesccol, .utilichart .movedesccol {
    color: #555;
  }

  .utilichart .labelcol em, .utilichart .widelabelcol em, .utilichart .pplabelcol em, .utilichart .statcol em, .utilichart .bstcol em, .utilichart .numcol {
    color: #777;
  }
  
  .utilichart .typecol img {
    opacity: 0.85;
  }
  
  .teambuilder-clipboard-container {
    border-color: #FF1101;
  }
  
  .teambuilder-clipboard-data, .teambuilder-clipboard-data:hover {
    border-radius: 3px;
    border: 1px solid #4F4D4D;
    color: #4F4D4D;
    background: #FFF;
  }
  
  .ps-popup {
    background: #F7E080;
    border: 1px solid #050026;
    text-shadow: none;
    color: #050026;
    border-radius: 3px;
    box-shadow: 1px 1px 0px rgba(0, 0, 0, 0.1) inset, -1px -1px 0px rgba(0, 0, 0, 0.18) inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
  }
  
  .ps-popup hr {
    border: none;
    border-bottom: 1px solid #050026;
  }
  
  .ps-popup a, .ps-popup a.ilink:hover {
    color: #315DFF;
  }
  
  .ps-popup button {
    background-color: #4F4D4D;
    border: 1px solid #050026;
    color: #FFF;
    text-shadow: 0px -1px 0px #050026;
    border-radius: 3px;
  }
  
  .popupmenu h3 {
    color: #050026;
  }
  
  .popupmenu button {
    width: 220px;
    overflow-x: hidden;
    color: #000;
    background: transparent;
    border: none;
    text-shadow: none;
    border-radius: 0px;
  }
  
  .popupmenu button:hover, .popupmenu button.sel:hover {
    border-color: #050026;
    background: rgba(255, 255, 255, 0.25);
  }
  
  .popupmenu button.sel {
    border-color: #050026;
  }
  
  .avatarlist button {
    background-color: transparent;
  }
  
  .avatarlist button:hover {
    border-color: #050026;
    background-color: rgba(255, 255, 255, 0.25);
    box-shadow: none;    
  }
  
  .credits h2 {
    color: #060127;
  }
  
  a.subtle, .credits ul, .credits ul li p {
    color: #0C034A;
  }
  
  a.subtle:hover {
    color: #10045B;
  }
  
  .mainmenufooter {
    bottom: 0px;
    left: 0px;
    padding: 10px;
    border-color: #4F4D4D;
    border-style: solid;
    border-width: 1px 1px 0px 0px;
    border-top-right-radius: 5px;
    background: rgba(255, 255, 255, 0.7);
    color: #222;
    text-shadow: none;
  }
  
  .mainmenufooter a {
    color: #222;
    text-shadow: none;
  }
  
  .mainmenufooter a:hover {
    color: #000;
  }
  

}
