css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.youtube.com") {
  li, .video-list-item, .yt-uix-simple-thumb-wrap, .guide-channels-list, a.guide-item, .yt-pl-thumb-link, .yt-pl-thumb-overlay, .yt-pl-thumb, .sidebar {
    border-radius: 14px;
  }
  img,.yt-thumb, .yt-masthead-picker-photo-change {
    border-radius: 14px;
  }
  .video-time {
    border-radius: 10px;
    font-size: 10px;
    margin-right: 3px;
    margin-bottom: 3px;
  }
   #masthead-search, #masthead-search-terms {
    border-radius: 14px 0px 0px 14px;
  }
  .search-btn-component {
    border-radius: 0px 14px 14px 0px;
  }
  .yt-uix-button{
    border-radius: 14px;
  }
  .yt-uix-button-has-icon{
    border-radius: 0px;
  }
  .yt-card {
    border-radius: 20px;
  }
  .yt-uix-menu-content {
    border-radius: 10px;
  }
  body .addto-watch-later-button, .addto-watch-later-button-sign-in, .addto-watch-later-button-loading, .addto-watch-later-button-error, .addto-watch-later-button-success, .addto-watch-later-button-remove, .yt-uix-button-default:hover, .yt-uix-button-default {
    border-radius: 10px;
  }
  .channel-header-profile-image-container {
    border-radius: 0px 0px 20px 20px;
  }
  #masthead-search .search-btn-component, #masthead-search .search-btn-component .start {
    border-radius: 0px 20px 20px 0px;
  }
  .channel-header-profile-image {
    border-radius: 0px 0px 20px 20px;
  }
  .channel-header .about-secondary-links, .channel-header .about-custom-links {
    border-radius: 20px 20px 0px 0px;
  }
  .watched-badge {
    border-radius: 18px 20px 30px 25px;
  }
}
