css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("soylentnews.org")
{
	.user ul {
		background-color: #222 !important;
	}
	.search-results h4 a {
		color: #ccc !important;
	}
	li.comments a {
		color: #0f0 !important;
	}
	body, div, form, #links ul li, #links ul, #topnav, img {
		font-size: 100% !important;
	}
	body, body.admin {
		background-color: #222 !important;
		color:#ddd !important;
	}
	fieldset {
		border: 1px solid #777 !important;
	}
	.user_admin, body .adminstoryform fieldset {
		background-color: #0000b3 !important;
		border: none !important;
	}
	a, .error {
		outline: 0 !important;
		color: #9696ff !important;
	}
	a:visited {
		color: #9696ff !important;
	}
	a:hover {
		color: #bfbfff !important;
	}
	hr {
		border-top: 1px solid #777 !important;
		border-bottom: none !important;
		border-left: none !important;
		border-right: none !important;
		
	}
	/* layout */
	#contents {
		min-width: 20em !important;
	}
	/* Header */
	#logo h1 a {
		width: 115px !important;
		height: 80px !important;
		background: url("https://soylentnews.org/images/logo_chillax.png") no-repeat !important;
	}
	#slogan h2, #slogan #head_message {
		color: #ccc !important;
		font-weight: bold !important;
		font-size: .813em !important;
	}
	#slogan #head_message a {
		color: #ccc !important;
	}
	#section_banner {
		background-color: #0000b3 !important;
		background: linear-gradient(#4848d9, #0000b3) !important;
		border-radius: 8px !important;
		font-size: 1.125em !important;
		font-weight: bold !important;
	}
	/* links */
	#links a, #links a:visited, #slashboxes a {
		color: #9696ff !important;
		text-decoration:none !important;
	}
	#links a:hover, #slashboxes a:hover {
		color: #bfbfff !important;
		text-decoration: underline !important;
	}
	.comment_count {
		color: #aaf !important;
	}
	#links h4 {
		font-weight: bold !important;
		font-size: .813em !important;
	}
  div.title h3 a, div.title h4 a, #poll-title h4 a, #top10journals-title h4 a {
      color: #fff !important;
  }
  div.title h3 a:hover, #poll-title h4 a:hover, #top10journals-title h4 a:hover {
      color: #aaa !important;
  }
	#links div.content {
		background-color: #222 !important;
    border: 1px solid #777 !important;
		border-radius: 0 0 10px 10px !important;
		padding: .3em .3em .6em .3em !important;
		font-size: .755em !important;
		line-height: 1.4em !important;
		position: relative !important;
	}
	#navigation-content li.user {
		background: linear-gradient(#4848d9, #0000b3) !important;
		border: 1px solid #0000b3 !important;
		color: #fff !important;
	}
	#navigation-content ul ul {
		background-color: transparent !important;
	}
	/* slashboxes */
	div.title {
		/*background-color: #666 !important;*/
		background: linear-gradient(#4848d9, #0000b3) !important;
		border: 1px solid #0000b3 !important;
		border-bottom: none !important;
		border-radius: 10px 10px 0 0 !important;
	}
	#slashboxes h4 {
		font-size: .813em !important;
		color: #fff !important;
	}
	#slashboxes div.content {
		color:#fff !important;
		background-color: #222 !important;
    border: 1px solid #777 !important;
		border-radius: 0 0 10px 10px !important;
		font-size: .755em !important;
		line-height: 1.4em !important;
	}
	#slashboxes .highlight {
		background-color: #555 !important;
	}
	.indexhead {
		font-size: .875em !important;
	}
	/* Commnets Pages */
	.commentTop .title {
		background-color: #0000b3 !important;
		background: linear-gradient(#4848d9, #0000b3) !important;
		border-radius: 10px 10px 0 0 !important;
	}

	.commentTop .details {
		border: 1px solid #777 !important;
		background-color: #444 !important;
		background: linear-gradient(#444, #222) !important;	
		color: #fff !important;
	}
	.commentTop .details a, .commentTop .details a:visited {
		color: #9696ff !important;
	}
	.commentTop .details a:hover {
		color: #bfbfff !important;
	}
	.commentSub {
		border: 1px solid #777 !important;
		border-top: none !important;
		background-color: #222 !important;
	}
	.commentBody {
		border: 1px solid #777 !important;
		border-bottom: none !important;
		border-top: none !important;
		background-color:#222 !important;
	}
	.commentBody blockquote, .commentBody .quote {
		background: linear-gradient(to right, rgba(255, 255, 255, 0.07), transparent 40%) repeat scroll 0% 0% transparent !important;
		border-left-color: rgba(255,255,255, 0.4) !important;
	}
	#commentlisting ul {
		border-left: 1px solid #777 !important;
	}
	.preview li.comment {
		border-bottom: 1px solid #777!important;
	}
	#commentwrap, .commentwrap, .commentBoxForm {
		background-color: #222 !important;
	}
	.linkCommentPage {
		background-color: #555 !important;
	}
	/* FORMTABS */
	#formtabs ul li {
		background-color: #339 !important;
		border-radius: .3em .3em 0 0 !important;
		color: #fff !important;
	}
	#formtabs ul li.active {
		color: #fff !important;
	}
	#formtabs ul li a {
	text-decoration:none !important;
		color:#fff !important;
	}
	#formtabs ul li a:visited {
		color:#fff !important;
	}
	#formtabs ul li a:hover {
		color:#fff !important;
	}
	/* Quick links content */
	#slashboxes #index_qlinks-content ul, #slashboxes #index_qlinks-content li, #slashboxes #features_qlinks-content ul, #slashboxes #features_qlinks-content li {
		list-style: none !important;
		padding: 0 !important;
		margin: 0 !important;
	}
	/* General */
	.generaltitle h3 {
		color: #fff !important;
		font-size: 1em !important;
	}
	.generalbody {
		background-color: #222 !important;
		border: 1px solid #777 !important;
	}
	.generalbody .title {
		border: none !important;
		width: auto !important;
		background: none !important;
	}
	/* articles */
	.generaltitle div.title, .article div.title, .search-results h4 {
		background-color: #0000b3 !important;
		background: linear-gradient(#4848d9, #0000b3) !important;
		border-radius: 10px 10px 0 0 !important;
	}
	.generaltitle div.title {
		border-top: 1px solid #0000b3 !important;
		border-right: 1px solid #0000b3 !important;
		border-left: 1px solid #0000b3 !important;
	}
	#journal .article h3 {
		color: #fff !important;
		font-size: 1em !important;
	}
	.article .details, .search-results .data {
		font-size: .7em !important;
		font-weight: normal !important;
		border: 1px solid #777 !important;
		background-color: #444 !important;
		background: linear-gradient(#444, #222) !important;
	}
	.article .body, .search-results .intro, .search-results .author {
		border: 1px solid #777 !important;
		border-top: none !important;
		background-color: #222 !important;
	}
	.article blockquote,  .article .quote {
		background: linear-gradient(to right, rgba(255, 255, 255, 0.05), transparent 60%) repeat scroll 0% 0% transparent !important;
		border-left-color: rgba(255,255,255, 0.3) !important;
	}
	div.storylinks, .search-results .details {
		background-color: #444 !important;
		background: linear-gradient(#444, #222) !important;
		border: 1px solid #777 !important;
		border-top: none !important;
		border-radius: 0 0 10px 10px !important;
		font-size: .813em !important;
	}
	div.storylinks div ul {
		padding: .5em 1em !important; 
		margin: 0 !important;
		line-height: .875em !important;
	}
	div.storylinks ul li {
		padding: .3em .5em !important; 
		border-left: 1px solid #555 !important; 
	}
	/* Footer */
	#footer em {
		font-size: .813em !important;
		float: right !important;
		width: 45% !important;
	}
	#footer .search {
		font-size: .7em !important;
		width: 50% !important;
		padding: 0 .3em !important;
		margin: 0 !important;
	}
	#footer legend, #footer fieldset, #footer label {
		margin: 0 !important;
		padding: 0 !important;
		border: none !important;
	}
	#footer .search input {
		margin: 0 !important;
	}
	#footer .copyright {
		font-size: x-small !important;
		color: #000 !important;
	}
	#footer .admin li {
		border-left: 1px solid #555 !important;
		margin: 0 !important;
		padding: 0 !important;
		list-style: none !important;
		display: inline !important;
	}
	.story_main {
		background-color: #ccc !important;
	}
	input[type=submit], #user-info-content .logout a, #userlogin-content .logout a, div.storylinks ul li.more a, #commentlisting .nbutton p b a {
		background: linear-gradient(#444, #222) !important;
		color: #ccc !important;
		text-decoration: none !important;
		border: 1px solid #777 !important;
	}
	input[type=submit]:hover, #user-info-content .logout a:hover, #userlogin-content .logout a:hover, div.storylinks ul li.more a:hover, #commentlisting .nbutton p b a:hover {
		background: linear-gradient(#222, #444) !important;
		color: #ccc !important;
		text-decoration: none !important;
		cursor: pointer !important;
	}
	input[type=text], input[type=password] {
		border: 1px solid #777 !important;
	}
	#adminmenu a {
		color: #0000b3 !important;
	}
	.data_head {
		background-color: #0000b3 !important;
	}
	.data_head th:first-child {
		border-left: 1px solid #0000b3 !important;
	}
	.data_head th:last-child {
		border-right: 1px solid #0000b3 !important;
	}
	.data, .data_hl1, .data_hl2, .data_hl3 {
		background-color: #222 !important;
		color: #fff !important;
	}
	.data_hl1 {
		background-color: #333 !important;
	}
	.data_hl1 td:first-child, .data_hl2 td:first-child {
		border-left: 1px solid #777 !important;
	}
	.data_hl1 td:last-child, .data_hl2 td:last-child {
		border-right: 1px solid #777 !important;
	}
	table.data tr:last-of-type td:last-of-type {
		border-bottom: 1px solid #777 !important;
	}
	table.data tr:nth-last-child(2) td {
		border-bottom: 1px solid #777 !important;
	}
	.data_hl2 {
		background-color: #292929 !important;
	}
	.data .status0 {
		background-color: #aa6 !important;
	}
	.data .status1 {
		background-color: #a66 !important;
	}
	.data .status2 {
		background-color: #6a6 !important;
	}
	.status0 a, .status1 a, .status2 a {
		color:#fff !important;
	}
	.lt_tb_col {
		background-color: #222 !important;
	}
	/* Comments */
	.commentBox {
		background-color: #0000b3 !important;
	}
	.commentBox a {
		color: #fff !important;
		font-size: .9em !important;
	}
	.commentBox a:hover {
		color: #aaa !important;
		font-size: .9em !important;
	}
	/* Poll */
	.barColor {
		background-color: #0000b3 !important;
	}
	.barPercent, .barVotes {
		background-color: #222 !important;
		color: #ccc !important;
		border-top: 1px solid #ccc !important;
	}
	/* Bottom Nav */
	.btmnav {
		color: #9696ff !important;
		font-size: .7em !important;
	}
	.btmnav ul li a, .btmnav ul li a:visited {
		color: #9696ff !important;
	}
	.btmnav ul li a:hover {
		color: #bfbfff !important;
	}
	/* Journal */
	.journaldate {
		position: relative !important;
	}
	.generalbody #journalgeneric .article {
		height: auto !important;
		margin: 0 0 1em 0 !important;
	}
	#authors .generalbody h3 a {
		color: #fff !important;
	}
	/* Forms */
	textarea {
		width: 80% !important;
	}
	#journal input.button, #admin input.button {
		width: auto !important;
	}
	/* Misc */
	.tb_col {
		background-color: #0000b3 !important;
	}
	/* Usermenu */
	.user {
		border-radius: 3px !important;
	}
	#navigation-content .user, #navigation-content .user ul, #navigation-content .user ul li {
		background-color: #262626 !important;
	}
	#navigation-content ul:last-child {
		border-bottom-left-radius: 3px !important;
		border-bottom-right-radius: 3px !important;
	}
	ul.menu li a, ul.menu li a:visited {
		color: #fff !important;
	}
	ul.menu li.selected a, ul.menu li a:hover {
		color: #bfbfff !important;
	}
	.generalbody ul.menu li a {
		color: #bfbfff !important;
	}
	#usermenu {
		border: 1px solid #777 !important;
		border-bottom: none !important;
		background-color: #222 !important;
	}
	#usermenu ul.menu {
		border-bottom: 1px solid #777 !important;
		padding: .2em 0 0 0 !important;
	}
	#usermenu ul.menu li {
		padding: .2em !important;
	}
	#usermenu ul.menu li a {
		background-color: #0000b3 !important;
		background: linear-gradient(#4848d9, #0000b3) !important;
		border-radius: .3em .3em 0 0 !important;
		border: none !important;
		padding: .2em .6em !important;
		text-decoration: none !important;
		color: #fff !important;
	}
	#usermenu ul.menu li.selected a {
		background-color: #777 !important;
		background: linear-gradient(#777, #555) !important;	
		text-decoration: none !important;
		color: #fff !important;
	}
	#adminmenu ul.menu a, #adminmenu ul.menu a:visited {
		color: #0000b3 !important;
	}
	.menuoldstyle li {
		display: inline !important;
		list-style: none !important;
		margin: 0 !important;
		padding: 0 2px 0 1px !important;
	}
	.menuoldstyle ul {
		padding: 0 !important;
		margin: 0 0 1em 0 !important;
	}
	.menuoldstyle li:before {
		content: "| " !important;
	}
	.menuoldstyle li:first-child:before {
		content: "" !important;
	}
	#article .briefarticle {
		padding: .3em .6em !important; 
		background-color: #222 !important;
		font-size: .813em !important;
		color:#000 !important;
	}
	#article .briefarticle a {
		color: #9696ff !important;
	}
	#article .briefarticle .storytitle {
		font-weight: bold !important;
	}
	#index .briefarticle {
		padding: .3em .6em !important;  
		color:#fff !important;
		margin-bottom: .6em !important;
		background-color: #0000b3 !important;
		background: linear-gradient(#4848d9, #0000b3) !important;
		border-radius: 10px !important;
	}
	#index .briefarticle a {
		color: #fff !important;
		text-decoration: none !important;
		font-weight: bold !important;
	}
	.briefarticle .section {
		color: #222 !important;
	}
	.briefarticle .introhide {
		display : none !important;
	}
	.briefarticle .intro {
		padding-left: 1em !important;
		padding-top: 0.5em !important;
	}
	/* Messaging */
	.message_menu {
		border: 1px solid #777 !important;
		background-color: #222 !important;
		color: #fff !important;
		border-bottom: none !important;
	}
	.message_menu ul {
		margin: 0 !important;
	}
	.message_subject {
		border: 1px solid #777 !important;
		background-color: #444 !important;
		background: linear-gradient(#444, #222) !important;
		color: #fff !important;
	}
	.message_body {
		border: 1px solid #777 !important;
		border-bottom: none !important;
		border-top: none !important;
		background-color: #222 !important;
	}
	.message_footer {
		border: 1px solid #777 !important;
		border-top: none !important;
		background-color: #222 !important;
	}
}
