css:
@-moz-document url(http://www.memrise.com/home/) {

body, span, .container {
	font-size: 12px !important;
  
}
  
.container {
	width: 1385px !important;
	max-width: 1385px !important;
	
} 

.tabbed-sidebar, .sidebar-boxes, .li.active, .sidebar-profile {
	width: 110px;
}	
	
.sidebar-tabs, .sidebar-premium, .sidebar-leaderboard {
	transform: scale(0.7);
	margin-left: -45px;
	margin-bottom: -70px;
	margin-top: -30px;
}
	
.sidebar-profile{
	margin-top: -20px;
}

.left, .right {
	width:100% !important;
}

.number.js-num {
	font-size: 16px !important;
}	
	
.profile-header, .profile-link {
	height:35px !important;
}	


.tabbed-main {
	width: 1250px !important;
	float:left;
	margin-left: 25px;
	margin-top: -50px;
}


.card-top {
	width:100%;
	padding: 10px 10px 0px !important;
}
	
.card-image-col, .img-crop{
	width:60px !important;;
}
	
.course-photo {
	margin-left: 0px !important;
	height: 60px !important;
}	
	
.detail, .pull-right	{
	width: 185px !important;
}

.detail {margin-bottom: 5px;}	
.pull-right {	float: none !important;}	
	
	
.title {
	font-size:16px !important;  
  }
  
.goal-setter-btn {
	height:16px !important;
	min-width: 100px !important;
}

.leaderboard-icon, .icon{
	background-size:110px 16px !important;
  	height: 16px !important;

}  

.leaderboard-btn {
	height: 24px !important; width:24px !important;
  	margin-right: -15px !important;
}

.onoff {  
	padding: 0px 5px 5px 0px !important;
	width:102px;
}  
.orange, .pts, .onoff {
	font-size: 9px !important;
}	
  
.goal-setter {
	height: 50px;
	width: 230px !important;
	margin: 5px -50px -5px !important;
	
}  

	
.goal-setter-progress {
  width: 270px !important;
  margin: 0px -42px 5px !important;
  height: 8px !important;
	position: absolute !important;
	top: 65px !important;
}
.streak-row {
	transform: scale(0.7) !important;
	margin: -8px 0px !important;
}

.streak-edit {
	position:absolute !important;
	top: 9px;
	right: 50px;
}
 
.card-bottom {
	padding: 5px 0px 5px 0px !important;
	min-height: 0px !important;
	width: 300px;

}

.button {
	min-width: 49px !important;
	max-width: 49px;
	height: 26px;
	padding: 0px 0px !important;
	margin-right: 10px;
}  

.button.pink, .button.green, .button.blue {
	background-image: none;
}	

.button.pink{background:#F13A98;}	
.button.green{background:#7EC826;}
.button.blue{background:#69C3F4;}
	
.course-card-container {
	margin-bottom:12px !important;
	margin-right: 12px;
	width:300px;
	height:240px;
	display:inline-block;
	vertical-align: top;
}

.card-main-container {
	width:160px !important;
	height:185px;
	top: 16px; right: 16px;
}

.button.blue{
	margin-top: -10px;
	margin-bottom: -10px;
	height: 49px !important;
}	

.text {
	white-space: unset !important;
}
	
	.fb-like-box, .app-promo-box, .streak, .right.half, .duration, .level.first, .level.second, .level.third, .tail, .name, .content-general, .profile-image, .premium-badge, .premium-header, .premium-link, .img-star {
	display: none !important;
}
	
}
