css:
@-moz-document domain("jeuxvideo.com") {
/* Suppresion du panier */
#header-store-jv{
	display:none;
}
/* Couleur bleu du header */
#header-bottom,
#header-bottom .bloc-sticker,
#header-top{
    background: #438FCD;
}
/* Modification de la couleur de fond du sous-menu */
#navbar-jv .content-menu-jv{
	background: #000;
}
/* Modification du background */
#content{
    margin-top: 0;
    margin-bottom: 0;
    padding-bottom: 20px;
    background-color: #000;
    background-image: url('http://image.noelshack.com/fichiers/2015/26/1435340498-mirrors-edge-catalyst.jpg');
    background-repeat: no-repeat;
    background-position: center top;
    background-attachment: fixed;
    background-size: 100%;
}
/* Rouge pour les titres des blocs, les boutons Poster et Répondre */
.panel-jv-forum > .panel-heading,
#table-liste-topic-forum thead,
.btn-repondre-msg,
.btn-poster-msg,
.bloc-liste-num-page .page-active{
    background-color: #F50A11;
}
/* Suppression de la bordure des boutons */
.btn-repondre-msg,
.btn-poster-msg,
.btn-actu-new-list-forum{
    border: none;
}
/* Couleur bleu au survol des boutons répondre et poster */
.btn-repondre-msg:hover,
.btn-poster-msg:hover{
    background-color: #438FCD;
}
/* Lien blanc sur le bouton poster au survol */
.btn-poster-msg:hover{
	color: #fff;
}
/* Couleur noir pour les boutons nouveau sujet, liste de sujets et actualiser */
.btn-actu-new-list-forum{
 	background-color: #000;   
}
/* Au survol des boutons : couleur noir sur fond blanc */ 
.btn-actu-new-list-forum:hover{
 	background-color: #fff; 
    color:#000;
}
/* Changement de couleur des blocs message */
.bloc-message-forum:nth-child(even){
    background: #fff;
}
/* Coulour noir pour la date et numéros de page */
.bloc-date-msg .lien-jv,
.bloc-liste-num-page .lien-jv{
    color: #000;
}
/* Couleur rouge pour les titres de blocs, le survol de la date de message et le survol des liens dans les posts */
.titre-head-bloc .titre-bloc,
.bloc-date-msg .lien-jv:hover,
.bloc-contenu .lien-jv:hover{
    color: #F50A11; 
}
/* Fond noir pour les blocs à droite et pour le survol des items de menu */
.panel-jv-forum > .panel-heading + .panel-body,
#navbar-jv .liste-rubrique > li:hover,
#navbar-jv .liste-rubrique > li.open{
    background: #000;
}
/* Couleur blanche pour l'entête de la liste des topics */
#table-liste-topic-forum thead,
.titre-info-fofo{
 	color: #fff!important;   
}
/* Fond blanc opaque pour le container */
.container-content{
 	background: rgba(255, 255, 255, 0.9);   
}
/* Suppression des blocs à droite */
#prospect-square2,
.outbrain{
 	display: none!important;   
}
/* Bordures rouge pour le sous-menu */
#navbar-jv .content-menu-jv{
	border-top: 5px solid #F50A11;
	border-bottom: 5px solid #F50A11;
}
/* Modification couleur liens du footer */
#jv-footer .liens-pied{
	border-top: 5px solid #438FCD!important;
}
#jv-footer .liens-pied .a-footer{
	color: #438FCD;
}
/* Fin modifications liens du footer */

/* Habillage de l'éditeur */
.jv-editor .conteneur-editor{
    background: #FF0013;
}
.jv-editor .text-editor,
.jv-editor .area-editor{
    background: #000
}
.jv-editor .area-editor,
.jv-editor .label-previsu{
	color: #fff;
}
.jv-editor .btn-group{
    background: #fff;
    border: none;
}
.btn-jv-editor-toolbar{
 	color: #000;   
}
.jv-editor .conteneur-editor,
.jv-editor .previsu-editor{
    border: 1px solid #000;
}
/* Fin de l'habillage de l'éditeur */

}
