css:
@-moz-document domain("linkibl.com") {
body {
    background-image: none !important;
    background-color: #2F2F2F !important;
    color: #FFF !important;
}

.well.tipwell {
    background-color: #383838;
    border: 2px solid #888888;
}
.padding-top-10 {
    margin-top: 12px;
}
.container-narrow {
    background-image: none;
    background-color: #3D3D3D;
}
.container {
    width: 98%;
}
.narrow-list {
    font-size: 12px;
}
#sc-header-area .sc-list {
    margin-top: 2px;
    margin-bottom: 2px;
}
.portlet-title u, .content-title u {
    color: #EFEFEF;
}
.clickable.related_topic_name {
    color: #DDD;
}
.col-md-3 {
    width: 18%;
}
.col-md-3,#sidebar {
    margin-left: 4%;
}
.list_thumbnail, .thumbnail {
    background-color: #282828;
}

.row.list_share_titlebar {
    background: none;
    background-color: #444 !important;
}

a.list_sharetitle {
    color: #FFF;
}
.mainnav {
    background-color: #2A2A2A;
}
.form-control {
    background-color: #2F2F2F;
}
.navbar-inverse {
    background-color: #414141;
}
#logo {
     background-color: #414141;
}
.list_thumbnail {
    margin-bottom: 6px;
}
.list_share_image_thumbnail {
    max-height: 77px;
    min-height: 65px;
    margin-bottom: 1px;
}
.col-xs-12 .zero-padding-left .zero-padding-right {
    font-size: 18px;
    text-shadow: rgb(255, 255, 255) 0px 0px 15px;
}
.btn {
    background-color: #313B42;
	color: #FFF !important;
    text-shadow: rgb(255, 255, 255) 0px 0px 0px !important;
    border:1px solid rgba(35, 46, 56, 0.521569);
}
.btn-default:active, .btn-default.active {
border-color:rgb(94, 94, 94);
background-color:rgb(97, 112, 122);
}
.form-control {
font-size:18px;
border:1px solid rgba(92, 92, 92, 0.819608);
}
.list_description a:visited {
    color: #668AA4;
}
.list_description.share, .list_description a {
    color: #CFE7FF;
    font-size: 18px;
}
.list_resharesource {
    color: #E7E7E7;
}
.content-title u {
    color: #FFFFFF !important;
}
#comment_content {
    width:100%;
border:1px solid rgb(66, 66, 66);
background-color:rgb(54, 54, 54);
}
.share-widget-actions {
    background-color: #494949;
    border: 1px solid #4F4F4F;
}
.clickableDescription {
    color: #E8E8E8;
}
.topicfont.websource {
    color: #E8E8E8;
}
a.breadcrumbs:visited {
    color: #EAEAEA;
}
a.breadcrumbs {
    color: #E5E5E5;
    font-size: 9px;
}
.footer {
    background-color: #383838;
    border-top: 1px solid #414141;
}
.col-md-6 #right_wrapper {
    width: 30% !important;
}
.col-md-6, #share_side {
    width: 70%;
}
.col-md-6 {
    width: 30%;
}
}
