css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain('www.journaldugeek.com'), domain('www.jdubuzz.com'), domain('www.journaldugamer.com') {
  
  * {
    box-sizing: initial;
    background-color: #14171A !important;
    /*color: #BDC3C7 !important;*/
  }
  
  /* Page d'accueil */
  .main-header, .tiles, .section-title-container, .side, .tests-push, #mosaic-network-bottom, 
  div[class="post"] > div[class="row"] > div[class="col-sm-6"]:nth-child(1),
  div[class="post journaldugamer"] > div[class="row"]  > div[class="col-sm-6"]:nth-child(1),
  div[class="post jdubuzz"] > div[class="row"]  > div[class="col-sm-6"]:nth-child(1),
  div[class="post pixelistes"] > div[class="row"]  > div[class="col-sm-6"]:nth-child(1){
     display: none !important;
   }
  
  /*Fuck adblockfucker*/
  .blockadd {
    display: none !important;
  }
  
  /*Affichage en mode liste à la place du mode grille*/
  .views .active {
    display: none !important;
  }
  
  .views .gridview, .views .listview {
    display: initial;
  }
  
  .col-sm-6 {
    width: 100%;
  }
  
  .listview .post, .pagination .pagination-items li {
    border: none;
  }
  
  /* Articles */
  
  span, .post-data .subscribe-btn, .share, .subscribe, .hashtags, .aller-plus-loin  {
    display: none !important;
  }
  
  .container {width: 90%;}
  .content {padding: 0;}
  .post-data-header .img-responsive {height: auto; width: 100%;}
  .col-md-offset-1 {margin-left :0 !important;}
  .post-content .row .col-md-10, .post-content .row .col-md-10 iframe {width: 100%;}
  .col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
    padding: 0;
  }
  
  /* Page d'accueil + articles */
   
    /* header */
    .topbar .container .news-push {display: none;}
    .topbar .secondary-menu {display: none;}
    .main-menu-ctn .promo {display: none;}
    .main-menu-ctn .pages-menu .pages-nav {display: none !important;}
    .single-test {display: none;}
 
    /* content */
    .col-md-8 {width: 100%;}
    .side-ctn {display: none;}
  
    /* footer */
    .jdg-recommend {display: none;} 
    footer {display: none;} 
    #popin {display: none;}
  
  
  .col-xs-1, .col-sm-1, .col-md-1, .col-lg-1, .col-xs-2, .col-sm-2, .col-md-2, .col-lg-2, .col-xs-3, .col-sm-3, .col-md-3, .col-lg-3, .col-xs-4, .col-sm-4, .col-md-4, .col-lg-4, .col-xs-5, .col-sm-5, .col-md-5, .col-lg-5, .col-xs-6, .col-sm-6, .col-md-6, .col-lg-6, .col-xs-7, .col-sm-7, .col-md-7, .col-lg-7, .col-xs-8, .col-sm-8, .col-md-8, .col-lg-8, .col-xs-9, .col-sm-9, .col-md-9, .col-lg-9, .col-xs-10, .col-sm-10, .col-md-10, .col-lg-10, .col-xs-11, .col-sm-11, .col-md-11, .col-lg-11, .col-xs-12, .col-sm-12, .col-md-12, .col-lg-12 {
    position: initial;
  }

  .post-content .chapeau {
    color: whitesmoke;
  }
  
  
  /*Carousel photo*/
  .fancybox-nav > span, .fancybox-item {
	  display: initial !important;
    border-radius: 5px;
 }
  
  .fancybox-nav {
    background: transparent !important;
  }
  
  /* Dark mode*/
  .inner .main {
    background: #14171A !important;
    color: #BDC3C7 !important;
  }
  
  h1, .post-title > a {
    color: #98035A !important;
  } 
  
  body, strong {
    background: #14171A !important;
    color: #BDC3C7 !important;
  }
  
  .post-content p {
    color: whitesmoke;
  }
  
  .comments-bloc {
    border-bottom: none;
  }
  
  .comments-bloc input[type="submit"] {
    background-color: #98035A !important;
 }
   
}
