{
	.ytp-time-duration, .ytp-time-current{
	color:#33CC33;
  text-shadow: 0px 0px 7px rgba(0, 0, 0, 0.5)
}
	/*barva nacitani progressu*/
	.html5-load-progress, .ytp-load-progress {
   background:#8AA0B8 !important;
}
	/*barva play progressu*/
	.ytp-play-progress{
	background:#00A300;
		
	/*vice barev*/
  background: linear-gradient(to right, #009933 0%, #00FF00 50%, #009900 100%);
	box-shadow: 0px 0px 12px rgba(93, 255, 44, 1)
}
	/* barva  volume */
		.ytp-big-mode .ytp-volume-slider-handle:before, .ytp-volume-slider-track{
    background:#33CC33;
}
  	/*pozadí volume */
	.ytp-volume-slider-handle:after{
	background:#4B4B4B;
} 
  /*barva play buttonu (tečka)*/
	.ytp-scrubber-button, .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background: #FFFFFF !important;
   border-color: #009933!important;
   box-shadow: 0px 0px 6px #009900, 0px 0px 8px #66FF33, 0px 0px 10px #66FF66 !important;
}	
  /*play buttonu (tečka) HOVER */
  .html5-scrubber-button:hover, .ytp-scrubber-button:hover{
   background: #00CC00 !important;
   border-color: #1B1B1B !important;
   box-shadow: 0px 0px 6px #FFFF00, 0px 0px 8px #FFCC00, 0px 0px 10px #FF9900 !important;
}
	/*barva buttonů */
  .ytp-playlist-menu-button-icon .ytp-svg-fill,.ytp-chrome-top .ytp-button:not(.ytp-playlist-menu-button) .ytp-svg-fill,.ytp-chrome-controls .ytp-button .ytp-svg-fill{
		fill: #66FF33;
	}	
	/*barva HD(720p..), pokud je dostupné
  .ytp-settings-button.ytp-hd-quality-badge:after,.ytp-settings-button.ytp-4k-quality-badge:after,.ytp-settings-button.ytp-5k-quality-badge:after,.ytp-settings-button.ytp-8k-quality-badge:after{
	background-color:#995C1F;
}	*/
	/* pozadí -> nabídka, poznámky, titulky atd */
	.ytp-panelpopup{
	background:rgba(60, 177, 8, 0.9) none repeat scroll 0% 0%;
	color:#FFFFFF;
} 
	/*TEXT z panel pop-up*/
	.ytp-menu-label-secondary{
	color:#FFFFFF;
}
	/*fajfka - autoplay*/
	.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox{
	background: rgba(60, 177, 8, 0.9) none repeat scroll 0% 0%;
}
}
