/* https://userstyles.org/styles/116178.css | (c) 2015 LittleGreyGrey | https://www.fifiblog.com */
@-moz-document regexp("^https?://www.baidu.com/s\\?(.+)$") {
  
  body, a, a em {
      font-family: "微软雅黑" !important;
      letter-spacing: 1px !important;
      font-weight: bolder !important;
      text-decoration: none !important;
  }
  #content_right, #foot, #content_left > div:not(.result-op):not(.c-container), .rrecom-btn-parent, .rrecom-content {
      display: none !important;
      height: 0 !important;
      position: fixed !important;
      z-index: -999 !important;
  }
  #3000, #3001, #3002, #3003, #3004, #3005, #3006, #3007, #3008, #3009, #30010, #30011, #30012, #3013, #3014, #3015, #3016, #3017, #3018, #3019, #3020, #5000, #5001, #5002, #5003, #5004, #5005, #5006, #5007, #5008, #5009 {
      display: none !important;
      height: 0 !important;
      position: fixed !important;
      z-index: -999 !important;
  }
  #content_left .result-op, #content_left .result {
      -webkit-transition: all 300ms ease-in-out !important;
      -moz-transition: all 300ms ease-in-out !important;
      margin: 0 !important;
      padding: 7px !important;
      border: 1px solid #eee !important;
      border-bottom: none !important;
      background-color: #fff !important;
  }
  #content_left .result-op:hover, #content_left .result:hover {
      -webkit-box-shadow: 0 0 10px #000 !important;
      -moz-box-shadow: 0 0 10px #000 !important;
      -webkit-border-radius: 5px !important;
      -moz-border-radius: 5px !important;
      background-color: #eee !important;
      position: relative !important;
  }
  #content_left .result-op:first-child, #content_left .result:last-child {
      border-bottom: 1px solid #eee !important;
  }
  .container_l {
      width: 1200px !important;
      position: absolute !important;
      left: 50% !important;
      margin-left: -600px !important;
  }
  #s_tab {
      margin: 0 auto !important;
      width: 1080px !important;
      background: none !important;
  }
  .s_form_wrapper {
      margin: 0 auto !important;
      width: 1190px !important;
  }
  #s_tab b {
      border: none !important;
      border-top: 3px solid #38f !important;
  }
  .s_ipt_wr {
      width: 552px !important;
  }
  .info-item.info-detail {
      font-size: 10px !important;
  }
  #wrapper {
    z-index: 9999 !important;
    position: relative !important;
    background-color: #fff !important;
  }
  .slowmsg {
    left: 500px !important;
    z-index: 9999 !important;
  }
}
