@namespace url(http://www.w3.org/1999/xhtml);
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*Change wallpaper, copy/paste new url between brackets below */
#tab-view-deck {background: url(/*[[xx-wallpaper-xx]]*/) center bottom no-repeat !important;
    background-size: cover!important; 
    border-top: none !important;
    -moz-box-flex: 1 !important;}

/* Defined min and max size of the tabs, default is 180px on all tabs. Change if desire below: */
.tabbrowser-tab:not([pinned])[fadein] {
    min-width: /*[[othtabmin]]*/px !important;
    max-width: /*[[othtabmax]]*/px !important;
    height:26px !important;
    margin-left:0px !important;margin-right:2px !important;}

.tabbrowser-tab:hover:not([pinned]){
    margin-left:1px !important;margin-right:1px !important;}
        
.tabbrowser-tab[selected]:not([pinned])[fadein] {
    min-width: /*[[seltabmin]]*/px !important;
    max-width: /*[[seltabmax]]*/px !important;
    margin-left:2px !important; margin-right:0px !important;}  

.tabbrowser-tab[pinned] {margin-right:2px !important;}

.tabbrowser-tab:not([pinned="true"]){
   color: /*[[othcolor]]*/ !important;
   background-image: radial-gradient(65% 2% at 50% 1%,rgba(186,186,186,0.5) 40%, rgba(197,197,197,.4) 50%, rgba(197,197,197,.02) 75%)!important;
   background-position: center 3px;
   background-repeat: no-repeat;
   background-size: 100% 80%;
   font-family: Calibri !important;     
   font-size: 1.05em !important;
   font-weight:bold!important;
   text-shadow: #000000 1px 1px 0px !important;}

.tabbrowser-tab[selected]:not([pinned])[fadein] {
   color: /*[[selcolor]]*/ !important; 
   font-size: 1.28em !important;
   text-shadow: #000000 2px 1px 1px !important;}

.tabbrowser-tab:hover:not([pinned]){
   color: /*[[hovcolor]]*/ !important;
   background-image:radial-gradient(at top, rgba(255,255,255,.35), transparent, transparent), radial-gradient(at bottom, rgba(220,220,220,.45), rgba(255,255,255,.05), transparent), radial-gradient(65% 2% at 50% 1%,rgba(186,186,186,0.5) 40%, rgba(197,197,197,.4) 50%, rgba(197,197,197,.02) 75%)!important;
   background-position: center 2px;
   background-size: 100% 100%;	
   font-size: 1.15em !important;
   text-shadow: #000000 1px 1px 0px !important;}

/*Change tab's iconsize (related to choosed fontsizes above: 1em is equal) */
.tabbrowser-tab:not([pinned="true"]) .tab-icon-image {
    width: .95em !important;
    height: .95em !important;}
    
.tabbrowser-tab[selected]:not([pinned])[fadein] .tab-icon-image {
    width: 1em !important;
    height: 1em !important;}
    
.tabbrowser-tab:hover:not([pinned]) .tab-icon-image {
    width: .95em !important;
    height: .95em !important;}

/* bg inactive tabs except last tab - cascade to left, from https://userstyles.org/styles/102124/australis-background-tabs-visible-hover */
#tabbrowser-tabs .tabbrowser-tab:not([selected]) .tab-stack > .tab-background {
    background-image: url(data:image/png;base64,),
url(data:image/png;base64,),
url(data:image/png;base64,);
    background-position: left bottom, 30px bottom, right bottom; 
    background-repeat: no-repeat; 
    background-size: 30px 97%, calc(100% - 60px) 97%, 30px 97%;}

/* bg last inactive tab to the right */
#tabbrowser-tabs .tabbrowser-tab[selected]~ .tabbrowser-tab:last-child .tab-stack > .tab-background {
    background-image: url(data:image/png;base64,),
url(data:image/png;base64,),
url(data:image/png;base64,); }

#tabbrowser-tabs[overflow] > .tabbrowser-tab:not([selected])[pinned]:not([last-visible-tab]) .tab-stack > .tab-background {
    background-image: url(data:image/png;base64,),
url(data:image/png;base64,),
url(data:image/png;base64,); }

#tabbrowser-tabs .tabbrowser-tab:not([selected]):hover .tab-stack > .tab-background{
    background-position: left bottom, 30px bottom, right bottom; 
    background-repeat: no-repeat; 
    background-size: 30px 99%, calc(100% - 60px) 99%, 30px 99%;}
  
#tabbrowser-tabs, #TabsToolbar:not(:-moz-lwtheme)::after, #TabsToolbar{ 
    -moz-appearance: none !important; 
    margin-top: 1px !important;}

#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
#tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after {
    background-image: none !important;}

@media (min-resolution: 2dppx) {
#tabbrowser-tabs[movingtab] > .tabbrowser-tab[beforeselected]:not([last-visible-tab])::after,
.tabbrowser-tab:not([selected]):not([afterselected-visible]):not([afterhovered]):not([first-visible-tab]):not(:hover)::before,
#tabbrowser-tabs:not([overflow]) > .tabbrowser-tab[last-visible-tab]:not([selected]):not([beforehovered]):not(:hover)::after {
    background-image: none !important;}}

.tabbrowser-tabs [class^="scrollbutton"] {
    width: auto!important;}

/* Change background on active tab */
.tab-background, .tab-background-middle {
    background-color:transparent!important;}

.tab-background .tab-background-middle[selected=true]{
    background-image:url("chrome://browser/skin/customizableui/background-noise-toolbar.png"), url(data:image/png;base64,), linear-gradient(to top, transparent, rgba(255,255,255,0.89) 2%,rgba(230,230,232,.98) 10%, rgba(240,245,247,.24) 100%,transparent ) !important; 
    background-size:auto 100%!important;}

.tab-background-start[selected=true]::before{
    clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-start)!important;}

.tab-background-end[selected=true]::before{
    clip-path: url(chrome://browser/content/browser.xul#tab-curve-clip-path-end)!important;}

.tab-background .tab-background-start[selected=true]::before,.tab-background .tab-background-end[selected=true]::before{
    content: "" !important;
    display: -moz-box !important;
    background-color: transparent !important;
    background-image: url("chrome://browser/skin/customizableui/background-noise-toolbar.png"), url(data:image/png;base64,), linear-gradient(to top, transparent, rgba(255,255,255,0.89) 2%,rgba(230,230,232,.98) 10%,  rgba(240,245,247,.24) 100%,transparent ) !important; 
    background-size:auto 100%!important;}

/*NewTab button */
.tabbrowser-arrowscrollbox .tabs-newtab-button,#new-tab-button {
    opacity: .8!important;
    width: 24px!important;
    border-radius: 20px!important;
    margin: 5px 4px 4px 5px!important; 
    background-image: radial-gradient(ellipse at left top,#fff 20%,transparent 80%),radial-gradient(ellipse at right top,#fff 10%,transparent 50%), linear-gradient(to top,#999 0%,transparent 40%)!important;}

.tabbrowser-arrowscrollbox .tabs-newtab-button,#new-tab-button {
    box-shadow: inset 0 0 1px 0 rgba(50,50,50,1)!important;}

.tabbrowser-arrowscrollbox .tabs-newtab-button:hover,#new-tab-button:hover {
    opacity: 1!important; 
    box-shadow: inset 0 0 1px 0 rgba(0,0,0,1)!important; 
    background-image: radial-gradient(ellipse at left top, #eee 33%,transparent 95%),radial-gradient(ellipse at right top,#fff 10%,transparent 50%), linear-gradient(to top,#999 0%,transparent 50%)!important;}

.tabbrowser-arrowscrollbox > .scrollbutton-up[disabled],.tabbrowser-arrowscrollbox > .scrollbutton-down[disabled] {
    opacity: 1!important;}

tabbrowser toolbarbutton[class^="scrollbutton"], #TabsToolbar toolbarbutton[class^="scrollbutton"] {
    background: none !important;}

