/*Nav bar */
#nav-bar #bookmarks-menu-button > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#nav-bar #bookmarks-menu-button > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {}

#bookmarks-menu-button > .toolbarbutton-menubutton-dropmarker::before { 
    display:none !important; }

#navigator-toolbox #nav-bar .toolbarbutton-1:not(:-moz-any(#back-button, #forward-button)){
    margin-top: -5px !important;
    margin-bottom: -5px !important}

#navigator-toolbox > #nav-bar #back-button .toolbarbutton-icon {padding: 5px !important}

#navigator-toolbox #back-button{
    margin-left: 8px !important;
    margin-bottom: -3px !important;
    margin-top: -3px !important}

#navigator-toolbox > #nav-bar .toolbarbutton-1:not(#back-button):not(#forward-button) {
    padding-left: 3px !important;
    padding-right: 3px !important}

#navigator-toolbox > #nav-bar .toolbarbutton-1 {
    padding-left: 0px !important;
    padding-right: 0px !important}

#nav-bar{
    background: linear-gradient(to bottom, transparent, rgba(235,235,237,.98) 2%, rgba(220,225,227,0.3) 98%, rgba(0,0,0,1) 100%)  !important; 
    border-top: 0px solid transparent!important;
    box-shadow: 0px 1px 3px rgba(0,0,0,.8),inset 0px -1px 10px rgba(230,235,237,.5)!important;}

/*Reduce horizontal padding of buttons icons*/
#navigator-toolbox > #nav-bar .toolbarbutton-1:not([type=menu]):not(#back-button):not(#forward-button) > .toolbarbutton-icon,
#navigator-toolbox > #nav-bar .toolbarbutton-1[type=menu]:not(#back-button):not(#forward-button):not(#feed-button),
#navigator-toolbox > #nav-bar #feed-button > .toolbarbutton-icon,
#navigator-toolbox > #nav-bar #bookmarks-menu-button > .toolbarbutton-menubutton-button > .toolbarbutton-icon,
#navigator-toolbox > #nav-bar #bookmarks-menu-button > .toolbarbutton-menubutton-dropmarker > .dropmarker-icon {
    margin-left: 1px !important; 
    margin-right: 1px !important}

#urlbar-search-splitter {
    padding: 0 !important; 
    margin: 0 -3px !important;}

#urlbar, #searchbar > *{
    -moz-appearance:none !important;  
    border: 2px solid;
    -moz-border-top-colors:  rgba(255,255,255,.1) rgba(80,80,80,.5)!important;
    -moz-border-right-colors: rgba(255,255,255,.3) rgba(0,0,0,.2)!important;
    -moz-border-left-colors:  rgba(255,255,255,.1) rgba(0,0,0,.3)!important;
    -moz-border-bottom-colors:  rgba(255,255,255,.3) rgba(0,0,0,.3)!important;
    box-shadow: 0px 0px 10px rgba(255,255,255,.1), inset 0px -2px 30px rgba(0,0,0,.2) !important; 
    background: linear-gradient(to top, transparent, rgba(0,0,0,.02)) !important;  
    border-radius: 0 4px 4px 0 !important;
    -moz-appearance:none !important; color: rgba(240,240,240,1)!important; 
    font-weight: bold !important;  
    text-shadow: 1px 1px 1px #000!important;}

#searchbar > * {
    border-radius: 4px 4px 4px 4px !important;}

#urlbar:hover, #searchbar > *:hover {
    background: -moz-linear-gradient(bottom, rgba(255,255,255,.1), transparent) !important;}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button) > .toolbarbutton-icon {
    border: 1px solid rgba(24, 25, 26, 0.5) !important;
    background: rgba(250,251,252,.5)!important;
    box-shadow: none !important;}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"]:not(:-moz-lwtheme) #forward-button > .toolbarbutton-icon,
#nav-bar-customization-target:not(:-moz-lwtheme) #forward-button > .toolbarbutton-icon {
    border-color: rgba(23, 51, 78, 0.25) rgba(23, 51, 78, 0.27) rgba(23, 51, 78, 0.3) !important;}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] > #back-button > .toolbarbutton-icon {
    padding: 4px !important;}

/* from userstyle betterAustralis, https://userstyles.org/styles/100721/betteraustralis */
