#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] :-moz-any(#back-button,#forward-button)[open="true"] > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button):not([disabled="true"]):hover:active > .toolbarbutton-icon,
#nav-bar-customization-target :-moz-any(#back-button,#forward-button)[open="true"], #main-window #navigator-toolbox :-moz-any(#back-button, #forward-button):hover > .toolbarbutton-icon {
    background: #D8DADC !important;
    box-shadow: 0 0px 0 0 rgba(24, 25, 26, 0.93) inset !important;}

#nav-bar[currentset*="unified-back-forward-button,urlbar-container"] #forward-button > .toolbarbutton-icon,
#nav-bar-customization-target #forward-button > .toolbarbutton-icon {
    clip-path: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg"><clipPath id="forward-btn-clip-path" clipPathUnits="objectBoundingBox"> <path d="m 0,0 c .3,.25 .3,.75, 0,1 l 1,0 0,-1 z"/></clipPath></svg>#forward-btn-clip-path')  !important;
    border-left-style: none !important;
    border-radius: 0 !important;}

